    10→
    11→import { useMemo } from 'react';
    12→import { getStatusConfig, PROPOSAL_STATUS } from '../../../config/proposalStatusConfig.js';
    13→
    14→/**
    15→ * Compute button states for a proposal card.
    16→ *
    17→ * @param {object} params
    18→ * @param {object} params.proposal - The proposal object with Status, guest documents review finalized?, remindersByGuest (number)
    19→ * @param {object} params.virtualMeeting - Virtual meeting data with requested by, booked date, confirmedBySplitLease, meeting declined
    20→ * @param {object} params.guest - Guest user object with ID documents submitted?
    21→ * @param {object} params.listing - Listing object with House manual
    22→ * @param {string} params.currentUserId - Current user's ID
    23→ * @returns {object} Button states for virtualMeeting, guestAction1, guestAction2, cancelProposal
    24→ */
    25→export function useProposalButtonStates({ proposal, virtualMeeting, guest, listing, currentUserId }) {
    26→  return useMemo(() => {
    27→    if (!proposal) {
    28→      return {
    29→        virtualMeeting: { visible: false },
    30→        guestAction1: { visible: false },
    31→        guestAction2: { visible: false },
    32→        cancelProposal: { visible: false },
    33→      };
    34→    }
    35→
    36→    const rawStatus = proposal.Status || proposal.status;
