    45→      PROPOSAL_STATUS.INITIAL_PAYMENT_LEASE_ACTIVATED,
    46→      PROPOSAL_STATUS.PROPOSAL_SUBMITTED_BY_SL_AWAITING_APPLICATION,
    47→      PROPOSAL_STATUS.PROPOSAL_SUBMITTED_BY_SL_PENDING_CONFIRMATION,
    48→    ].map(s => (typeof s === 'string' ? s.trim() : s));
    49→
    50→    let vmButton = { visible: !vmHiddenStatuses.includes(status) };
    51→
    52→    if (vmButton.visible) {
    53→      if (vm?.['meeting declined']) {
    54→        vmButton = {
    55→          ...vmButton,
