    57→          fontColor: '#DB2E2E',
    58→          bold: true,
    59→          tooltip: 'click to request another one',
    60→          disabled: false,
    61→        };
    62→      } else if (vm?.['booked date'] && vm?.['confirmedBySplitLease']) {
    63→        vmButton = { ...vmButton, label: 'Meeting confirmed', disabled: true };
    64→      } else if (vm?.['booked date']) {
    65→        vmButton = { ...vmButton, label: 'Virtual Meeting Accepted', disabled: true };
    66→      } else if (vm?.['requested by'] === currentUserId) {
    67→        vmButton = { ...vmButton, label: 'Virtual Meeting Requested', disabled: true };
    68→      } else if (vm?.['requested by']) {
    69→        vmButton = { ...vmButton, label: 'Respond to Virtual Meeting Request', disabled: false };
    70→      } else {
    71→        vmButton = { ...vmButton, label: 'Request Virtual Meeting', disabled: false };
    72→      }
    73→    }
    74→
    75→    // ========== BUTTON 2: Guest Action 1 ==========
    76→    let ga1Button = { visible: !!status && config.guestAction1 !== 'Invisible' };
    77→
    78→    if (ga1Button.visible) {
    79→      // Reminder limit check
    80→      if (config.guestAction1 === 'Remind Split Lease' && (proposal['remindersByGuest (number)'] || 0) > 3) {
    81→        ga1Button.visible = false;
    82→      }
    83→      // Lease docs finalized check
    84→      if (status === PROPOSAL_STATUS.LEASE_DOCUMENTS_SENT && !proposal['guest documents review finalized?']) {
    85→        ga1Button.visible = false;
    86→      }
    87→    }
    88→
    89→    if (ga1Button.visible) {
    90→      if (status === PROPOSAL_STATUS.REJECTED_BY_HOST) {
    91→        ga1Button = { ...ga1Button, label: 'Delete Proposal', backgroundColor: '#EF4444' };
    92→      } else {
    93→        ga1Button = { ...ga1Button, label: config.guestAction1 };
    94→      }
    95→    }
    96→
    97→    // ========== BUTTON 3: Guest Action 2 ==========
    98→    let ga2Button = { visible: config.guestAction2 !== 'Invisible' };
    99→
   100→    if (ga2Button.visible) {
   101→      // ID docs submitted check
   102→      if (status === PROPOSAL_STATUS.LEASE_DOCUMENTS_SENT && !guest?.['ID documents submitted?']) {
   103→        ga2Button.visible = false;
   104→      }
   105→    }
   106→
   107→    if (ga2Button.visible) {
   108→      ga2Button = { ...ga2Button, label: config.guestAction2 };
   109→    }
   110→
   111→    // ========== BUTTON 4: Cancel Proposal ==========
   112→    const cancelHiddenStatuses = [
   113→      PROPOSAL_STATUS.PROPOSAL_SUBMITTED_BY_SL_AWAITING_APPLICATION,
   114→      PROPOSAL_STATUS.PROPOSAL_SUBMITTED_BY_SL_PENDING_CONFIRMATION,
   115→    ].map(s => (typeof s === 'string' ? s.trim() : s));
   116→
   117→    let cancelButton = { visible: !cancelHiddenStatuses.includes(status) };
   118→
   119→    if (cancelButton.visible) {
   120→      const terminalStatuses = [
   121→        PROPOSAL_STATUS.CANCELLED_BY_GUEST,
   122→        PROPOSAL_STATUS.CANCELLED_BY_SPLITLEASE,
   123→        PROPOSAL_STATUS.REJECTED_BY_HOST,
   124→      ].map(s => (typeof s === 'string' ? s.trim() : s));
   125→
   126→      if (terminalStatuses.includes(status)) {
   127→        cancelButton = { ...cancelButton, label: 'Delete Proposal' };
   128→      } else if (status === PROPOSAL_STATUS.HOST_COUNTEROFFER) {
   129→        cancelButton = { ...cancelButton, label: 'Reject Modified Terms' };
   130→      } else if (config.usualOrder > 5 && listing?.['House manual']) {
   131→        cancelButton = { ...cancelButton, label: 'See House Manual', backgroundColor: '#6D31C2' };
   132→      } else if (config.usualOrder > 5) {
   133→        cancelButton.visible = true;
   134→        cancelButton.label = 'Cancel Proposal';
   135→      } else {
   136→        cancelButton = { ...cancelButton, label: 'Cancel Proposal' };
   137→      }
   138→    }
   139→
   140→    return {
