/**
 * Virtual Meeting Business Rules
 *
 * PILLAR II: Rule Engines (The "Conditional" Layer)
 *
 * This module encapsulates the predicate functions for virtual meetings.
 * Based on Bubble.io's 5-state virtual meeting workflow.
 *
 * Virtual Meeting States:
 * 1. No VM exists -> Show "Request Virtual Meeting" button
 * 2. VM requested by host -> Show "Respond to Virtual Meeting"
 * 3. VM booked but not confirmed -> Show meeting details, await confirmation
 * 4. VM confirmed by Split Lease -> Show meeting link and join button
 * 5. VM declined -> Show "Request Alternative Meeting" button
 */

/**
 * Virtual Meeting State Enum
 *
 * IMPORTANT: State names are PERSPECTIVE-NEUTRAL
 * - REQUESTED_BY_ME: Current user requested, waiting for other party's response
 * - REQUESTED_BY_OTHER: Other party requested, current user should respond
 *
 * Legacy aliases are provided for backward compatibility with existing code.
 */
export const VM_STATES = {
  NO_MEETING: 'no_meeting',
  REQUESTED_BY_ME: 'requested_by_me',           // Current user requested
  REQUESTED_BY_OTHER: 'requested_by_other',     // Other party requested
  BOOKED_AWAITING_CONFIRMATION: 'booked_awaiting_confirmation',
  CONFIRMED: 'confirmed',
  DECLINED: 'declined',
  EXPIRED: 'expired',                           // All suggested dates passed without booking
  // Legacy aliases (for backward compatibility - will be removed in future)
  REQUESTED_BY_GUEST: 'requested_by_me',        // Alias → REQUESTED_BY_ME
  REQUESTED_BY_HOST: 'requested_by_other'       // Alias → REQUESTED_BY_OTHER
};

/**
 * Parse suggested dates - handles both array and JSON string formats
 * @param {Array|string} suggestedDates - Dates as array or JSON string
 * @returns {Array} Array of date strings
 */
function parseSuggestedDates(suggestedDates) {
  if (!suggestedDates) return [];

  // If already an array, return as-is
  if (Array.isArray(suggestedDates)) return suggestedDates;

  // If it's a string, try to parse it as JSON
  if (typeof suggestedDates === 'string') {
    try {
      const parsed = JSON.parse(suggestedDates);
      return Array.isArray(parsed) ? parsed : [];
    } catch (e) {
      return [];
    }
  }

  return [];
}

/**
 * Check if all suggested dates have expired (are in the past)
 *
 * @param {Object} virtualMeeting - Virtual meeting object (normalized)
 * @returns {boolean} True if all dates are expired
 */
export function areAllDatesExpired(virtualMeeting) {
  if (!virtualMeeting) return false;

  const now = new Date();
  const bookedDate = virtualMeeting.bookedDate || virtualMeeting['booked date'];
  const rawSuggestedDates = virtualMeeting.suggestedDates || virtualMeeting['suggested dates and times'];

  // Parse suggested dates (handles JSON string or array)
  const suggestedDates = parseSuggestedDates(rawSuggestedDates);

  // If there's a booked date, check if it's in the past
  if (bookedDate) {
    return new Date(bookedDate) < now;
  }

  // If no suggested dates, not expired (just pending)
  if (suggestedDates.length === 0) {
    return false;
  }

  // Check if ALL suggested dates are in the past
  return suggestedDates.every(dateStr => new Date(dateStr) < now);
}

/**
 * Determine the current state of a virtual meeting
 *
 * @param {Object} virtualMeeting - Virtual meeting object
 * @param {string} currentUserId - Current user's ID
 * @returns {string} VM_STATES value
 */
export function getVirtualMeetingState(virtualMeeting, currentUserId) {
  // State 1: No VM exists
  if (!virtualMeeting) {
    return VM_STATES.NO_MEETING;
  }

  // State 5: VM declined
  if (virtualMeeting.meetingDeclined) {
    return VM_STATES.DECLINED;
  }

  // State 4: VM confirmed by Split Lease
  if (virtualMeeting.bookedDate && virtualMeeting.confirmedBySplitlease) {
    return VM_STATES.CONFIRMED;
  }

  // State 3: VM booked but not confirmed
  if (virtualMeeting.bookedDate && !virtualMeeting.confirmedBySplitlease) {
    return VM_STATES.BOOKED_AWAITING_CONFIRMATION;
  }

  // State 6: All suggested dates expired without booking
  // Check AFTER booked states but BEFORE requested states
  if (areAllDatesExpired(virtualMeeting)) {
    return VM_STATES.EXPIRED;
  }

  // State 2: VM requested but no booked date yet
  if (virtualMeeting.requestedBy === currentUserId) {
    return VM_STATES.REQUESTED_BY_ME;
  }

  // Other party requested, current user should respond
  return VM_STATES.REQUESTED_BY_OTHER;
}

/**
 * Check if a new VM can be requested
 *
 * @param {Object} virtualMeeting - Virtual meeting object (or null)
 * @returns {boolean} True if new request can be made
 */
export function canRequestNewMeeting(virtualMeeting) {
  if (!virtualMeeting) {
    return true;
  }

  // Can request new meeting if previous was declined
  if (virtualMeeting.meetingDeclined) {
    return true;
  }

  // Can request new meeting if all dates expired
  if (areAllDatesExpired(virtualMeeting)) {
    return true;
  }

  return false;
}

/**
 * Check if guest can respond to a VM request
 *
 * @param {Object} virtualMeeting - Virtual meeting object
 * @param {string} currentUserId - Current user's ID
 * @returns {boolean} True if guest can respond
 */
export function canRespondToMeeting(virtualMeeting, currentUserId) {
  if (!virtualMeeting) {
    return false;
  }

  // Can respond if host requested and no booked date yet
  return (
    virtualMeeting.requestedBy !== currentUserId &&
    !virtualMeeting.bookedDate &&
    !virtualMeeting.meetingDeclined
  );
}

/**
 * Check if VM button should be disabled
 *
 * @param {Object} virtualMeeting - Virtual meeting object
 * @param {string} currentUserId - Current user's ID
 * @returns {boolean} True if button should be disabled
 */
export function isVMButtonDisabled(virtualMeeting, currentUserId) {
  if (!virtualMeeting) {
    return false; // Can request new meeting
  }

  // Disable if guest requested and waiting for host response
  if (
    virtualMeeting.requestedBy === currentUserId &&
    !virtualMeeting.bookedDate &&
    !virtualMeeting.meetingDeclined
  ) {
    return true;
  }

  return false;
}

/**
 * Check if meeting can be joined
 *
 * @param {Object} virtualMeeting - Virtual meeting object
 * @returns {boolean} True if meeting can be joined
 */
export function canJoinMeeting(virtualMeeting) {
  if (!virtualMeeting) {
    return false;
  }

  return (
    virtualMeeting.bookedDate &&
    virtualMeeting.confirmedBySplitlease &&
    virtualMeeting.meetingLink
  );
}

/**
 * Check if meeting details can be viewed
 *
 * @param {Object} virtualMeeting - Virtual meeting object
 * @returns {boolean} True if meeting details can be viewed
 */
export function canViewMeetingDetails(virtualMeeting) {
  if (!virtualMeeting) {
    return false;
  }

  return virtualMeeting.bookedDate !== null;
}

/**
 * Check if guest can cancel their own VM request
 *
 * @param {Object} virtualMeeting - Virtual meeting object
 * @param {string} currentUserId - Current user's ID
 * @returns {boolean} True if request can be cancelled
 */
export function canCancelVMRequest(virtualMeeting, currentUserId) {
  if (!virtualMeeting) {
    return false;
  }

  // Can only cancel if guest initiated the request
  // and meeting hasn't been booked or confirmed yet
  return (
    virtualMeeting.requestedBy === currentUserId &&
    !virtualMeeting.bookedDate &&
    !virtualMeeting.confirmedBySplitlease &&
    !virtualMeeting.meetingDeclined
  );
}

/**
 * Get the appropriate VM button text based on current state
 *
 * @param {Object} virtualMeeting - Virtual meeting object
 * @param {string} currentUserId - Current user's ID
 * @returns {string} Button text
 */
export function getVMButtonText(virtualMeeting, currentUserId) {
  if (!virtualMeeting) {
    return 'Request Virtual Meeting';
  }

  if (virtualMeeting.meetingDeclined) {
    return 'Request Alternative Meeting';
  }

  // Check for expired dates
  if (areAllDatesExpired(virtualMeeting) && !virtualMeeting.bookedDate) {
    return 'Request New Times';
  }

  if (!virtualMeeting.bookedDate) {
    if (virtualMeeting.requestedBy === currentUserId) {
      return 'Meeting Requested';
    } else {
      return 'Respond to Virtual Meeting';
    }
  }

  if (virtualMeeting.bookedDate && !virtualMeeting.confirmedBySplitlease) {
    return 'View Meeting Details';
  }

  if (virtualMeeting.bookedDate && virtualMeeting.confirmedBySplitlease) {
    return 'Join Virtual Meeting';
  }

  return 'Virtual Meeting';
}

/**
 * Get button style class based on VM state
 *
 * @param {Object} virtualMeeting - Virtual meeting object
 * @param {string} currentUserId - Current user's ID
 * @returns {string} Button style class
 */
export function getVMButtonStyle(virtualMeeting, currentUserId) {
  if (!virtualMeeting) {
    return 'primary';
  }

  if (virtualMeeting.meetingDeclined) {
    return 'warning';
  }

  // Expired state - show warning/amber style to prompt action
  if (areAllDatesExpired(virtualMeeting) && !virtualMeeting.bookedDate) {
    return 'expired';
  }

  if (virtualMeeting.bookedDate && virtualMeeting.confirmedBySplitlease) {
    return 'success';
  }

  if (virtualMeeting.requestedBy === currentUserId && !virtualMeeting.bookedDate) {
    return 'disabled';
  }

  return 'secondary';
}

/**
 * Get comprehensive VM state info for UI rendering
 *
 * @param {Object} virtualMeeting - Virtual meeting object
 * @param {string} currentUserId - Current user's ID
 * @returns {Object} State info object
 */
export function getVMStateInfo(virtualMeeting, currentUserId) {
  const state = getVirtualMeetingState(virtualMeeting, currentUserId);

  return {
    state,
    showButton: true,
    buttonText: getVMButtonText(virtualMeeting, currentUserId),
    buttonStyle: getVMButtonStyle(virtualMeeting, currentUserId),
    buttonDisabled: isVMButtonDisabled(virtualMeeting, currentUserId),
    canRequest: canRequestNewMeeting(virtualMeeting),
    canRespond: canRespondToMeeting(virtualMeeting, currentUserId),
    canJoin: canJoinMeeting(virtualMeeting),
    canViewDetails: canViewMeetingDetails(virtualMeeting),
    canCancel: canCancelVMRequest(virtualMeeting, currentUserId)
  };
}

/**
 * Check if a specific date is blocked.
 *
 * @intent Determine if a date is unavailable due to blocking.
 * @rule Compares dates in YYYY-MM-DD format (ignoring time).
 *
 * @param {object} params - Named parameters.
 * @param {Date} params.date - Date to check.
 * @param {Array} params.blockedDates - Array of blocked date strings or Date objects.
 * @returns {boolean} True if date is blocked, false otherwise.
 *
 * @throws {Error} If date is not a valid Date object.
 * @throws {Error} If blockedDates is not an array.
 *
 * @example
 * const blocked = isDateBlocked({
 *   date: new Date('2025-12-25'),
 *   blockedDates: ['2025-12-25', '2025-12-26']
 * })
 * // => true
 */
export function isDateBlocked({ date, blockedDates }) {
  // No Fallback: Validate inputs
  if (!(date instanceof Date) || isNaN(date.getTime())) {
    throw new Error(
      'isDateBlocked: date must be a valid Date object'
    )
  }

  if (!Array.isArray(blockedDates)) {
    throw new Error(
      `isDateBlocked: blockedDates must be an array, got ${typeof blockedDates}`
    )
  }

  // Empty blockedDates array means nothing is blocked
  if (blockedDates.length === 0) {
    return false
  }

  // Format the check date as YYYY-MM-DD
  const dateStr = date.toISOString().split('T')[0]

  // Check if any blocked date matches
  return blockedDates.some(blocked => {
    if (typeof blocked === 'string') {
      // Extract date portion (YYYY-MM-DD) from string
      const blockedDateStr = blocked.split('T')[0]
      return blockedDateStr === dateStr
    }
    if (blocked instanceof Date) {
      const blockedDateStr = blocked.toISOString().split('T')[0]
      return blockedDateStr === dateStr
    }
    // Skip invalid entries silently (they don't block anything)
    return false
  })
}

/**
 * Check if a date is within the available range.
 *
 * @intent Validate that a date falls within the listing's availability window.
 * @rule Date must be >= firstAvailable (if specified).
 * @rule Date must be <= lastAvailable (if specified).
 * @rule Null/undefined bounds are treated as unbounded (no restriction).
 *
 * @param {object} params - Named parameters.
 * @param {Date} params.date - Date to check.
 * @param {string|Date|null} params.firstAvailable - First available date (inclusive).
 * @param {string|Date|null} params.lastAvailable - Last available date (inclusive).
 * @returns {boolean} True if date is within range, false otherwise.
 *
 * @throws {Error} If date is not a valid Date object.
 *
 * @example
 * const inRange = isDateInRange({
 *   date: new Date('2025-12-15'),
 *   firstAvailable: '2025-12-01',
 *   lastAvailable: '2026-01-31'
 * })
 * // => true
 */
export function isDateInRange({ date, firstAvailable, lastAvailable }) {
  // No Fallback: Validate date
  if (!(date instanceof Date) || isNaN(date.getTime())) {
    throw new Error(
      'isDateInRange: date must be a valid Date object'
    )
  }

  // Normalize check date (ignore time component)
  const checkDate = new Date(date)
  checkDate.setHours(0, 0, 0, 0)

  // Check first available bound
  if (firstAvailable !== null && firstAvailable !== undefined) {
    const firstDate = new Date(firstAvailable)
    if (isNaN(firstDate.getTime())) {
      throw new Error(
        `isDateInRange: firstAvailable is not a valid date: ${firstAvailable}`
      )
    }
    firstDate.setHours(0, 0, 0, 0)
    if (checkDate < firstDate) {
      return false
    }
  }

  // Check last available bound
  if (lastAvailable !== null && lastAvailable !== undefined) {
    const lastDate = new Date(lastAvailable)
    if (isNaN(lastDate.getTime())) {
      throw new Error(
        `isDateInRange: lastAvailable is not a valid date: ${lastAvailable}`
      )
    }
    lastDate.setHours(0, 0, 0, 0)
    if (checkDate > lastDate) {
      return false
    }
  }

  return true
}

/**
 * Check if selected days form a contiguous (consecutive) block.
 *
 * @intent Enforce the business rule that split lease stays must be consecutive nights.
 * @rule Days must be consecutive (Mon-Fri ✓, Mon+Wed ✗).
 * @rule Handles week wrap-around cases (Fri-Sun ✓, Sat-Tue ✓).
 * @rule For wrap-around, uses inverse logic: if unselected days are contiguous, selected days wrap around properly.
 * @rule 6 or more days is always contiguous (only 1 gap maximum).
 *
 * This function consolidates the contiguous validation logic that previously existed in:
 * - availabilityValidation.js (isContiguousSelection)
 * - scheduleSelector/validators.js (isContiguous)
 *
 * @param {object} params - Named parameters.
 * @param {number[]} params.selectedDayIndices - Array of day indices (0=Sunday, 1=Monday, ..., 6=Saturday).
 * @returns {boolean} True if days are contiguous, false otherwise.
 *
 * @throws {Error} If selectedDayIndices is not an array.
 * @throws {Error} If any day index is invalid (not 0-6).
 *
 * @example
 * isScheduleContiguous({ selectedDayIndices: [1, 2, 3, 4, 5] }) // => true (Mon-Fri)
 * isScheduleContiguous({ selectedDayIndices: [1, 3, 5] }) // => false (Mon, Wed, Fri - not contiguous)
 * isScheduleContiguous({ selectedDayIndices: [5, 6, 0] }) // => true (Fri-Sun, wraps around week)
 * isScheduleContiguous({ selectedDayIndices: [6, 0, 1, 2] }) // => true (Sat-Tue, wraps around week)
 */
export function isScheduleContiguous({ selectedDayIndices }) {
  // No Fallback: Validate input
  if (!Array.isArray(selectedDayIndices)) {
    throw new Error(
      `isScheduleContiguous: selectedDayIndices must be an array, got ${typeof selectedDayIndices}`
    )
  }

  // Empty selection is not contiguous
  if (selectedDayIndices.length === 0) {
    return false
  }

  // Single day is contiguous
  if (selectedDayIndices.length === 1) {
    return true
  }

  // Validate all day indices
  for (const day of selectedDayIndices) {
    if (typeof day !== 'number' || isNaN(day) || day < 0 || day > 6) {
      throw new Error(
        `isScheduleContiguous: Invalid day index ${day}, must be 0-6`
      )
    }
  }

  // Sort the selected days (immutable)
  const sorted = selectedDayIndices.toSorted((a, b) => a - b)

  // If 6 or more days selected, it's contiguous (only 1 gap or no gaps)
  if (sorted.length >= 6) {
    return true
  }

  // Check for standard contiguous sequence (no wrap around)
  let isStandardContiguous = true
  for (let i = 1; i < sorted.length; i++) {
    if (sorted[i] !== sorted[i - 1] + 1) {
      isStandardContiguous = false
      break
    }
  }

  if (isStandardContiguous) {
    return true
  }

  // Check if selection includes both Sunday (0) and Saturday (6) - wrap-around case
  const hasZero = sorted.includes(0)
  const hasSix = sorted.includes(6)

  if (hasZero && hasSix) {
    // Week wrap-around case: use inverse logic (check not-selected days)
    // If the NOT selected days are contiguous, then selected days wrap around and are contiguous
    const allDays = [0, 1, 2, 3, 4, 5, 6]
    const notSelectedDays = allDays.filter(d => !sorted.includes(d))

    if (notSelectedDays.length === 0) {
      return true // All days selected
    }

    // Check if not-selected days form a contiguous block
    const minNotSelected = Math.min(...notSelectedDays)
    const maxNotSelected = Math.max(...notSelectedDays)

    // Generate expected contiguous range for not-selected days
    const expectedNotSelected = Array.from(
      { length: maxNotSelected - minNotSelected + 1 },
      (_, i) => minNotSelected + i
    )

    // If not-selected days are contiguous, then selected days wrap around properly
    const notSelectedContiguous = notSelectedDays.length === expectedNotSelected.length &&
      notSelectedDays.every((day, index) => day === expectedNotSelected[index])

    return notSelectedContiguous
  }

  return false
}

/**
 * Check if a listing has photos.
 *
 * @intent Validate that a listing has at least one photo for search display.
 * @rule Listings without photos must not appear in search results.
 *
 * @param {object} params - Named parameters.
 * @param {object} params.listing - The listing object to check.
 * @returns {boolean} True if listing has at least one photo.
 *
 * @throws {Error} If listing is null or undefined.
 *
 * @example
 * const valid = hasListingPhotos({ listing: { 'Features - Photos': ['photo1'] } })
 * // => true
 *
 * const invalid = hasListingPhotos({ listing: { 'Features - Photos': [] } })
 * // => false
 */
export function hasListingPhotos({ listing }) {
  // No Fallback: Validate input
  if (listing === null || listing === undefined) {
    throw new Error('hasListingPhotos: listing cannot be null or undefined')
  }

  const photos = listing['Features - Photos']

  // Check for null, undefined, or empty array
  if (!photos) {
    return false
  }

  // Handle string representation (JSON array from database)
  if (typeof photos === 'string') {
    try {
      const parsed = JSON.parse(photos)
      return Array.isArray(parsed) && parsed.length > 0
    } catch {
      return false
    }
  }

  // Handle array directly
  if (Array.isArray(photos)) {
    return photos.length > 0
  }

  return false
}

/**
 * Determines if a user has a valid profile photo URL.
 *
 * @function hasProfilePhoto
 * @intent Check if user has uploaded a profile photo to display in UI
 * @rule A profile photo is valid if the URL is a non-empty string
 * @rule Empty strings, null, and undefined are considered "no photo"
 *
 * @param {object} params - Named parameters
 * @param {string|null|undefined} params.photoUrl - The profile photo URL from user data
 * @returns {boolean} True if user has a valid profile photo URL
 *
 * @example
 * hasProfilePhoto({ photoUrl: 'https://example.com/photo.jpg' }) // true
 * hasProfilePhoto({ photoUrl: '' }) // false
 * hasProfilePhoto({ photoUrl: null }) // false
 * hasProfilePhoto({ photoUrl: undefined }) // false
 */
export function hasProfilePhoto({ photoUrl }) {
  // NO FALLBACK: Explicit check without defaulting
  if (photoUrl === null || photoUrl === undefined) {
    return false;
  }

  // Check if photoUrl is a non-empty string
  if (typeof photoUrl !== 'string') {
    return false;
  }

  // Empty string is considered "no photo"
  return photoUrl.trim().length > 0;
}

/**
 * Determine if a user type indicates Guest privileges.
 *
 * @intent Enforce business rules for Guest role identification.
 * @rule Guest types from Supabase:
 *   - "A Guest (I would like to rent a space)"
 *   - "Split Lease" (internal users with both Host and Guest privileges)
 *
 * @param {object} params - Named parameters.
 * @param {string|null} params.userType - The user type value from Supabase or localStorage.
 * @returns {boolean} True if user has Guest privileges.
 *
 * @example
 * isGuest({ userType: 'A Guest (I would like to rent a space)' })
 * // => true
 *
 * isGuest({ userType: 'A Host (I have a space available to rent)' })
 * // => false
 *
 * isGuest({ userType: 'Split Lease' })
 * // => true (internal users have both roles)
 */
export function isGuest({ userType }) {
  // No user type means not authenticated or type not set
  if (!userType || typeof userType !== 'string') {
    return false
  }

  const type = userType.trim()

  // Split Lease internal users have both Host and Guest privileges
  if (type === 'Split Lease') {
    return true
  }

  // Check if type includes "Guest"
  return type.includes('Guest')
}

/**
 * Determine if a user type indicates Host privileges.
 *
 * @intent Enforce business rules for Host role identification.
 * @rule Host types from Supabase:
 *   - "A Host (I have a space available to rent)"
 *   - "Trial Host"
 *   - "Split Lease" (internal users with both Host and Guest privileges)
 *
 * @param {object} params - Named parameters.
 * @param {string|null} params.userType - The user type value from Supabase or localStorage.
 * @returns {boolean} True if user has Host privileges.
 *
 * @example
 * isHost({ userType: 'A Host (I have a space available to rent)' })
 * // => true
 *
 * isHost({ userType: 'A Guest (I would like to rent a space)' })
 * // => false
 *
 * isHost({ userType: 'Split Lease' })
 * // => true (internal users have both roles)
 */
export function isHost({ userType }) {
  // No user type means not authenticated or type not set
  if (!userType || typeof userType !== 'string') {
    return false
  }

  const type = userType.trim()

  // Split Lease internal users have both Host and Guest privileges
  if (type === 'Split Lease') {
    return true
  }

  // Check if type includes "Host" (covers both regular and trial hosts)
  return type.includes('Host')
}

