/**
 * Determines if the full user name (first + last) should be displayed.
 *
 * @function shouldShowFullName
 * @intent Business rule for when to show full name vs first name only
 * @rule Show full name only if lastName exists and viewport is not mobile
 * @rule Mobile viewports show first name only for space conservation
 * @rule If no lastName, always show first name only
 *
 * @param {object} params - Named parameters
 * @param {string} params.firstName - User's first name (required)
 * @param {string|null|undefined} params.lastName - User's last name (optional)
 * @param {boolean} params.isMobile - Whether the viewport is mobile sized
 * @returns {boolean} True if full name should be displayed
 *
 * @example
 * shouldShowFullName({ firstName: 'John', lastName: 'Doe', isMobile: false }) // true
 * shouldShowFullName({ firstName: 'John', lastName: 'Doe', isMobile: true }) // false
 * shouldShowFullName({ firstName: 'John', lastName: null, isMobile: false }) // false
 */
export function shouldShowFullName({ firstName, lastName, isMobile }) {
  // NO FALLBACK: firstName is required
  if (typeof firstName !== 'string' || firstName.trim().length === 0) {
    throw new Error('shouldShowFullName requires a valid firstName');
  }

  // Validate isMobile is a boolean
  if (typeof isMobile !== 'boolean') {
    throw new Error('shouldShowFullName requires isMobile to be a boolean');
  }

  // Don't show full name on mobile (space constraints)
  if (isMobile) {
    return false;
  }

  // Don't show full name if lastName is missing or empty
  if (lastName === null || lastName === undefined) {
    return false;
  }

  if (typeof lastName !== 'string' || lastName.trim().length === 0) {
    return false;
  }

  // Show full name: not mobile AND has valid lastName
  return true;
}

/**
 * Check authentication status workflow.
 * Orchestrates checking cookies, secure storage, and session validation.
 *
 * @intent Determine if user is currently authenticated across multiple sources.
 * @rule Checks Split Lease cookies first (cross-domain compatibility).
 * @rule Falls back to secure storage tokens.
 * @rule Validates session hasn't expired.
 *
 * This workflow coordinates infrastructure layer (cookies, storage) with
 * business rules (session validity).
 *
 * @param {object} params - Named parameters.
 * @param {object} params.splitLeaseCookies - Split Lease cookie auth status.
 * @param {boolean} params.splitLeaseCookies.isLoggedIn - Logged in via cookies.
 * @param {string} params.splitLeaseCookies.username - Username from cookies.
 * @param {boolean} params.authState - Auth state from secure storage.
 * @param {boolean} params.hasValidTokens - Whether valid tokens exist.
 * @returns {object} Authentication status with source information.
 *
 * @example
 * const status = checkAuthStatusWorkflow({
 *   splitLeaseCookies: { isLoggedIn: true, username: 'john@example.com' },
 *   authState: true,
 *   hasValidTokens: true
 * })
 * // => { isAuthenticated: true, source: 'cookies', username: 'john@example.com' }
 */
export function checkAuthStatusWorkflow({
  splitLeaseCookies,
  authState,
  hasValidTokens
}) {
  // No Fallback: Validate inputs
  if (!splitLeaseCookies || typeof splitLeaseCookies !== 'object') {
    throw new Error(
      'checkAuthStatusWorkflow: splitLeaseCookies is required'
    )
  }

  if (typeof authState !== 'boolean') {
    throw new Error(
      'checkAuthStatusWorkflow: authState must be a boolean'
    )
  }

  if (typeof hasValidTokens !== 'boolean') {
    throw new Error(
      'checkAuthStatusWorkflow: hasValidTokens must be a boolean'
    )
  }

  // Priority 1: Check Split Lease cookies (cross-domain compatibility)
  if (splitLeaseCookies.isLoggedIn) {
    return {
      isAuthenticated: true,
      source: 'cookies',
      username: splitLeaseCookies.username
    }
  }

  // Priority 2: Check secure storage tokens
  if (authState && hasValidTokens) {
    return {
      isAuthenticated: true,
      source: 'secure_storage',
      username: null // Username would need to be fetched separately
    }
  }

  // Not authenticated
  return {
    isAuthenticated: false,
    source: null,
    username: null
  }
}

/**
 * Validate token and fetch user data workflow.
 * Three-step process to validate authentication and retrieve user profile.
 *
 * @intent Validate user's authentication token and fetch their profile data.
 * @rule Step 1: Validate token via Bubble API (authentication check).
 * @rule Step 2: Fetch user display data from Supabase.
 * @rule Step 3: Fetch and cache user type if not already stored.
 *
 * This is an orchestration workflow that coordinates:
 * - External API validation (Bubble)
 * - Database queries (Supabase)
 * - Data transformation (user profile)
 *
 * @param {object} params - Named parameters.
 * @param {string} params.token - Authentication token to validate.
 * @param {string} params.userId - User ID for fetching profile.
 * @param {Function} params.bubbleValidateFn - Function to validate token with Bubble API.
 * @param {Function} params.supabaseFetchUserFn - Function to fetch user from Supabase.
 * @param {string|null} params.cachedUserType - Cached user type (if available).
 * @returns {Promise<object|null>} User data object or null if invalid.
 *
 * @throws {Error} If required parameters are missing.
 * @throws {Error} If validation or fetch functions are not provided.
 *
 * @example
 * const userData = await validateTokenWorkflow({
 *   token: 'abc123',
 *   userId: 'user_456',
 *   bubbleValidateFn: async (token, userId) => { ... },
 *   supabaseFetchUserFn: async (userId) => { ... },
 *   cachedUserType: 'Guest'
 * })
 * // => { userId: 'user_456', firstName: 'John', userType: 'Guest', ... }
 */
export async function validateTokenWorkflow({
  token,
  userId,
  bubbleValidateFn,
  supabaseFetchUserFn,
  cachedUserType = null
}) {
  // No Fallback: Strict validation
  if (!token || typeof token !== 'string') {
    throw new Error(
      'validateTokenWorkflow: token is required and must be a string'
    )
  }

  if (!userId || typeof userId !== 'string') {
    throw new Error(
      'validateTokenWorkflow: userId is required and must be a string'
    )
  }

  if (typeof bubbleValidateFn !== 'function') {
    throw new Error(
      'validateTokenWorkflow: bubbleValidateFn must be a function'
    )
  }

  if (typeof supabaseFetchUserFn !== 'function') {
    throw new Error(
      'validateTokenWorkflow: supabaseFetchUserFn must be a function'
    )
  }

  // Step 1: Validate token via Bubble API
  const isValidToken = await bubbleValidateFn(token, userId)

  if (!isValidToken) {
    // Token is invalid
    return null
  }

  // Step 2: Fetch user data from Supabase
  const userData = await supabaseFetchUserFn(userId)

  if (!userData) {
    // User not found
    return null
  }

  // Step 3: Handle user type (use cached or fetch from userData)
  let userType = cachedUserType

  if (!userType || userType === '') {
    userType = userData['Type - User Current'] || null
  }

  // Handle protocol-relative URLs for profile photos
  let profilePhoto = userData['Profile Photo']
  if (profilePhoto && profilePhoto.startsWith('//')) {
    profilePhoto = 'https:' + profilePhoto
  }

  // Return user data object
  return {
    userId: userData._id,
    firstName: userData['Name - First'] || null,
    fullName: userData['Name - Full'] || null,
    profilePhoto: profilePhoto || null,
    userType: userType
  }
}

/**
 * Workflow: Accept a host's counteroffer proposal.
 *
 * @intent Orchestrate acceptance of host counteroffer following business rules.
 * @rule Only applicable when proposal status is "Host Countered".
 * @rule Updates status to "Accepted" and sets acceptance timestamp.
 * @rule Validates proposal can be accepted before attempting update.
 *
 * @param {object} params - Named parameters.
 * @param {object} params.supabase - Supabase client instance.
 * @param {object} params.proposal - Processed proposal object.
 * @param {function} params.canAcceptProposal - Rule function to check if acceptable.
 * @returns {Promise<object>} Result object with success status and message.
 *
 * @throws {Error} If required parameters are missing.
 *
 * @example
 * const result = await acceptProposalWorkflow({
 *   supabase,
 *   proposal: processedProposal,
 *   canAcceptProposal
 * })
 * // => { success: true, message: 'Proposal accepted', updated: true }
 */
import { PROPOSAL_STATUSES } from '../../constants/proposalStatuses.js'

export async function acceptProposalWorkflow({
  supabase,
  proposal,
  canAcceptProposal
}) {
  // Validation
  if (!supabase) {
    throw new Error('acceptProposalWorkflow: supabase client is required')
  }

  if (!proposal || !proposal.id) {
    throw new Error('acceptProposalWorkflow: proposal with id is required')
  }

  if (!canAcceptProposal) {
    throw new Error('acceptProposalWorkflow: canAcceptProposal rule function is required')
  }

  // Step 1: Check if acceptance is allowed
  const canAccept = canAcceptProposal({
    proposalStatus: proposal.status,
    deleted: proposal.deleted
  })

  if (!canAccept) {
    return {
      success: false,
      message: 'This proposal cannot be accepted (not in "Host Countered" status)',
      updated: false
    }
  }

  // Step 2: Update proposal status to "Accepted"
  try {
    const { error } = await supabase
      .from('proposal')
      .update({
        'Status': PROPOSAL_STATUSES.PROPOSAL_OR_COUNTEROFFER_ACCEPTED.key,
        'Modified Date': new Date().toISOString()
      })
      .eq('_id', proposal.id)

    if (error) {
      throw error
    }

    return {
      success: true,
      message: 'Proposal accepted successfully! The host will be notified.',
      updated: true
    }
  } catch (err) {
    return {
      success: false,
      message: `Failed to accept proposal: ${err.message}`,
      updated: false,
      error: err
    }
  }
}

/**
 * Workflow: Cancel a proposal with complex decision tree.
 *
 * @intent Orchestrate proposal cancellation following business rules.
 * @rule Implements complete cancellation decision tree (7 variations).
 * @rule Different actions based on: source, current status, usual order, house manual access.
 * @rule Updates Supabase with appropriate status and cancellation reason.
 *
 * Decision Tree (from WORKFLOW-PASS2-ASSIMILATION.md):
 * 1. Already cancelled → Show alert, no action
 * 2. Compare modal source + Usual Order ≤5 → Status: "Cancelled by Guest"
 * 3. Compare modal source + Usual Order >5 → Alert to call, no DB update
 * 4. Main page + Usual Order ≤5 + No house manual → Status: "Cancelled by Guest"
 * 5. Main page + Usual Order >5 + No house manual → Alert to call, no DB update
 * 6. Main page + Usual Order ≤5 + Has house manual → Alert to call, no DB update
 * 7. Main page + Usual Order >5 + Has house manual → Alert to call, no DB update
 *
 * @param {object} params - Named parameters.
 * @param {object} params.supabase - Supabase client instance.
 * @param {object} params.proposal - Processed proposal object.
 * @param {string} params.source - 'main' | 'compare-modal' | 'other'.
 * @param {function} params.canCancelProposal - Rule function to check if cancellable.
 * @returns {Promise<object>} Result object with success status and message.
 *
 * @throws {Error} If required parameters are missing.
 *
 * @example
 * const result = await cancelProposalWorkflow({
 *   supabase,
 *   proposal: processedProposal,
 *   source: 'main',
 *   canCancelProposal
 * })
 * // => { success: true, message: 'Proposal cancelled', updated: true }
 */
import { PROPOSAL_STATUSES } from '../../constants/proposalStatuses.js'

export async function cancelProposalWorkflow({
  supabase,
  proposal,
  source = 'main',
  canCancelProposal
}) {
  // Validation
  if (!supabase) {
    throw new Error('cancelProposalWorkflow: supabase client is required')
  }

  if (!proposal || !proposal.id) {
    throw new Error('cancelProposalWorkflow: proposal with id is required')
  }

  if (!canCancelProposal) {
    throw new Error('cancelProposalWorkflow: canCancelProposal rule function is required')
  }

  // Step 1: Check if cancellation is allowed
  const canCancel = canCancelProposal({
    proposalStatus: proposal.status,
    deleted: proposal.deleted
  })

  if (!canCancel) {
    return {
      success: false,
      message: 'This proposal cannot be cancelled (already in terminal state)',
      updated: false
    }
  }

  // Step 2: Extract decision factors
  const usualOrder = proposal.usualOrder || 0
  const hasAccessedHouseManual = proposal.houseManualAccessed === true
  const isFromCompareModal = source === 'compare-modal'

  // Step 3: Apply decision tree
  let shouldUpdateDatabase = false
  let alertMessage = null

  if (isFromCompareModal) {
    // Compare Modal Source
    if (usualOrder <= 5) {
      // Variation 2: Update database
      shouldUpdateDatabase = true
    } else {
      // Variation 3: Alert to call
      alertMessage = 'Please call Split Lease to cancel this proposal (Usual Order > 5)'
    }
  } else {
    // Main Page Source
    if (!hasAccessedHouseManual) {
      if (usualOrder <= 5) {
        // Variation 4: Update database
        shouldUpdateDatabase = true
      } else {
        // Variation 5: Alert to call
        alertMessage = 'Please call Split Lease to cancel this proposal (Usual Order > 5)'
      }
    } else {
      // Has accessed house manual - always alert to call (Variations 6 & 7)
      alertMessage = 'Please call Split Lease to cancel this proposal (House Manual accessed)'
    }
  }

  // Step 4: Execute action
  if (shouldUpdateDatabase) {
    try {
      const { error } = await supabase
        .from('proposal')
        .update({
          'Status': PROPOSAL_STATUSES.CANCELLED_BY_GUEST.key,
          'reason for cancellation': 'Guest initiated cancellation',
          'Modified Date': new Date().toISOString()
        })
        .eq('_id', proposal.id)

      if (error) {
        throw error
      }

      return {
        success: true,
        message: 'Proposal cancelled successfully',
        updated: true
      }
    } catch (err) {
      return {
        success: false,
        message: `Failed to cancel proposal: ${err.message}`,
        updated: false,
        error: err
      }
    }
  } else {
    // Alert only, no database update
    return {
      success: true,
      message: alertMessage,
      updated: false,
      requiresPhoneCall: true
    }
  }
}

/**
 * Workflow: Load complete proposal details with all enrichments.
 *
 * @intent Orchestrate loading and processing of proposal data from Supabase.
 * @rule Separates infrastructure (fetching) from domain logic (processing).
 * @rule Fetches raw data, then transforms via processors.
 * @rule Returns processed, validated proposal object ready for UI consumption.
 *
 * This workflow replaces the mixed fetcher/transformer pattern from proposalDataFetcher.js
 * by explicitly separating:
 * - Data fetching (infrastructure - still uses Supabase client)
 * - Data processing (domain logic - uses Logic Core processors)
 *
 * @param {object} params - Named parameters.
 * @param {object} params.supabase - Supabase client instance.
 * @param {object} params.rawProposal - Base proposal object (minimal data).
 * @param {object} [params.processListingData] - Listing processor function.
 * @param {object} [params.processUserData] - User processor function.
 * @param {object} [params.processProposalData] - Proposal processor function.
 * @returns {Promise<object>} Fully enriched and processed proposal object.
 *
 * @throws {Error} If rawProposal is missing.
 * @throws {Error} If critical data cannot be loaded.
 *
 * @example
 * import { loadProposalDetailsWorkflow } from '../logic/workflows/booking/loadProposalDetailsWorkflow.js'
 * import { processProposalData } from '../logic/processors/proposal/processProposalData.js'
 * import { processUserData } from '../logic/processors/user/processUserData.js'
 * import { processListingData } from '../logic/processors/listing/processListingData.js'
 * import { supabase } from '../lib/supabase.js'
 *
 * const enrichedProposal = await loadProposalDetailsWorkflow({
 *   supabase,
 *   rawProposal: { _id: 'abc123', Listing: 'xyz', Guest: '456' },
 *   processProposalData,
 *   processUserData,
 *   processListingData
 * })
 */
export async function loadProposalDetailsWorkflow({
  supabase,
  rawProposal,
  processListingData,
  processUserData,
  processProposalData
}) {
  // Validation
  if (!rawProposal) {
    throw new Error('loadProposalDetailsWorkflow: rawProposal is required')
  }

  if (!supabase) {
    throw new Error('loadProposalDetailsWorkflow: supabase client is required')
  }

  // Step 1: Fetch listing data
  let processedListing = null
  if (rawProposal.Listing) {
    const { data: listingData, error: listingError } = await supabase
      .from('listing')
      .select('*')
      .eq('_id', rawProposal.Listing)
      .single()

    if (!listingError && listingData) {
      // Process listing if processor provided
      if (processListingData) {
        try {
          processedListing = processListingData({ rawListing: listingData })
        } catch (err) {
          console.warn('Warning: Failed to process listing data:', err.message)
          // Continue without processed listing - UI will handle gracefully
        }
      } else {
        // No processor provided, use raw data
        processedListing = listingData
      }
    }
  }

  // Step 2: Fetch guest user data
  let processedGuest = null
  if (rawProposal.Guest) {
    const { data: guestData, error: guestError } = await supabase
      .from('user')
      .select(`
        _id,
        "Name - First",
        "Name - Full",
        "Profile Photo",
        "About Me / Bio",
        "email as text",
        "Phone Number (as text)",
        "Verify - Linked In ID",
        "Verify - Phone",
        "is email confirmed",
        "user verified?"
      `)
      .eq('_id', rawProposal.Guest)
      .single()

    if (!guestError && guestData) {
      // Process user if processor provided
      if (processUserData) {
        try {
          processedGuest = processUserData({ rawUser: guestData })
        } catch (err) {
          console.warn('Warning: Failed to process guest data:', err.message)
        }
      } else {
        processedGuest = guestData
      }
    }
  }

  // Step 3: Fetch host user data (from listing creator)
  let processedHost = null
  if (processedListing && processedListing['Created By']) {
    const { data: hostData, error: hostError } = await supabase
      .from('user')
      .select(`
        _id,
        "Name - First",
        "Name - Full",
        "Profile Photo",
        "About Me / Bio",
        "email as text",
        "Phone Number (as text)",
        "Verify - Linked In ID",
        "Verify - Phone",
        "is email confirmed",
        "user verified?"
      `)
      .eq('_id', processedListing['Created By'])
      .single()

    if (!hostError && hostData) {
      // Process user if processor provided
      if (processUserData) {
        try {
          processedHost = processUserData({ rawUser: hostData })
        } catch (err) {
          console.warn('Warning: Failed to process host data:', err.message)
        }
      } else {
        processedHost = hostData
      }
    }
  }

  // Step 4: Fetch house rules (if any)
  let houseRules = null
  if (
    rawProposal['House Rules'] &&
    Array.isArray(rawProposal['House Rules']) &&
    rawProposal['House Rules'].length > 0
  ) {
    const { data: rulesData, error: rulesError } = await supabase
      .schema('reference_table')
      .from('zat_features_houserule')
      .select('_id, Name, Icon')
      .in('_id', rawProposal['House Rules'])

    if (!rulesError && rulesData) {
      houseRules = rulesData
    }
  }

  // Step 5: Fetch virtual meeting data (if any)
  let virtualMeeting = null
  if (rawProposal['virtual meeting']) {
    const { data: vmData, error: vmError } = await supabase
      .from('virtualmeetingschedulesandlinks')
      .select('*')
      .eq('_id', rawProposal['virtual meeting'])
      .single()

    if (!vmError && vmData) {
      virtualMeeting = vmData
    }
  }

  // Step 6: Process proposal with all enriched data
  let processedProposal
  if (processProposalData) {
    try {
      processedProposal = processProposalData({
        rawProposal,
        listing: processedListing,
        guest: processedGuest,
        host: processedHost
      })
    } catch (err) {
      // If processor throws, this is a critical error
      throw new Error(`Failed to process proposal data: ${err.message}`)
    }
  } else {
    // No processor provided, create basic structure
    processedProposal = {
      ...rawProposal,
      _listing: processedListing,
      _guest: processedGuest,
      _host: processedHost
    }
  }

  // Step 7: Attach additional enrichments
  if (houseRules) {
    processedProposal._houseRules = houseRules
  }

  if (virtualMeeting) {
    processedProposal._virtualMeeting = virtualMeeting
  }

  return processedProposal
}

