     1→import { isScheduleContiguous } from '../../rules/scheduling/isScheduleContiguous.js'
     2→
     3→/**
     4→ * Validate a schedule selection against listing requirements.
     5→ * Orchestrates multiple validation rules to determine if a schedule is acceptable.
     6→ *
     7→ * @intent Enforce all business rules for schedule selection in one workflow.
     8→ * @rule Days must be contiguous (consecutive).
     9→ * @rule Must have at least one day selected.
    10→ * @rule May optionally check against minimum/maximum nights from listing.
    11→ * @rule Returns error codes (not UI messages) - presentation layer maps to messages.
    12→ *
    13→ * @param {object} params - Named parameters.
    14→ * @param {number[]} params.selectedDayIndices - Array of selected day indices (0-6).
    15→ * @param {object} [params.listing] - Optional listing object with constraints.
    16→ * @param {number} [params.listing.minimumNights] - Minimum nights required.
    17→ * @param {number} [params.listing.maximumNights] - Maximum nights allowed.
    18→ * @param {string[]} [params.listing.daysNotAvailable] - Days not available (day names).
    19→ * @returns {object} Validation result with valid flag, errorCode, and metadata.
    20→ *
    21→ * @throws {Error} If selectedDayIndices is not an array.
    22→ *
    23→ * @example
    24→ * const result = validateScheduleWorkflow({
    25→ *   selectedDayIndices: [1, 2, 3, 4, 5],
    26→ *   listing: { minimumNights: 2, maximumNights: 7 }
    27→ * })
    28→ * // => { valid: true, errorCode: null, nightsCount: 5, isContiguous: true }
    29→ *
    30→ * const invalid = validateScheduleWorkflow({
    31→ *   selectedDayIndices: [1, 3, 5],
    32→ *   listing: {}
    33→ * })
    34→ * // => { valid: false, errorCode: 'NOT_CONTIGUOUS', nightsCount: 3, isContiguous: false }
    35→ */
