/**
 * Formats user's display name for UI presentation.
 *
 * @function processUserDisplayName
 * @intent Transform user name data into formatted display string
 * @rule If showFull is true and lastName exists, return "FirstName LastName"
 * @rule If showFull is false or no lastName, return "FirstName"
 * @rule NO FALLBACK: firstName is required, throw if missing
 * @rule Trims whitespace and ensures proper capitalization
 *
 * @param {object} params - Named parameters
 * @param {string} params.firstName - User's first name (required)
 * @param {string|null|undefined} params.lastName - User's last name (optional)
 * @param {boolean} params.showFull - Whether to show full name (first + last)
 * @returns {string} Formatted display name
 * @throws {Error} If firstName is missing or invalid
 *
 * @example
 * processUserDisplayName({ firstName: 'John', lastName: 'Doe', showFull: true }) // "John Doe"
 * processUserDisplayName({ firstName: 'John', lastName: 'Doe', showFull: false }) // "John"
 * processUserDisplayName({ firstName: 'Jane', lastName: null, showFull: true }) // "Jane"
 * processUserDisplayName({ firstName: '', lastName: 'Doe', showFull: true }) // throws Error
 */
export function processUserDisplayName({ firstName, lastName, showFull }) {
  // NO FALLBACK: firstName is mandatory for user identity
  if (typeof firstName !== 'string' || firstName.trim().length === 0) {
    throw new Error('processUserDisplayName requires a valid firstName. Cannot display user without name.');
  }

  // Validate showFull is a boolean
  if (typeof showFull !== 'boolean') {
    throw new Error('processUserDisplayName requires showFull to be a boolean');
  }

  // Normalize firstName (trim whitespace)
  const normalizedFirstName = firstName.trim();

  // If not showing full name or no lastName, return first name only
  if (!showFull || !lastName) {
    return normalizedFirstName;
  }

  // Validate lastName if we're trying to use it
  if (typeof lastName !== 'string' || lastName.trim().length === 0) {
    return normalizedFirstName;
  }

  // Return full name with space separator
  const normalizedLastName = lastName.trim();
  return `${normalizedFirstName} ${normalizedLastName}`;
}

/**
 * Generates user initials from first and last name for avatar display.
 *
 * @function processUserInitials
 * @intent Create 1-2 letter initials for avatar when no profile photo available
 * @rule If both names provided, use first letter of each (uppercase)
 * @rule If only firstName, use first letter (uppercase)
 * @rule NO FALLBACK: firstName is required, throw if missing
 *
 * @param {object} params - Named parameters
 * @param {string} params.firstName - User's first name (required)
 * @param {string|null|undefined} params.lastName - User's last name (optional)
 * @returns {string} 1-2 letter initials (e.g., "JD" or "J")
 * @throws {Error} If firstName is missing or invalid
 *
 * @example
 * processUserInitials({ firstName: 'John', lastName: 'Doe' }) // "JD"
 * processUserInitials({ firstName: 'Jane', lastName: null }) // "J"
 * processUserInitials({ firstName: 'Bob', lastName: '' }) // "B"
 * processUserInitials({ firstName: '', lastName: 'Smith' }) // throws Error
 */
export function processUserInitials({ firstName, lastName }) {
  // NO FALLBACK: firstName is mandatory for user identity
  if (typeof firstName !== 'string' || firstName.trim().length === 0) {
    throw new Error('processUserInitials requires a valid firstName. Cannot generate initials without user name.');
  }

  // Get first letter of firstName (uppercase)
  const firstInitial = firstName.trim().charAt(0).toUpperCase();

  // If lastName exists and is valid, add its first letter
  if (lastName && typeof lastName === 'string' && lastName.trim().length > 0) {
    const lastInitial = lastName.trim().charAt(0).toUpperCase();
    return firstInitial + lastInitial;
  }

  // Return just first initial if no valid lastName
  return firstInitial;
}

/**
 * Proposal Data Processor
 *
 * PILLAR III: Data Processors (The "Truth" Layer)
 *
 * This processor transforms raw Supabase/Bubble.io data into clean,
 * validated internal shapes. It enforces the "No Fallback" principle
 * by throwing explicit errors for missing critical data.
 *
 * Intent: Create safe, typed objects for UI consumption, enforcing data integrity.
 */

/**
 * Transform raw user data from Bubble.io format
 * @param {Object} rawUser - Raw user object from Supabase
 * @returns {Object} Transformed user object
 */
export function processUserData(rawUser) {
  if (!rawUser) {
    throw new Error('processUserData: User data is required');
  }

  if (!rawUser._id) {
    throw new Error('processUserData: User ID (_id) is required');
  }

  return {
    id: rawUser._id,
    firstName: rawUser['Name - First'] || null,
    lastName: rawUser['Name - Last'] || null,
    fullName: rawUser['Name - Full'] || null,
    profilePhoto: rawUser['Profile Photo'] || null,
    bio: rawUser['About Me / Bio'] || null,
    linkedInVerified: rawUser['Verify - Linked In ID'] || false,
    phoneVerified: rawUser['Verify - Phone'] || false,
    userVerified: rawUser['user verified?'] || false,
    proposalsList: rawUser['Proposals List'] || []
  };
}

/**
 * Transform raw listing data from Bubble.io format
 * @param {Object} rawListing - Raw listing object from Supabase
 * @returns {Object} Transformed listing object
 */
export function processListingData(rawListing) {
  if (!rawListing) {
    return null;
  }

  // Extract address from JSONB structure
  const addressData = rawListing['Location - Address'];
  const addressString = typeof addressData === 'object' && addressData?.address
    ? addressData.address
    : (typeof addressData === 'string' ? addressData : null);

  return {
    id: rawListing._id,
    name: rawListing.Name || 'Untitled Listing',
    description: rawListing.Description || null,
    address: addressString,
    addressData: addressData, // Keep full JSONB for map coordinates
    borough: rawListing['Location - Borough'] || null,
    hood: rawListing['Location - Hood'] || null,
    boroughName: rawListing.boroughName || null, // Resolved name from lookup table
    hoodName: rawListing.hoodName || null, // Resolved name from lookup table
    photos: rawListing['Features - Photos'] || [],
    featuredPhotoUrl: rawListing.featuredPhotoUrl || null,
    houseRules: rawListing.houseRules || [],
    checkInTime: rawListing['NEW Date Check-in Time'] || null,
    checkOutTime: rawListing['NEW Date Check-out Time'] || null,
    hostUserId: rawListing['Host User'] || null
  };
}

/**
 * Transform raw host data from Bubble.io format
 * @param {Object} rawHost - Raw host object from Supabase
 * @returns {Object} Transformed host object
 */
export function processHostData(rawHost) {
  if (!rawHost) {
    return null;
  }

  return {
    id: rawHost._id,
    firstName: rawHost['Name - First'] || null,
    lastName: rawHost['Name - Last'] || null,
    fullName: rawHost['Name - Full'] || null,
    profilePhoto: rawHost['Profile Photo'] || null,
    bio: rawHost['About Me / Bio'] || null,
    linkedInVerified: rawHost['Verify - Linked In ID'] || false,
    phoneVerified: rawHost['Verify - Phone'] || false,
    userVerified: rawHost['user verified?'] || false,
    // hostUserId same as user._id after migration (Host User column contains user._id directly)
    hostUserId: rawHost._id || null
  };
}

/**
 * Transform raw virtual meeting data from Bubble.io format
 * @param {Object} rawVirtualMeeting - Raw virtual meeting object from Supabase
 * @returns {Object} Transformed virtual meeting object
 */
export function processVirtualMeetingData(rawVirtualMeeting) {
  if (!rawVirtualMeeting) {
    return null;
  }

  return {
    id: rawVirtualMeeting._id,
    bookedDate: rawVirtualMeeting['booked date'] || null,
    confirmedBySplitlease: rawVirtualMeeting.confirmedBySplitLease || false,
    meetingLink: rawVirtualMeeting['meeting link'] || null,
    meetingDeclined: rawVirtualMeeting['meeting declined'] || false,
    requestedBy: rawVirtualMeeting['requested by'] || null,
    suggestedTimeslots: rawVirtualMeeting['suggested dates and times'] || [],
    guestName: rawVirtualMeeting['guest name'] || null,
    hostName: rawVirtualMeeting['host name'] || null,
    proposalId: rawVirtualMeeting.proposal || null,
    uniqueId: rawVirtualMeeting.unique_id || null
  };
}

/**
 * Transform complete proposal data from Bubble.io format
 * Includes nested transformations for listing, host, and virtual meeting
 *
 * @param {Object} rawProposal - Raw proposal object from Supabase
 * @returns {Object} Transformed proposal object
 */
export function processProposalData(rawProposal) {
  if (!rawProposal) {
    throw new Error('processProposalData: Proposal data is required');
  }

  if (!rawProposal._id) {
    throw new Error('processProposalData: Proposal ID (_id) is required');
  }

  // Extract and transform nested data
  const listing = processListingData(rawProposal.listing);
  const host = processHostData(rawProposal.listing?.host);
  const virtualMeeting = processVirtualMeetingData(rawProposal.virtualMeeting);

  return {
    // Core identifiers
    id: rawProposal._id,
    _id: rawProposal._id, // Keep for compatibility

    // Status and state
    status: rawProposal.Status || 'Unknown',
    deleted: rawProposal.Deleted || false,

    // Schedule - Original proposal terms
    daysSelected: rawProposal['Days Selected'] || [],
    nightsSelected: rawProposal['Nights Selected (Nights list)'] || [],
    reservationWeeks: rawProposal['Reservation Span (Weeks)'] || 0,
    nightsPerWeek: rawProposal['nights per week (num)'] || 0,
    checkInDay: rawProposal['check in day'] || null,
    checkOutDay: rawProposal['check out day'] || null,
    moveInStart: rawProposal['Move in range start'] || null,
    moveInEnd: rawProposal['Move in range end'] || null,

    // Pricing - Original proposal terms
    totalPrice: rawProposal['Total Price for Reservation (guest)'] || 0,
    nightlyPrice: rawProposal['proposal nightly price'] || 0,
    cleaningFee: rawProposal['cleaning fee'] || 0,
    damageDeposit: rawProposal['damage deposit'] || 0,

    // Counteroffer fields (hc = host-changed)
    counterOfferHappened: rawProposal['counter offer happened'] || false,
    hcDaysSelected: rawProposal['hc days selected'] || null,
    hcReservationWeeks: rawProposal['hc reservation span (weeks)'] || null,
    hcNightsPerWeek: rawProposal['hc nights per week'] || null,
    hcCheckInDay: rawProposal['hc check in day'] || null,
    hcCheckOutDay: rawProposal['hc check out day'] || null,
    hcTotalPrice: rawProposal['hc total price'] || null,
    hcNightlyPrice: rawProposal['hc nightly price'] || null,
    hcCleaningFee: rawProposal['hc cleaning fee'] || null,
    hcDamageDeposit: rawProposal['hc damage deposit'] || null,
    hcHouseRules: rawProposal['hc house rules'] || null,

    // Metadata
    createdDate: rawProposal['Created Date'] || null,
    modifiedDate: rawProposal['Modified Date'] || null,
    aboutYourself: rawProposal.about_yourself || null,
    specialNeeds: rawProposal.special_needs || null,
    reasonForCancellation: rawProposal['reason for cancellation'] || null,
    proposalStage: rawProposal['Proposal Stage'] || null,
    isFinalized: rawProposal['Is Finalized'] || false,
    isSuggestedByHost: rawProposal['Is Suggested by Host'] || false,

    // Related IDs
    guestId: rawProposal.Guest || null,
    listingId: rawProposal.Listing || null,
    rentalApplicationId: rawProposal['rental application'] || null,
    virtualMeetingId: rawProposal['virtual meeting'] || null,

    // Nested transformed data
    listing,
    host,
    virtualMeeting,

    // House rules (resolved from listing or counteroffer)
    houseRules: rawProposal.houseRules || listing?.houseRules || []
  };
}

/**
 * Get display text for proposal in dropdown
 * Format: "{host name} - {listing name}"
 * Shows the host who owns the listing, allowing guests to identify proposals by host
 *
 * @param {Object} proposal - Transformed proposal object
 * @returns {string} Display text for dropdown option
 */
export function getProposalDisplayText(proposal) {
  if (!proposal) return 'Unknown Proposal';

  const hostName = proposal.host?.firstName || proposal.host?.fullName || 'Host';
  const listingName = proposal.listing?.name || 'Property';

  return `${hostName} - ${listingName}`;
}

/**
 * Format price for display
 * @param {number} price - Price value
 * @param {boolean} includeCents - Whether to include cents
 * @returns {string} Formatted price string
 */
export function formatPrice(price, includeCents = true) {
  if (price === null || price === undefined) return null;

  const formatter = new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD',
    minimumFractionDigits: includeCents ? 2 : 0,
    maximumFractionDigits: includeCents ? 2 : 0
  });

  return formatter.format(price);
}

/**
 * Format date for display
 * @param {string|Date} date - Date value
 * @returns {string} Formatted date string
 */
export function formatDate(date) {
  if (!date) return null;

  const dateObj = typeof date === 'string' ? new Date(date) : date;

  if (isNaN(dateObj.getTime())) return null;

  return dateObj.toLocaleDateString('en-US', {
    year: 'numeric',
    month: 'short',
    day: 'numeric'
  });
}

/**
 * Format datetime for display
 * @param {string|Date} datetime - Datetime value
 * @returns {string} Formatted datetime string
 */
export function formatDateTime(datetime) {
  if (!datetime) return null;

  const dateObj = typeof datetime === 'string' ? new Date(datetime) : datetime;

  if (isNaN(dateObj.getTime())) return null;

  return dateObj.toLocaleString('en-US', {
    weekday: 'long',
    year: 'numeric',
    month: 'long',
    day: 'numeric',
    hour: 'numeric',
    minute: '2-digit',
    timeZone: 'America/New_York',
    timeZoneName: 'short'
  });
}

/**
 * Get the effective terms for a proposal (counteroffer if exists, otherwise original)
 * @param {Object} proposal - Transformed proposal object
 * @returns {Object} Effective terms object
 */
export function getEffectiveTerms(proposal) {
  if (!proposal) {
    throw new Error('getEffectiveTerms: Proposal is required');
  }

  // If counteroffer happened and has values, use those
  if (proposal.counterOfferHappened) {
    return {
      daysSelected: proposal.hcDaysSelected || proposal.daysSelected,
      reservationWeeks: proposal.hcReservationWeeks || proposal.reservationWeeks,
      nightsPerWeek: proposal.hcNightsPerWeek || proposal.nightsPerWeek,
      checkInDay: proposal.hcCheckInDay || proposal.checkInDay,
      checkOutDay: proposal.hcCheckOutDay || proposal.checkOutDay,
      totalPrice: proposal.hcTotalPrice || proposal.totalPrice,
      nightlyPrice: proposal.hcNightlyPrice || proposal.nightlyPrice,
      cleaningFee: proposal.hcCleaningFee || proposal.cleaningFee,
      damageDeposit: proposal.hcDamageDeposit || proposal.damageDeposit,
      isCounteroffer: true
    };
  }

  // Otherwise use original terms
  return {
    daysSelected: proposal.daysSelected,
    reservationWeeks: proposal.reservationWeeks,
    nightsPerWeek: proposal.nightsPerWeek,
    checkInDay: proposal.checkInDay,
    checkOutDay: proposal.checkOutDay,
    totalPrice: proposal.totalPrice,
    nightlyPrice: proposal.nightlyPrice,
    cleaningFee: proposal.cleaningFee,
    damageDeposit: proposal.damageDeposit,
    isCounteroffer: false
  };
}

/**
 * Check if current page is a protected page requiring authentication.
 *
 * @intent Determine if the current URL requires user authentication.
 * @rule Protected pages redirect to home if user is not logged in.
 * @rule Handles both clean URLs (/account-profile) and .html URLs (/account-profile.html).
 *
 * @param {object} params - Named parameters.
 * @param {string} params.pathname - Current URL pathname.
 * @returns {boolean} True if current page requires authentication.
 *
 * @throws {Error} If pathname is not a string.
 *
 * @example
 * const protected = isProtectedPage({ pathname: '/account-profile' })
 * // => true
 *
 * const public = isProtectedPage({ pathname: '/search' })
 * // => false
 */
export function isProtectedPage({ pathname }) {
  // No Fallback: Validate input
  if (typeof pathname !== 'string') {
    throw new Error(
      `isProtectedPage: pathname must be a string, got ${typeof pathname}`
    )
  }

  const protectedPaths = [
    '/account-profile',
    '/host-dashboard'
  ]

  // Normalize path by removing .html extension for consistent matching
  const normalizedPath = pathname.replace(/\.html$/, '')

  // Check if current path matches any protected path exactly or starts with it
  return protectedPaths.some(path =>
    normalizedPath === path || normalizedPath.startsWith(path + '/')
  )
}

/**
 * Check if a specific date is blocked.
 *
 * @intent Determine if a date is unavailable due to blocking.
 * @rule Compares dates in YYYY-MM-DD format (ignoring time).
 *
 * @param {object} params - Named parameters.
 * @param {Date} params.date - Date to check.
 * @param {Array} params.blockedDates - Array of blocked date strings or Date objects.
 * @returns {boolean} True if date is blocked, false otherwise.
 *
 * @throws {Error} If date is not a valid Date object.
 * @throws {Error} If blockedDates is not an array.
 *
 * @example
 * const blocked = isDateBlocked({
 *   date: new Date('2025-12-25'),
 *   blockedDates: ['2025-12-25', '2025-12-26']
 * })
 * // => true
 */
export function isDateBlocked({ date, blockedDates }) {
  // No Fallback: Validate inputs
  if (!(date instanceof Date) || isNaN(date.getTime())) {
    throw new Error(
      'isDateBlocked: date must be a valid Date object'
    )
  }

  if (!Array.isArray(blockedDates)) {
    throw new Error(
      `isDateBlocked: blockedDates must be an array, got ${typeof blockedDates}`
    )
  }

  // Empty blockedDates array means nothing is blocked
  if (blockedDates.length === 0) {
    return false
  }

  // Format the check date as YYYY-MM-DD
  const dateStr = date.toISOString().split('T')[0]

  // Check if any blocked date matches
  return blockedDates.some(blocked => {
    if (typeof blocked === 'string') {
      // Extract date portion (YYYY-MM-DD) from string
      const blockedDateStr = blocked.split('T')[0]
      return blockedDateStr === dateStr
    }
    if (blocked instanceof Date) {
      const blockedDateStr = blocked.toISOString().split('T')[0]
      return blockedDateStr === dateStr
    }
    // Skip invalid entries silently (they don't block anything)
    return false
  })
}

/**
 * Check if a date is within the available range.
 *
 * @intent Validate that a date falls within the listing's availability window.
 * @rule Date must be >= firstAvailable (if specified).
 * @rule Date must be <= lastAvailable (if specified).
 * @rule Null/undefined bounds are treated as unbounded (no restriction).
 *
 * @param {object} params - Named parameters.
 * @param {Date} params.date - Date to check.
 * @param {string|Date|null} params.firstAvailable - First available date (inclusive).
 * @param {string|Date|null} params.lastAvailable - Last available date (inclusive).
 * @returns {boolean} True if date is within range, false otherwise.
 *
 * @throws {Error} If date is not a valid Date object.
 *
 * @example
 * const inRange = isDateInRange({
 *   date: new Date('2025-12-15'),
 *   firstAvailable: '2025-12-01',
 *   lastAvailable: '2026-01-31'
 * })
 * // => true
 */
export function isDateInRange({ date, firstAvailable, lastAvailable }) {
  // No Fallback: Validate date
  if (!(date instanceof Date) || isNaN(date.getTime())) {
    throw new Error(
      'isDateInRange: date must be a valid Date object'
    )
  }

  // Normalize check date (ignore time component)
  const checkDate = new Date(date)
  checkDate.setHours(0, 0, 0, 0)

  // Check first available bound
  if (firstAvailable !== null && firstAvailable !== undefined) {
    const firstDate = new Date(firstAvailable)
    if (isNaN(firstDate.getTime())) {
      throw new Error(
        `isDateInRange: firstAvailable is not a valid date: ${firstAvailable}`
      )
    }
    firstDate.setHours(0, 0, 0, 0)
    if (checkDate < firstDate) {
      return false
    }
  }

  // Check last available bound
  if (lastAvailable !== null && lastAvailable !== undefined) {
    const lastDate = new Date(lastAvailable)
    if (isNaN(lastDate.getTime())) {
      throw new Error(
        `isDateInRange: lastAvailable is not a valid date: ${lastAvailable}`
      )
    }
    lastDate.setHours(0, 0, 0, 0)
    if (checkDate > lastDate) {
      return false
    }
  }

  return true
}

/**
 * Check if selected days form a contiguous (consecutive) block.
 *
 * @intent Enforce the business rule that split lease stays must be consecutive nights.
 * @rule Days must be consecutive (Mon-Fri ✓, Mon+Wed ✗).
 * @rule Handles week wrap-around cases (Fri-Sun ✓, Sat-Tue ✓).
 * @rule For wrap-around, uses inverse logic: if unselected days are contiguous, selected days wrap around properly.
 * @rule 6 or more days is always contiguous (only 1 gap maximum).
 *
 * This function consolidates the contiguous validation logic that previously existed in:
 * - availabilityValidation.js (isContiguousSelection)
 * - scheduleSelector/validators.js (isContiguous)
 *
 * @param {object} params - Named parameters.
 * @param {number[]} params.selectedDayIndices - Array of day indices (0=Sunday, 1=Monday, ..., 6=Saturday).
 * @returns {boolean} True if days are contiguous, false otherwise.
 *
 * @throws {Error} If selectedDayIndices is not an array.
 * @throws {Error} If any day index is invalid (not 0-6).
 *
 * @example
 * isScheduleContiguous({ selectedDayIndices: [1, 2, 3, 4, 5] }) // => true (Mon-Fri)
 * isScheduleContiguous({ selectedDayIndices: [1, 3, 5] }) // => false (Mon, Wed, Fri - not contiguous)
 * isScheduleContiguous({ selectedDayIndices: [5, 6, 0] }) // => true (Fri-Sun, wraps around week)
 * isScheduleContiguous({ selectedDayIndices: [6, 0, 1, 2] }) // => true (Sat-Tue, wraps around week)
 */
export function isScheduleContiguous({ selectedDayIndices }) {
  // No Fallback: Validate input
  if (!Array.isArray(selectedDayIndices)) {
    throw new Error(
      `isScheduleContiguous: selectedDayIndices must be an array, got ${typeof selectedDayIndices}`
    )
  }

  // Empty selection is not contiguous
  if (selectedDayIndices.length === 0) {
    return false
  }

  // Single day is contiguous
  if (selectedDayIndices.length === 1) {
    return true
  }

  // Validate all day indices
  for (const day of selectedDayIndices) {
    if (typeof day !== 'number' || isNaN(day) || day < 0 || day > 6) {
      throw new Error(
        `isScheduleContiguous: Invalid day index ${day}, must be 0-6`
      )
    }
  }

  // Sort the selected days (immutable)
  const sorted = selectedDayIndices.toSorted((a, b) => a - b)

  // If 6 or more days selected, it's contiguous (only 1 gap or no gaps)
  if (sorted.length >= 6) {
    return true
  }

  // Check for standard contiguous sequence (no wrap around)
  let isStandardContiguous = true
  for (let i = 1; i < sorted.length; i++) {
    if (sorted[i] !== sorted[i - 1] + 1) {
      isStandardContiguous = false
      break
    }
  }

  if (isStandardContiguous) {
    return true
  }

  // Check if selection includes both Sunday (0) and Saturday (6) - wrap-around case
  const hasZero = sorted.includes(0)
  const hasSix = sorted.includes(6)

  if (hasZero && hasSix) {
    // Week wrap-around case: use inverse logic (check not-selected days)
    // If the NOT selected days are contiguous, then selected days wrap around and are contiguous
    const allDays = [0, 1, 2, 3, 4, 5, 6]
    const notSelectedDays = allDays.filter(d => !sorted.includes(d))

    if (notSelectedDays.length === 0) {
      return true // All days selected
    }

    // Check if not-selected days form a contiguous block
    const minNotSelected = Math.min(...notSelectedDays)
    const maxNotSelected = Math.max(...notSelectedDays)

    // Generate expected contiguous range for not-selected days
    const expectedNotSelected = Array.from(
      { length: maxNotSelected - minNotSelected + 1 },
      (_, i) => minNotSelected + i
    )

    // If not-selected days are contiguous, then selected days wrap around properly
    const notSelectedContiguous = notSelectedDays.length === expectedNotSelected.length &&
      notSelectedDays.every((day, index) => day === expectedNotSelected[index])

    return notSelectedContiguous
  }

  return false
}

/**
 * Check if a listing has photos.
 *
 * @intent Validate that a listing has at least one photo for search display.
 * @rule Listings without photos must not appear in search results.
 *
 * @param {object} params - Named parameters.
 * @param {object} params.listing - The listing object to check.
 * @returns {boolean} True if listing has at least one photo.
 *
 * @throws {Error} If listing is null or undefined.
 *
 * @example
 * const valid = hasListingPhotos({ listing: { 'Features - Photos': ['photo1'] } })
 * // => true
 *
 * const invalid = hasListingPhotos({ listing: { 'Features - Photos': [] } })
 * // => false
 */
export function hasListingPhotos({ listing }) {
  // No Fallback: Validate input
  if (listing === null || listing === undefined) {
    throw new Error('hasListingPhotos: listing cannot be null or undefined')
  }

  const photos = listing['Features - Photos']

  // Check for null, undefined, or empty array
  if (!photos) {
    return false
  }

  // Handle string representation (JSON array from database)
  if (typeof photos === 'string') {
    try {
      const parsed = JSON.parse(photos)
      return Array.isArray(parsed) && parsed.length > 0
    } catch {
      return false
    }
  }

  // Handle array directly
  if (Array.isArray(photos)) {
    return photos.length > 0
  }

  return false
}

/**
 * Determine if a user type indicates Host privileges.
 *
 * @intent Enforce business rules for Host role identification.
 * @rule Host types from Supabase:
 *   - "A Host (I have a space available to rent)"
 *   - "Trial Host"
 *   - "Split Lease" (internal users with both Host and Guest privileges)
 *
 * @param {object} params - Named parameters.
 * @param {string|null} params.userType - The user type value from Supabase or localStorage.
 * @returns {boolean} True if user has Host privileges.
 *
 * @example
 * isHost({ userType: 'A Host (I have a space available to rent)' })
 * // => true
 *
 * isHost({ userType: 'A Guest (I would like to rent a space)' })
 * // => false
 *
 * isHost({ userType: 'Split Lease' })
 * // => true (internal users have both roles)
 */
export function isHost({ userType }) {
  // No user type means not authenticated or type not set
  if (!userType || typeof userType !== 'string') {
    return false
  }

  const type = userType.trim()

  // Split Lease internal users have both Host and Guest privileges
  if (type === 'Split Lease') {
    return true
  }

  // Check if type includes "Host" (covers both regular and trial hosts)
  return type.includes('Host')
}

/**
 * Determine if a user type indicates Guest privileges.
 *
 * @intent Enforce business rules for Guest role identification.
 * @rule Guest types from Supabase:
 *   - "A Guest (I would like to rent a space)"
 *   - "Split Lease" (internal users with both Host and Guest privileges)
 *
 * @param {object} params - Named parameters.
 * @param {string|null} params.userType - The user type value from Supabase or localStorage.
 * @returns {boolean} True if user has Guest privileges.
 *
 * @example
 * isGuest({ userType: 'A Guest (I would like to rent a space)' })
 * // => true
 *
 * isGuest({ userType: 'A Host (I have a space available to rent)' })
 * // => false
 *
 * isGuest({ userType: 'Split Lease' })
 * // => true (internal users have both roles)
 */
export function isGuest({ userType }) {
  // No user type means not authenticated or type not set
  if (!userType || typeof userType !== 'string') {
    return false
  }

  const type = userType.trim()

  // Split Lease internal users have both Host and Guest privileges
  if (type === 'Split Lease') {
    return true
  }

  // Check if type includes "Guest"
  return type.includes('Guest')
}

/**
 * Determines if a user has a valid profile photo URL.
 *
 * @function hasProfilePhoto
 * @intent Check if user has uploaded a profile photo to display in UI
 * @rule A profile photo is valid if the URL is a non-empty string
 * @rule Empty strings, null, and undefined are considered "no photo"
 *
 * @param {object} params - Named parameters
 * @param {string|null|undefined} params.photoUrl - The profile photo URL from user data
 * @returns {boolean} True if user has a valid profile photo URL
 *
 * @example
 * hasProfilePhoto({ photoUrl: 'https://example.com/photo.jpg' }) // true
 * hasProfilePhoto({ photoUrl: '' }) // false
 * hasProfilePhoto({ photoUrl: null }) // false
 * hasProfilePhoto({ photoUrl: undefined }) // false
 */
export function hasProfilePhoto({ photoUrl }) {
  // NO FALLBACK: Explicit check without defaulting
  if (photoUrl === null || photoUrl === undefined) {
    return false;
  }

  // Check if photoUrl is a non-empty string
  if (typeof photoUrl !== 'string') {
    return false;
  }

  // Empty string is considered "no photo"
  return photoUrl.trim().length > 0;
}

