import { isScheduleContiguous } from '../../rules/scheduling/isScheduleContiguous.js'

/**
 * Validate a schedule selection against listing requirements.
 * Orchestrates multiple validation rules to determine if a schedule is acceptable.
 *
 * @intent Enforce all business rules for schedule selection in one workflow.
 * @rule Days must be contiguous (consecutive).
 * @rule Must have at least one day selected.
 * @rule May optionally check against minimum/maximum nights from listing.
 * @rule Returns error codes (not UI messages) - presentation layer maps to messages.
 *
 * @param {object} params - Named parameters.
 * @param {number[]} params.selectedDayIndices - Array of selected day indices (0-6).
 * @param {object} [params.listing] - Optional listing object with constraints.
 * @param {number} [params.listing.minimumNights] - Minimum nights required.
 * @param {number} [params.listing.maximumNights] - Maximum nights allowed.
 * @param {string[]} [params.listing.daysNotAvailable] - Days not available (day names).
 * @returns {object} Validation result with valid flag, errorCode, and metadata.
 *
 * @throws {Error} If selectedDayIndices is not an array.
 *
 * @example
 * const result = validateScheduleWorkflow({
 *   selectedDayIndices: [1, 2, 3, 4, 5],
 *   listing: { minimumNights: 2, maximumNights: 7 }
 * })
 * // => { valid: true, errorCode: null, nightsCount: 5, isContiguous: true }
 *
 * const invalid = validateScheduleWorkflow({
 *   selectedDayIndices: [1, 3, 5],
 *   listing: {}
 * })
 * // => { valid: false, errorCode: 'NOT_CONTIGUOUS', nightsCount: 3, isContiguous: false }
 */
export function validateScheduleWorkflow({ selectedDayIndices, listing = {} }) {
  // No Fallback: Validate inputs
  if (!Array.isArray(selectedDayIndices)) {
    throw new Error(
      `validateScheduleWorkflow: selectedDayIndices must be an array, got ${typeof selectedDayIndices}`
    )
  }

  // Check if any days are selected
  if (selectedDayIndices.length === 0) {
    return {
      valid: false,
      errorCode: 'NO_DAYS_SELECTED',
      nightsCount: 0,
      isContiguous: false
    }
  }

  // Calculate nights (in split lease, nights = days selected)
  const nightsCount = selectedDayIndices.length

  // Check contiguous requirement (CRITICAL business rule)
  const isContiguous = isScheduleContiguous({ selectedDayIndices })

  if (!isContiguous) {
    return {
      valid: false,
      errorCode: 'NOT_CONTIGUOUS',
      nightsCount,
      isContiguous: false
    }
  }

  // Check against minimum nights (if specified)
  if (listing.minimumNights !== undefined && listing.minimumNights !== null) {
    const minNights = Number(listing.minimumNights)
    if (!isNaN(minNights) && nightsCount < minNights) {
      return {
        valid: false,
        errorCode: 'BELOW_MINIMUM_NIGHTS',
        nightsCount,
        isContiguous: true,
        minimumNights: minNights
      }
    }
  }

  // Check against maximum nights (if specified)
  if (listing.maximumNights !== undefined && listing.maximumNights !== null) {
    const maxNights = Number(listing.maximumNights)
    if (!isNaN(maxNights) && nightsCount > maxNights) {
      return {
        valid: false,
        errorCode: 'ABOVE_MAXIMUM_NIGHTS',
        nightsCount,
        isContiguous: true,
        maximumNights: maxNights
      }
    }
  }

  // Check against Days Not Available (if specified)
  if (listing.daysNotAvailable && Array.isArray(listing.daysNotAvailable)) {
    const DAY_NAMES = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday']

    const unavailableSelected = selectedDayIndices.filter(dayIndex => {
      const dayName = DAY_NAMES[dayIndex]
      return listing.daysNotAvailable.includes(dayName)
    })

    if (unavailableSelected.length > 0) {
      return {
        valid: false,
        errorCode: 'DAYS_NOT_AVAILABLE',
        nightsCount,
        isContiguous: true,
        unavailableDays: unavailableSelected
      }
    }
  }

  // All validations passed
  return {
    valid: true,
    errorCode: null,
    nightsCount,
    isContiguous: true
  }
}

/**
 * Check authentication status workflow.
 * Orchestrates checking cookies, secure storage, and session validation.
 *
 * @intent Determine if user is currently authenticated across multiple sources.
 * @rule Checks Split Lease cookies first (cross-domain compatibility).
 * @rule Falls back to secure storage tokens.
 * @rule Validates session hasn't expired.
 *
 * This workflow coordinates infrastructure layer (cookies, storage) with
 * business rules (session validity).
 *
 * @param {object} params - Named parameters.
 * @param {object} params.splitLeaseCookies - Split Lease cookie auth status.
 * @param {boolean} params.splitLeaseCookies.isLoggedIn - Logged in via cookies.
 * @param {string} params.splitLeaseCookies.username - Username from cookies.
 * @param {boolean} params.authState - Auth state from secure storage.
 * @param {boolean} params.hasValidTokens - Whether valid tokens exist.
 * @returns {object} Authentication status with source information.
 *
 * @example
 * const status = checkAuthStatusWorkflow({
 *   splitLeaseCookies: { isLoggedIn: true, username: 'john@example.com' },
 *   authState: true,
 *   hasValidTokens: true
 * })
 * // => { isAuthenticated: true, source: 'cookies', username: 'john@example.com' }
 */
export function checkAuthStatusWorkflow({
  splitLeaseCookies,
  authState,
  hasValidTokens
}) {
  // No Fallback: Validate inputs
  if (!splitLeaseCookies || typeof splitLeaseCookies !== 'object') {
    throw new Error(
      'checkAuthStatusWorkflow: splitLeaseCookies is required'
    )
  }

  if (typeof authState !== 'boolean') {
    throw new Error(
      'checkAuthStatusWorkflow: authState must be a boolean'
    )
  }

  if (typeof hasValidTokens !== 'boolean') {
    throw new Error(
      'checkAuthStatusWorkflow: hasValidTokens must be a boolean'
    )
  }

  // Priority 1: Check Split Lease cookies (cross-domain compatibility)
  if (splitLeaseCookies.isLoggedIn) {
    return {
      isAuthenticated: true,
      source: 'cookies',
      username: splitLeaseCookies.username
    }
  }

  // Priority 2: Check secure storage tokens
  if (authState && hasValidTokens) {
    return {
      isAuthenticated: true,
      source: 'secure_storage',
      username: null // Username would need to be fetched separately
    }
  }

  // Not authenticated
  return {
    isAuthenticated: false,
    source: null,
    username: null
  }
}

/**
 * Workflow: Accept a host's counteroffer proposal.
 *
 * @intent Orchestrate acceptance of host counteroffer following business rules.
 * @rule Only applicable when proposal status is "Host Countered".
 * @rule Updates status to "Accepted" and sets acceptance timestamp.
 * @rule Validates proposal can be accepted before attempting update.
 *
 * @param {object} params - Named parameters.
 * @param {object} params.supabase - Supabase client instance.
 * @param {object} params.proposal - Processed proposal object.
 * @param {function} params.canAcceptProposal - Rule function to check if acceptable.
 * @returns {Promise<object>} Result object with success status and message.
 *
 * @throws {Error} If required parameters are missing.
 *
 * @example
 * const result = await acceptProposalWorkflow({
 *   supabase,
 *   proposal: processedProposal,
 *   canAcceptProposal
 * })
 * // => { success: true, message: 'Proposal accepted', updated: true }
 */
import { PROPOSAL_STATUSES } from '../../constants/proposalStatuses.js'

export async function acceptProposalWorkflow({
  supabase,
  proposal,
  canAcceptProposal
}) {
  // Validation
  if (!supabase) {
    throw new Error('acceptProposalWorkflow: supabase client is required')
  }

  if (!proposal || !proposal.id) {
    throw new Error('acceptProposalWorkflow: proposal with id is required')
  }

  if (!canAcceptProposal) {
    throw new Error('acceptProposalWorkflow: canAcceptProposal rule function is required')
  }

  // Step 1: Check if acceptance is allowed
  const canAccept = canAcceptProposal({
    proposalStatus: proposal.status,
    deleted: proposal.deleted
  })

  if (!canAccept) {
    return {
      success: false,
      message: 'This proposal cannot be accepted (not in "Host Countered" status)',
      updated: false
    }
  }

  // Step 2: Update proposal status to "Accepted"
  try {
    const { error } = await supabase
      .from('proposal')
      .update({
        'Status': PROPOSAL_STATUSES.PROPOSAL_OR_COUNTEROFFER_ACCEPTED.key,
        'Modified Date': new Date().toISOString()
      })
      .eq('_id', proposal.id)

    if (error) {
      throw error
    }

    return {
      success: true,
      message: 'Proposal accepted successfully! The host will be notified.',
      updated: true
    }
  } catch (err) {
    return {
      success: false,
      message: `Failed to accept proposal: ${err.message}`,
      updated: false,
      error: err
    }
  }
}

