    redirectTo: '/account-profile' // Note: actual redirect handled in RentalApplicationPage component
  },

  // ===== AUTH PAGES =====
  {
    path: '/reset-password',
    file: 'reset-password.html',
    aliases: ['/reset-password.html'],
