   643→export async function signupUser(email, password, retype, additionalData = null) {
   644→  console.log('📝 Attempting signup via Supabase Auth for:', email);
   645→
   646→  // Client-side validation
   647→  if (!email || !password || !retype) {
   648→    return {
   649→      success: false,
   650→      error: 'All fields are required.'
   651→    };
   652→  }
   653→
   654→  if (password.length < 4) {
   655→    return {
   656→      success: false,
   657→      error: 'Password must be at least 4 characters long.'
   658→    };
   659→  }
   660→
   661→  if (password !== retype) {
   662→    return {
   663→      success: false,
   664→      error: 'The two passwords do not match!'
   665→    };
   666→  }
   667→
   668→  // Build payload with optional additional data
   669→  const payload = {
   670→    email,
   671→    password,
   672→    retype
   673→  };
   674→
   675→  // Add additional signup data if provided
   676→  if (additionalData) {
   677→    payload.additionalData = additionalData;
   678→    console.log('📝 Additional signup data:', additionalData);
   679→  }
   680→
   681→  try {
   682→    // CRITICAL: Use direct fetch to bypass Supabase client session handling
   683→    // The Supabase client's functions.invoke can hang indefinitely when there's a stale
   684→    // session because it tries to refresh the token before making requests. Since auth-user
   685→    // has verify_jwt=false, we don't need any auth token. Using direct fetch bypasses this.
   686→    console.log('🔄 Using direct fetch to bypass Supabase client session handling...');
   687→
   688→    // Clear localStorage directly to avoid signOut() hanging
   689→    try {
   690→      const storageKey = `sb-${import.meta.env.VITE_SUPABASE_URL?.split('//')[1]?.split('.')[0]}-auth-token`;
   691→      if (storageKey && storageKey !== 'sb-undefined-auth-token') {
   692→        localStorage.removeItem(storageKey);
   693→        console.log('✅ Cleared auth token from localStorage');
   694→      }
   695→    } catch (clearErr) {
   696→      console.warn('⚠️ Could not clear localStorage:', clearErr);
   697→    }
   698→
   699→    const supabaseUrl = import.meta.env.VITE_SUPABASE_URL;
   700→    const supabaseAnonKey = import.meta.env.VITE_SUPABASE_ANON_KEY;
   701→
   702→    console.log('📡 Calling auth-user edge function via direct fetch...');
   703→    const response = await fetch(`${supabaseUrl}/functions/v1/auth-user`, {
   704→      method: 'POST',
   705→      headers: {
   706→        'Content-Type': 'application/json',
   707→        'apikey': supabaseAnonKey,
   708→        'Authorization': `Bearer ${supabaseAnonKey}` // Use anon key, not user token
   709→      },
   710→      body: JSON.stringify({
   711→        action: 'signup',
   712→        payload
   713→      })
   714→    });
   715→
   716→    const data = await response.json();
   717→    console.log('📡 Response status:', response.status);
   718→    console.log('📡 Response data:', data);
   719→
   720→    // Handle HTTP errors
   721→    if (!response.ok) {
   722→      console.error('❌ Edge Function HTTP error:', response.status);
   723→      const errorMessage = data?.error || `HTTP ${response.status}: Failed to create account`;
   724→      return {
   725→        success: false,
   726→        error: errorMessage
   727→      };
   728→    }
   729→
   730→    // Handle error in response body
   731→    if (!data.success) {
   732→      console.error('❌ Signup failed:', data.error);
   733→      return {
   734→        success: false,
   735→        error: data.error || 'Signup failed. Please try again.'
   736→      };
   737→    }
   738→
   739→    console.log('✅ Edge Function returned successfully');
   740→
   741→    // Extract Supabase session data
   742→    const {
   743→      access_token,
   744→      refresh_token,
   745→      expires_in,
   746→      user_id,
   747→      host_account_id,
   748→      guest_account_id,
   749→      supabase_user_id,
   750→      user_type
   751→    } = data.data;
   752→
   753→    console.log('📋 Extracted session data:');
   754→    console.log('   access_token:', access_token ? `${access_token.substring(0, 20)}...` : 'MISSING');
   755→    console.log('   refresh_token:', refresh_token ? `${refresh_token.substring(0, 20)}...` : 'MISSING');
   756→    console.log('   user_id:', user_id);
   757→    console.log('   user_type:', user_type);
   758→
   759→    // Set Supabase session using the client
   760→    // This stores the session in localStorage and enables authenticated requests
   761→    console.log('🔐 About to call setSession...');
   762→    let sessionError = null;
   763→    try {
   764→      const result = await supabase.auth.setSession({
   765→        access_token,
   766→        refresh_token
   767→      });
   768→      sessionError = result.error;
   769→      console.log('🔐 setSession completed, error:', sessionError);
   770→    } catch (setSessionErr) {
   771→      console.error('🔐 setSession threw an exception:', setSessionErr);
   772→      sessionError = setSessionErr;
   773→    }
   774→
   775→    if (sessionError) {
   776→      console.error('❌ Failed to set Supabase session:', sessionError.message);
   777→      // Continue anyway - tokens are still valid, just not in client state
   778→    } else {
   779→      console.log('✅ Supabase session set successfully');
   780→    }
   781→
   782→    // CRITICAL: Verify the session is actually persisted before proceeding
   783→    // This fixes a race condition where the page can reload before localStorage is written
   784→    let verifyAttempts = 0;
   785→    const maxVerifyAttempts = 5;
   786→    while (verifyAttempts < maxVerifyAttempts) {
   787→      const { data: { session: verifiedSession } } = await supabase.auth.getSession();
   788→      if (verifiedSession && verifiedSession.access_token === access_token) {
   789→        console.log('✅ Session verified and persisted');
   790→        break;
   791→      }
   792→      verifyAttempts++;
   793→      console.log(`⏳ Waiting for session to persist (attempt ${verifyAttempts}/${maxVerifyAttempts})...`);
   794→      await new Promise(resolve => setTimeout(resolve, 100)); // Wait 100ms
   795→    }
   796→
   797→    if (verifyAttempts >= maxVerifyAttempts) {
   798→      console.warn('⚠️ Session may not be fully persisted, but continuing...');
   799→    }
   800→
   801→    // Store access_token as auth token for backward compatibility with existing code
   802→    setAuthToken(access_token);
   803→    setSessionId(user_id);
   804→
   805→    // Set auth state with user ID (public, non-sensitive)
   806→    setAuthState(true, user_id);
   807→
   808→    // Store user type
   809→    if (user_type) {
   810→      setUserType(user_type);
   811→    }
   812→
   813→    // Update login state
   814→    isUserLoggedInState = true;
   815→
   816→    console.log('✅ Signup successful (Supabase Auth)');
   817→    console.log('   User ID (_id):', user_id);
   818→    console.log('   Supabase Auth ID:', supabase_user_id);
   819→    console.log('   Host Account ID:', host_account_id);
   820→    console.log('   Guest Account ID:', guest_account_id);
   821→    console.log('   User Type:', user_type);
   822→    console.log('   Session expires in:', expires_in, 'seconds');
   823→
   824→    // Store Supabase user ID for reference
   825→    if (supabase_user_id) {
   826→      localStorage.setItem('splitlease_supabase_user_id', supabase_user_id);
   827→    }
   828→
   829→    return {
   830→      success: true,
   831→      user_id: user_id,
   832→      host_account_id: host_account_id,
   833→      guest_account_id: guest_account_id,
   834→      supabase_user_id: supabase_user_id,
   835→      user_type: user_type,
