  1039→/**
  1040→ * Check if current page is a protected page requiring authentication
  1041→ * Protected pages redirect to home if user is not logged in
  1042→ *
  1043→ * Handles both clean URLs (/guest-proposals) and .html URLs (/guest-proposals.html)
  1044→ * by normalizing the path before comparison
  1045→ *
  1046→ * @returns {boolean} True if current page requires authentication
  1047→ */
  1048→export function isProtectedPage() {
  1049→  const protectedPaths = [
  1050→    '/guest-proposals',
  1051→    '/host-proposals',
  1052→    '/account-profile',
  1053→    '/host-dashboard',
  1054→    '/self-listing',
  1055→    '/listing-dashboard',
  1056→    '/host-overview',
  1057→    '/favorite-listings',
  1058→    '/rental-application',
  1059→    '/preview-split-lease'
  1060→  ];
  1061→
  1062→  // Normalize path by removing .html extension for consistent matching
  1063→  const currentPath = window.location.pathname.replace(/\.html$/, '');
  1064→
  1065→  // Check if current path matches any protected path exactly or starts with it
  1066→  return protectedPaths.some(path =>
  1067→    currentPath === path || currentPath.startsWith(path + '/')
  1068→  );
  1069→}
