  1093→
  1094→  console.log('🔓 Attempting logout via Edge Function...');
  1095→
  1096→  // Sign out from Supabase Auth client explicitly
  1097→  // This ensures the client-side session is cleared from localStorage
  1098→  try {
  1099→    await supabase.auth.signOut();
  1100→    console.log('✅ Signed out from Supabase Auth client');
  1101→  } catch (err) {
  1102→    console.warn('⚠️ Error signing out from Supabase Auth client:', err);
