  1104→  }
  1105→
  1106→  try {
  1107→    const { data, error } = await supabase.functions.invoke('auth-user', {
  1108→      body: {
  1109→        action: 'logout',
  1110→        payload: {
  1111→          token
  1112→        }
  1113→      }
  1114→    });
  1115→
  1116→    // Clear auth data regardless of API response
  1117→    // This ensures clean logout even if API call fails
  1118→    clearAuthData();
  1119→
  1120→    if (error || !data.success) {
  1121→      console.log('⚠️ Logout API returned error, but local data cleared');
  1122→      return {
  1123→        success: true,
  1124→        message: 'Logged out locally'
  1125→      };
  1126→    }
  1127→
  1128→    console.log('✅ Logout successful');
  1129→    return {
  1130→      success: true,
  1131→      message: data.data.message || 'Logout successful'
  1132→    };
  1133→
  1134→  } catch (error) {
  1135→    console.error('❌ Logout error:', error);
  1136→    // Auth data already cleared above
  1137→    clearAuthData();
  1138→    return {
  1139→      success: true,
  1140→      message: 'Logged out locally (network error)'
  1141→    };
  1142→  }
  1143→}
