  1146→// URL Auth Error Detection (Magic Link / OTP Errors)
  1147→// ============================================================================
  1148→
  1149→/**
  1150→ * Check URL hash for Supabase auth errors
  1151→ * Returns error info if present, null otherwise
  1152→ *
  1153→ * When Supabase redirects after a failed magic link (expired OTP, already used, etc.),
  1154→ * it includes error params in the URL hash:
  1155→ * #error=access_denied&error_code=otp_expired&error_description=Email+link+is+invalid+or+has+expired
  1156→ *
  1157→ * @returns {Object|null} Error object with error, errorCode, errorDescription, or null if no error
  1158→ */
  1159→export function checkUrlForAuthError() {
  1160→  const hash = window.location.hash;
  1161→  if (!hash) return null;
  1162→
  1163→  const params = new URLSearchParams(hash.substring(1));
  1164→  const error = params.get('error');
  1165→  const errorCode = params.get('error_code');
  1166→  const errorDescription = params.get('error_description');
  1167→
  1168→  if (error || errorCode) {
  1169→    return {
  1170→      error,
  1171→      errorCode,
  1172→      errorDescription: errorDescription ? decodeURIComponent(errorDescription.replace(/\+/g, ' ')) : null
  1173→    };
  1174→  }
  1175→
  1176→  return null;
  1177→}
  1178→
  1179→/**
  1180→ * Clear auth error params from URL hash
  1181→ * Call this after handling the error to prevent re-processing
  1182→ * Uses replaceState to not add to browser history
  1183→ */
  1184→export function clearAuthErrorFromUrl() {
  1185→  if (window.location.hash) {
  1186→    window.history.replaceState(null, '', window.location.pathname + window.location.search);
  1187→  }
  1188→}
  1189→
  1190→// ============================================================================
  1191→// Password Reset Functions
  1192→// ============================================================================
  1193→
  1194→/**
  1195→ * Request password reset email via Edge Function (auth-user)
  1196→ * Always returns success to prevent email enumeration
  1197→ *
  1198→ * Uses Edge Functions - API keys stored server-side
  1199→ *
  1200→ * @param {string} email - User's email address
  1201→ * @returns {Promise<Object>} Response object with success status and message
  1202→ */
  1203→export async function requestPasswordReset(email) {
  1204→  console.log('🔐 Requesting password reset for:', email);
  1205→
  1206→  if (!email) {
  1207→    return {
  1208→      success: false,
  1209→      error: 'Email is required.'
  1210→    };
  1211→  }
  1212→
  1213→  try {
  1214→    const { data, error } = await supabase.functions.invoke('auth-user', {
  1215→      body: {
  1216→        action: 'request_password_reset',
  1217→        payload: {
  1218→          email,
  1219→          redirectTo: `${window.location.origin}/reset-password`
  1220→        }
  1221→      }
  1222→    });
  1223→
  1224→    if (error) {
  1225→      console.error('❌ Password reset request failed:', error);
  1226→      // Don't expose error details - always show generic message
  1227→      return {
  1228→        success: true, // Return success even on error to prevent email enumeration
  1229→        message: 'If an account with that email exists, a password reset link has been sent.'
  1230→      };
  1231→    }
  1232→
  1233→    console.log('✅ Password reset request processed');
  1234→    return {
  1235→      success: true,
  1236→      message: data?.data?.message || 'If an account with that email exists, a password reset link has been sent.'
  1237→    };
  1238→
  1239→  } catch (error) {
  1240→    console.error('❌ Password reset error:', error);
  1241→    return {
  1242→      success: true, // Return success even on error to prevent email enumeration
  1243→      message: 'If an account with that email exists, a password reset link has been sent.'
  1244→    };
  1245→  }
  1246→}
  1247→
  1248→/**
  1249→ * Update password after clicking reset link
  1250→ * Must be called when user has active session from PASSWORD_RECOVERY event
  1251→ *
  1252→ * After successful password update, the user remains logged in with their
  1253→ * existing session synced to secure storage.
  1254→ *
  1255→ * Uses Edge Functions - API keys stored server-side
  1256→ *
  1257→ * @param {string} newPassword - New password to set
  1258→ * @returns {Promise<Object>} Response object with success status
  1259→ */
  1260→export async function updatePassword(newPassword) {
  1261→  console.log('🔐 Updating password...');
  1262→
  1263→  if (!newPassword) {
  1264→    return {
  1265→      success: false,
  1266→      error: 'New password is required.'
  1267→    };
  1268→  }
  1269→
  1270→  if (newPassword.length < 4) {
  1271→    return {
  1272→      success: false,
  1273→      error: 'Password must be at least 4 characters long.'
  1274→    };
  1275→  }
  1276→
  1277→  // Get current session (from PASSWORD_RECOVERY event)
  1278→  const { data: { session }, error: sessionError } = await supabase.auth.getSession();
  1279→
  1280→  if (sessionError || !session) {
  1281→    console.error('❌ No active session for password update');
  1282→    return {
  1283→      success: false,
  1284→      error: 'Invalid or expired reset link. Please request a new password reset.'
  1285→    };
  1286→  }
  1287→
  1288→  try {
  1289→    const { data, error } = await supabase.functions.invoke('auth-user', {
  1290→      body: {
  1291→        action: 'update_password',
  1292→        payload: {
  1293→          password: newPassword,
  1294→          access_token: session.access_token
  1295→        }
  1296→      }
  1297→    });
  1298→
  1299→    if (error) {
  1300→      console.error('❌ Password update failed:', error);
  1301→
  1302→      // Extract detailed error from response body if available
  1303→      let errorMessage = 'Failed to update password. Please try again.';
  1304→
  1305→      // In newer Supabase JS versions, error.context is the Response object
  1306→      if (error.context && typeof error.context.json === 'function') {
  1307→        try {
  1308→          const errorBody = await error.context.json();
  1309→          console.error('   Error body from Response:', errorBody);
  1310→          if (errorBody?.error) {
  1311→            errorMessage = errorBody.error;
  1312→          }
  1313→        } catch (parseErr) {
  1314→          // Silent - use default message
  1315→        }
  1316→      } else if (error.context?.body) {
  1317→        // Fallback for older Supabase JS versions
  1318→        try {
  1319→          const errorBody = typeof error.context.body === 'string'
  1320→            ? JSON.parse(error.context.body)
  1321→            : error.context.body;
  1322→          if (errorBody?.error) {
  1323→            errorMessage = errorBody.error;
  1324→          }
  1325→        } catch (parseErr) {
  1326→          // Silent - use default message
  1327→        }
  1328→      }
  1329→
  1330→      return {
  1331→        success: false,
  1332→        error: errorMessage
  1333→      };
  1334→    }
  1335→
  1336→    if (!data.success && data.error) {
  1337→      return {
  1338→        success: false,
  1339→        error: data.error
  1340→      };
  1341→    }
  1342→
  1343→    console.log('✅ Password updated successfully');
  1344→
  1345→    // Keep user logged in by syncing the Supabase session to secure storage
  1346→    // The user has proven account ownership by accessing their email
  1347→    const userId = session.user?.user_metadata?.user_id || session.user?.id;
  1348→    const userType = session.user?.user_metadata?.user_type;
  1349→
  1350→    // Store session tokens
  1351→    setSecureAuthToken(session.access_token);
  1352→    if (userId) {
  1353→      setSecureSessionId(userId);
  1354→      setAuthState(true, userId);
  1355→    }
  1356→    if (userType) {
  1357→      setSecureUserType(userType);
  1358→    }
  1359→
  1360→    // Update login state
  1361→    isUserLoggedInState = true;
  1362→
  1363→    console.log('✅ User session preserved after password update');
  1364→    console.log('   User ID:', userId);
  1365→    console.log('   User Type:', userType);
  1366→
  1367→    return {
  1368→      success: true,
  1369→      message: data?.data?.message || 'Password updated successfully.'
  1370→    };
  1371→
  1372→  } catch (error) {
  1373→    console.error('❌ Password update error:', error);
  1374→    return {
  1375→      success: false,
  1376→      error: 'Network error. Please check your connection and try again.'
  1377→    };
  1378→  }
  1379→}
  1380→
  1381→// ============================================================================
  1382→// LinkedIn OAuth Functions
  1383→// ============================================================================
  1384→
  1385→/**
  1386→ * Initiate LinkedIn OAuth signup flow
  1387→ * Stores user type in localStorage before redirecting to LinkedIn
  1388→ *
  1389→ * @param {string} userType - 'Host' or 'Guest'
  1390→ * @returns {Promise<Object>} Result with success status or error
  1391→ */
  1392→export async function initiateLinkedInOAuth(userType) {
  1393→  console.log('[Auth] Initiating LinkedIn OAuth with userType:', userType);
  1394→
  1395→  // Store user type before redirect
  1396→  setLinkedInOAuthUserType(userType);
  1397→
  1398→  try {
  1399→    const { data, error } = await supabase.auth.signInWithOAuth({
  1400→      provider: 'linkedin_oidc',
  1401→      options: {
  1402→        redirectTo: `${window.location.origin}/account-profile`,
  1403→        scopes: 'openid profile email',
  1404→      }
  1405→    });
  1406→
  1407→    if (error) {
  1408→      clearLinkedInOAuthUserType();
  1409→      return { success: false, error: error.message };
  1410→    }
  1411→
  1412→    // OAuth redirect will happen automatically
  1413→    return { success: true, data };
  1414→  } catch (err) {
  1415→    clearLinkedInOAuthUserType();
  1416→    return { success: false, error: err.message };
  1417→  }
  1418→}
  1419→
  1420→/**
  1421→ * Handle LinkedIn OAuth callback after redirect
  1422→ * Creates user record if new user, or links accounts if existing
  1423→ *
  1424→ * @returns {Promise<Object>} Result with user data or error
  1425→ */
  1426→export async function handleLinkedInOAuthCallback() {
  1427→  console.log('[Auth] Handling LinkedIn OAuth callback');
  1428→
  1429→  // Get the session from OAuth callback
  1430→  const { data: { session }, error: sessionError } = await supabase.auth.getSession();
  1431→
  1432→  if (sessionError || !session) {
  1433→    clearLinkedInOAuthUserType();
  1434→    return {
  1435→      success: false,
  1436→      error: sessionError?.message || 'No session found after OAuth'
  1437→    };
  1438→  }
  1439→
  1440→  // Check if this is a fresh OAuth session (from LinkedIn)
  1441→  const user = session.user;
  1442→  const isLinkedInProvider = user?.app_metadata?.provider === 'linkedin_oidc';
  1443→
  1444→  if (!isLinkedInProvider) {
  1445→    return { success: false, error: 'Not a LinkedIn OAuth session' };
  1446→  }
  1447→
  1448→  // Retrieve stored user type
  1449→  const userType = getLinkedInOAuthUserType() || 'Guest';
  1450→
  1451→  // Extract LinkedIn data from user_metadata
  1452→  const linkedInData = {
  1453→    email: user.email,
  1454→    firstName: user.user_metadata?.given_name || user.user_metadata?.first_name || '',
  1455→    lastName: user.user_metadata?.family_name || user.user_metadata?.last_name || '',
  1456→    profilePhoto: user.user_metadata?.picture || user.user_metadata?.avatar_url || null,
  1457→    supabaseUserId: user.id,
  1458→  };
  1459→
  1460→  console.log('[Auth] LinkedIn data:', linkedInData);
  1461→  console.log('[Auth] Stored userType:', userType);
  1462→
  1463→  // Call Edge Function to create/link user record
  1464→  try {
  1465→    const response = await fetch(`${import.meta.env.VITE_SUPABASE_URL}/functions/v1/auth-user`, {
  1466→      method: 'POST',
  1467→      headers: {
  1468→        'Content-Type': 'application/json',
  1469→        'apikey': import.meta.env.VITE_SUPABASE_ANON_KEY,
  1470→        'Authorization': `Bearer ${session.access_token}`,
  1471→      },
  1472→      body: JSON.stringify({
  1473→        action: 'oauth_signup',
  1474→        payload: {
  1475→          ...linkedInData,
  1476→          userType,
  1477→          provider: 'linkedin_oidc',
  1478→          access_token: session.access_token,
  1479→          refresh_token: session.refresh_token,
  1480→        }
  1481→      })
  1482→    });
  1483→
  1484→    const data = await response.json();
  1485→
  1486→    if (!response.ok || !data.success) {
  1487→      clearLinkedInOAuthUserType();
  1488→      return {
  1489→        success: false,
  1490→        error: data.error || 'Failed to create user record',
  1491→        isDuplicate: data.isDuplicate || false,
  1492→        existingEmail: data.existingEmail || null,
  1493→      };
  1494→    }
  1495→
  1496→    // Clear OAuth storage keys
  1497→    clearLinkedInOAuthUserType();
  1498→
  1499→    // Store session data
  1500→    setAuthToken(session.access_token);
  1501→    setSessionId(data.data.user_id);
  1502→    setAuthState(true, data.data.user_id);
  1503→    if (userType) {
  1504→      setUserType(userType);
  1505→    }
  1506→
  1507→    return {
  1508→      success: true,
  1509→      data: data.data,
  1510→      isNewUser: data.data.isNewUser,
  1511→    };
  1512→
  1513→  } catch (err) {
  1514→    clearLinkedInOAuthUserType();
  1515→    return { success: false, error: err.message };
  1516→  }
  1517→}
  1518→
  1519→/**
  1520→ * Initiate LinkedIn OAuth login flow
  1521→ * Sets login flow flag and redirects to current page after OAuth
  1522→ *
  1523→ * @returns {Promise<Object>} Result with success status or error
  1524→ */
  1525→export async function initiateLinkedInOAuthLogin() {
  1526→  console.log('[Auth] Initiating LinkedIn OAuth Login');
  1527→
  1528→  // Clear any existing signup flow flags to prevent conflicts
  1529→  clearLinkedInOAuthUserType();
  1530→
  1531→  // Set login flow flag before redirect
  1532→  setLinkedInOAuthLoginFlow(true);
  1533→
  1534→  try {
  1535→    const { data, error } = await supabase.auth.signInWithOAuth({
  1536→      provider: 'linkedin_oidc',
  1537→      options: {
  1538→        // Stay on current page after OAuth (no redirect to profile)
  1539→        redirectTo: window.location.href,
  1540→        scopes: 'openid profile email',
  1541→      }
  1542→    });
  1543→
  1544→    if (error) {
  1545→      clearLinkedInOAuthLoginFlow();
  1546→      return { success: false, error: error.message };
  1547→    }
  1548→
  1549→    // OAuth redirect will happen automatically
  1550→    return { success: true, data };
  1551→  } catch (err) {
  1552→    clearLinkedInOAuthLoginFlow();
  1553→    return { success: false, error: err.message };
  1554→  }
  1555→}
  1556→
  1557→/**
  1558→ * Handle LinkedIn OAuth login callback
  1559→ * Verifies user exists in database, returns session data or error
  1560→ *
  1561→ * @returns {Promise<Object>} Result with user data or error (userNotFound: true if account doesn't exist)
  1562→ */
  1563→export async function handleLinkedInOAuthLoginCallback() {
  1564→  console.log('[Auth] Handling LinkedIn OAuth Login callback');
  1565→
  1566→  // Verify this is a login flow
  1567→  if (!getLinkedInOAuthLoginFlow()) {
  1568→    console.log('[Auth] Not a login flow callback, skipping');
  1569→    return { success: false, error: 'Not a login flow' };
  1570→  }
  1571→
  1572→  try {
  1573→    // Get the session from OAuth callback
  1574→    const { data: { session }, error: sessionError } = await supabase.auth.getSession();
  1575→
  1576→    if (sessionError || !session) {
  1577→      clearLinkedInOAuthLoginFlow();
  1578→      return {
  1579→        success: false,
  1580→        error: sessionError?.message || 'No session found after OAuth'
  1581→      };
  1582→    }
  1583→
  1584→    // Check if this is a fresh OAuth session (from LinkedIn)
  1585→    const user = session.user;
  1586→    const isLinkedInProvider = user?.app_metadata?.provider === 'linkedin_oidc';
  1587→
  1588→    if (!isLinkedInProvider) {
  1589→      clearLinkedInOAuthLoginFlow();
  1590→      return { success: false, error: 'Not a LinkedIn OAuth session' };
  1591→    }
  1592→
  1593→    // Extract LinkedIn data
  1594→    const email = user.email;
  1595→    const supabaseUserId = user.id;
  1596→
  1597→    console.log('[Auth] LinkedIn OAuth login data:', { email, supabaseUserId });
  1598→
  1599→    // Call Edge Function to verify user exists
  1600→    const response = await fetch(`${import.meta.env.VITE_SUPABASE_URL}/functions/v1/auth-user`, {
  1601→      method: 'POST',
  1602→      headers: {
  1603→        'Content-Type': 'application/json',
  1604→        'apikey': import.meta.env.VITE_SUPABASE_ANON_KEY,
  1605→        'Authorization': `Bearer ${session.access_token}`,
  1606→      },
  1607→      body: JSON.stringify({
  1608→        action: 'oauth_login',
  1609→        payload: {
  1610→          email,
  1611→          supabaseUserId,
  1612→          access_token: session.access_token,
  1613→          refresh_token: session.refresh_token,
  1614→        }
  1615→      })
  1616→    });
  1617→
  1618→    const data = await response.json();
  1619→
  1620→    // Always clear the login flow flag
  1621→    clearLinkedInOAuthLoginFlow();
  1622→
  1623→    if (!response.ok || !data.success) {
  1624→      // Check if user was not found
  1625→      if (data.data?.userNotFound) {
  1626→        console.log('[Auth] User not found for OAuth login:', email);
  1627→        return {
  1628→          success: false,
  1629→          userNotFound: true,
  1630→          email: email,
  1631→          error: 'No account found with this email'
  1632→        };
  1633→      }
  1634→
  1635→      return {
  1636→        success: false,
  1637→        error: data.error || 'Failed to login'
  1638→      };
  1639→    }
  1640→
  1641→    // Store session data
  1642→    const { user_id, user_type, supabase_user_id, access_token, refresh_token } = data.data;
  1643→
  1644→    setAuthToken(session.access_token);
  1645→    setSessionId(user_id);
  1646→    setAuthState(true, user_id);
  1647→    if (user_type) {
  1648→      setUserType(user_type);
  1649→    }
  1650→
  1651→    // Store Supabase user ID for reference
  1652→    if (supabase_user_id) {
  1653→      localStorage.setItem('splitlease_supabase_user_id', supabase_user_id);
  1654→    }
  1655→
  1656→    console.log('[Auth] LinkedIn OAuth login successful');
  1657→    return {
  1658→      success: true,
  1659→      data: data.data
  1660→    };
  1661→
  1662→  } catch (err) {
  1663→    clearLinkedInOAuthLoginFlow();
  1664→    console.error('[Auth] LinkedIn OAuth login callback error:', err);
  1665→    return { success: false, error: err.message };
  1666→  }
  1667→}
