   279→ */
   280→function getWeeklySchedulePeriod(weeksOffered) {
   281→  if (!weeksOffered || weeksOffered === 'Every week' || weeksOffered === 'every week') {
   282→    return 1;
   283→  }
   284→
   285→  // Handle numeric values (e.g., "2" means "2 weeks on, 2 weeks off")
