   300→      (pattern.includes('1 week on') && pattern.includes('1 week off'))) {
   301→    return 2;
   302→  }
   303→
   304→  if (pattern.includes('2 on 2 off') || pattern.includes('2on2off') ||
   305→      (pattern.includes('two week') && pattern.includes('two week')) ||
   306→      (pattern.includes('2 week on') && pattern.includes('2 week off'))) {
   307→    return 2;
   308→  }
   309→
   310→  if (pattern.includes('1 on 3 off') || pattern.includes('1on3off') ||
   311→      (pattern.includes('one week on') && pattern.includes('three week')) ||
   312→      (pattern.includes('1 week on') && pattern.includes('3 week off'))) {
   313→    return 4;
   314→  }
   315→
   316→  return 1; // Default: "Every week"
   317→}
   318→
   319→/**
   320→ * Get actual weeks during 4-week period based on pattern
   321→ * Handles both numeric values (e.g., 2) and text patterns (e.g., "2 weeks on, 2 weeks off")
   322→ */
   323→function getActualWeeksDuring4Week(weeksOffered) {
   324→  if (!weeksOffered || weeksOffered === 'Every week' || weeksOffered === 'every week') {
   325→    return 4; // Every week = 4 weeks per 4-week period
   326→  }
   327→
   328→  // Handle numeric values (e.g., "2" means "2 weeks on, 2 weeks off")
   329→  const numValue = typeof weeksOffered === 'number' ? weeksOffered : parseInt(weeksOffered, 10);
   330→  if (!isNaN(numValue) && numValue > 0 && numValue <= 4) {
   331→    // For "X weeks on, X weeks off" pattern:
   332→    // 1 on 1 off: 2 actual weeks per 4-week period (cycles twice in 4 weeks)
   333→    // 2 on 2 off: 2 actual weeks per 4-week period (one cycle in 4 weeks)
   334→    // 3 on 3 off: would be ~2 weeks in 4-week period
   335→    // This returns the actual active weeks in a 4-week span
   336→    return 2; // For X on X off patterns, you get half the time
   337→  }
   338→
   339→  const pattern = String(weeksOffered).toLowerCase();
   340→
   341→  // Check for various patterns
   342→  if (pattern.includes('1 on 1 off') || pattern.includes('1on1off') ||
   343→      (pattern.includes('one week on') && pattern.includes('one week off')) ||
   344→      (pattern.includes('1 week on') && pattern.includes('1 week off'))) {
   345→    return 2;
   346→  }
   347→
   348→  if (pattern.includes('2 on 2 off') || pattern.includes('2on2off') ||
   349→      (pattern.includes('two week') && pattern.includes('two week')) ||
   350→      (pattern.includes('2 week on') && pattern.includes('2 week off'))) {
   351→    return 2;
   352→  }
   353→
   354→  if (pattern.includes('1 on 3 off') || pattern.includes('1on3off') ||
   355→      (pattern.includes('one week on') && pattern.includes('three week')) ||
   356→      (pattern.includes('1 week on') && pattern.includes('3 week off'))) {
   357→    return 1;
   358→  }
   359→
   360→  return 4; // Default: "Every week" = present all 4 weeks
   361→}
   362→
   363→/**
   364→ * Get nightly rate based on number of nights from listing
   365→ * @param {number} nightsCount - Number of nights selected
   366→ * @param {Object} listing - Listing object with price fields
   367→ * @returns {number} Base nightly rate from host
   368→ */
   369→function getNightlyRateForNights(nightsCount, listing) {
   370→  // Map nights to price fields
   371→  const priceFieldMap = {
   372→    1: listing.nightlyHostRateFor1Night || listing['💰Nightly Host Rate for 1 night'],
   373→    2: listing.nightlyHostRateFor2Nights || listing['💰Nightly Host Rate for 2 nights'],
   374→    3: listing.nightlyHostRateFor3Nights || listing['💰Nightly Host Rate for 3 nights'],
   375→    4: listing.nightlyHostRateFor4Nights || listing['💰Nightly Host Rate for 4 nights'],
   376→    5: listing.nightlyHostRateFor5Nights || listing['💰Nightly Host Rate for 5 nights'],
   377→    7: listing.nightlyHostRateFor7Nights || listing['💰Nightly Host Rate for 7 nights']
   378→  };
   379→
   380→  const rate = parseFloat(priceFieldMap[nightsCount] || 0);
   381→
   382→  // If no rate found for exact nights, fall back to 4-night rate
   383→  if (!rate || rate === 0) {
   384→    return parseFloat(listing['💰Nightly Host Rate for 4 nights'] || 0);
   385→  }
   386→
   387→  return rate;
   388→}
   389→
   390→/**
   391→ * Create empty price breakdown for invalid cases
   392→ * @returns {Object} Empty price breakdown
   393→ */
   394→function createEmptyPriceBreakdown() {
   395→  return {
   396→    basePrice: 0,
   397→    nightlyRate: 0,
   398→    discountAmount: 0,
   399→    markupAmount: 0,
   400→    totalPrice: 0,
   401→    pricePerNight: 0,
   402→    numberOfNights: 0,
   403→    fourWeekRent: 0,
   404→    reservationTotal: 0,
   405→    initialPayment: 0,
   406→    cleaningFee: 0,
   407→    damageDeposit: 0,
   408→    valid: false
   409→  };
   410→}
