  // ...
}

// Option 2: Use the existing logger.js utility (better)
import { logger } from './logger.js';

export function checkSplitLeaseCookies() {
  // ...
  logger.debug('Split Lease Cookie Auth Check:', { isLoggedIn, username });
  // ...
}
```

**Testing:**
