useEffect(() => {
  onSelectionChange?.(selectedDays)
// eslint-disable-next-line react-hooks/exhaustive-deps
}, [selectedDays])

useEffect(() => {
  onPriceChange?.(priceBreakdown)
// eslint-disable-next-line react-hooks/exhaustive-deps
