     1→/**
     2→ * Split Lease Authentication Utilities
     3→ * Extracted from input/index/script.js
     4→ *
     5→ * Provides authentication-related functions for:
     6→ * - Cookie checking and parsing
     7→ * - Token management
     8→ * - Login status detection
     9→ * - Username extraction
    10→ * - Session validation
    11→ *
    12→ * No fallback mechanisms - returns null or throws error on auth failure
    13→ *
    14→ * Usage:
    15→ *   import { checkAuthStatus, getUsernameFromCookies } from './auth.js'
    16→ */
    17→
    18→import {
    19→  AUTH_STORAGE_KEYS,
    20→  SESSION_VALIDATION,
    21→  SIGNUP_LOGIN_URL,
    22→  ACCOUNT_PROFILE_URL
    23→} from './constants.js';
    24→import { supabase } from './supabase.js';
    25→import {
    26→  setAuthToken as setSecureAuthToken,
    27→  getAuthToken as getSecureAuthToken,
    28→  setSessionId as setSecureSessionId,
    29→  getSessionId as getSecureSessionId,
    30→  setAuthState,
    31→  getAuthState,
    32→  getUserId as getPublicUserId,
    33→  setUserType as setSecureUserType,
    34→  getUserType as getSecureUserType,
    35→  clearAllAuthData,
    36→  hasValidTokens,
    37→  migrateFromLegacyStorage,
    38→  getFirstName as getSecureFirstName,
    39→  getAvatarUrl as getSecureAvatarUrl,
    40→  setFirstName as setSecureFirstName,
    41→  setAvatarUrl as setSecureAvatarUrl,
    42→  setLinkedInOAuthUserType,
    43→  getLinkedInOAuthUserType,
    44→  clearLinkedInOAuthUserType,
    45→  setLinkedInOAuthLoginFlow,
    46→  getLinkedInOAuthLoginFlow,
    47→  clearLinkedInOAuthLoginFlow
    48→} from './secureStorage.js';
    49→
    50→// ============================================================================
    51→// Auth State Management
    52→// ============================================================================
    53→
    54→let isUserLoggedInState = false;
    55→let authCheckAttempts = 0;
    56→const MAX_AUTH_CHECK_ATTEMPTS = SESSION_VALIDATION.MAX_AUTH_CHECK_ATTEMPTS;
    57→
    58→// ============================================================================
    59→// Cookie Parsing Utilities
    60→// ============================================================================
    61→
    62→/**
    63→ * Parse username from document cookies
    64→ * Decodes URL-encoded cookie values and removes surrounding quotes
    65→ *
    66→ * @returns {string|null} Username if found, null if no username cookie exists
    67→ */
    68→export function getUsernameFromCookies() {
    69→  const cookies = document.cookie.split('; ');
    70→  const usernameCookie = cookies.find(c => c.startsWith('username='));
    71→
    72→  if (usernameCookie) {
    73→    let username = decodeURIComponent(usernameCookie.split('=')[1]);
    74→    // Remove surrounding quotes if present (both single and double quotes)
    75→    username = username.replace(/^["']|["']$/g, '');
    76→    return username;
    77→  }
    78→
    79→  return null;
    80→}
    81→
    82→/**
    83→ * Check Split Lease cookies from Bubble app
    84→ * Verifies both loggedIn and username cookies
    85→ *
    86→ * @returns {Object} Authentication status object
    87→ *   - isLoggedIn: boolean indicating if user is logged in
    88→ *   - username: string with username or null if not set
    89→ */
    90→export function checkSplitLeaseCookies() {
    91→  const cookies = document.cookie.split('; ');
    92→  const loggedInCookie = cookies.find(c => c.startsWith('loggedIn='));
    93→  const usernameCookie = cookies.find(c => c.startsWith('username='));
    94→
    95→  const isLoggedIn = loggedInCookie ? loggedInCookie.split('=')[1] === 'true' : false;
    96→  const username = getUsernameFromCookies();
    97→
    98→  // Log the authentication status to console
    99→  console.log('🔐 Split Lease Cookie Auth Check:');
   100→  console.log('   Logged In:', isLoggedIn);
   101→  console.log('   Username:', username || 'not set');
   102→  console.log('   Raw Cookies:', { loggedInCookie, usernameCookie });
   103→
   104→  return { isLoggedIn, username };
   105→}
   106→
   107→// ============================================================================
   108→// Authentication Status Checking
   109→// ============================================================================
   110→
   111→/**
   112→ * Lightweight authentication status check
   113→ * Checks auth state (not tokens) and validates session
   114→ *
   115→ * Supports both:
   116→ * - Supabase Auth sessions (native signup)
   117→ * - Legacy Bubble auth (tokens in localStorage)
   118→ *
   119→ * No fallback mechanisms - returns boolean directly
   120→ * On failure: returns false without fallback logic
   121→ *
   122→ * @returns {Promise<boolean>} True if user is authenticated, false otherwise
   123→ */
   124→export async function checkAuthStatus() {
   125→  console.log('🔍 Checking authentication status...');
   126→
   127→  // Try to migrate from legacy storage first
   128→  const migrated = await migrateFromLegacyStorage();
   129→  if (migrated) {
   130→    console.log('✅ Migrated from legacy storage');
   131→  }
   132→
   133→  // First check cross-domain cookies from .split.lease (for compatibility)
   134→  const splitLeaseAuth = checkSplitLeaseCookies();
   135→
   136→  if (splitLeaseAuth.isLoggedIn) {
   137→    console.log('✅ User authenticated via Split Lease cookies');
   138→    console.log('   Username:', splitLeaseAuth.username);
   139→    isUserLoggedInState = true;
   140→    setAuthState(true);
   141→    return true;
   142→  }
   143→
   144→  // Check Supabase Auth session (for native Supabase signups)
   145→  // CRITICAL: Supabase client may not have loaded session from localStorage yet
   146→  // We need to handle the race condition where getSession() returns null initially
   147→  // but INITIAL_SESSION fires shortly after with a valid session
   148→  try {
   149→    let { data: { session }, error } = await supabase.auth.getSession();
   150→
   151→    // If no session on first check, wait briefly for Supabase to initialize
   152→    // This handles the race condition on page load
   153→    if (!session && !error) {
   154→      console.log('🔄 No immediate Supabase session, waiting briefly for initialization...');
   155→      await new Promise(resolve => setTimeout(resolve, 200));
   156→      const retryResult = await supabase.auth.getSession();
   157→      session = retryResult.data?.session;
   158→      error = retryResult.error;
   159→      if (session) {
   160→        console.log('✅ Found Supabase session after brief wait');
   161→      }
   162→    }
   163→
   164→    if (session && !error) {
   165→      console.log('✅ User authenticated via Supabase Auth session');
   166→      console.log('   User ID:', session.user?.id);
   167→      console.log('   Email:', session.user?.email);
   168→
   169→      // Sync Supabase session to our storage for consistency
   170→      const userId = session.user?.user_metadata?.user_id || session.user?.id;
   171→      const userType = session.user?.user_metadata?.user_type;
   172→
   173→      setSecureAuthToken(session.access_token);
   174→      if (userId) {
   175→        setSecureSessionId(userId);
   176→        setAuthState(true, userId);
   177→      }
   178→      if (userType) {
   179→        setSecureUserType(userType);
   180→      }
   181→
   182→      isUserLoggedInState = true;
   183→      return true;
   184→    }
   185→  } catch (err) {
   186→    console.log('⚠️ Supabase session check failed:', err.message);
   187→    // Continue to check legacy auth
   188→  }
   189→
   190→  // Check auth state (not tokens directly) - legacy Bubble auth
   191→  const authState = getAuthState();
   192→
   193→  if (authState) {
   194→    // Verify we actually have tokens
   195→    const hasTokens = await hasValidTokens();
   196→
   197→    if (hasTokens) {
   198→      console.log('✅ User authenticated via secure storage (legacy)');
   199→      isUserLoggedInState = true;
   200→      return true;
   201→    }
   202→  }
   203→
   204→  console.log('❌ User not authenticated');
   205→  isUserLoggedInState = false;
   206→  setAuthState(false);
   207→  return false;
   208→}
   209→
   210→// ============================================================================
   211→// Session Management
   212→// ============================================================================
   213→
   214→/**
   215→ * Validate session by checking if tokens exist
   216→ * Bubble API handles actual token expiry - we validate on each request
   217→ *
   218→ * @returns {boolean} True if session is valid, false if expired or missing
   219→ */
   220→export function isSessionValid() {
   221→  // Simply check if auth state is set
   222→  // Bubble will reject expired tokens on API calls
   223→  return getAuthState();
   224→}
   225→
   226→/**
   227→ * Clear authentication data from storage
   228→ * Removes all auth tokens, session IDs, timestamps, user type, and cookies
   229→ */
   230→export function clearAuthData() {
   231→  // Use secure storage clear (handles both secure tokens and state)
   232→  clearAllAuthData();
   233→
   234→  isUserLoggedInState = false;
   235→  console.log('🗑️ Authentication data cleared (secure storage and cookies)');
   236→}
   237→
   238→// ============================================================================
   239→// Token Management
   240→// ============================================================================
   241→
   242→/**
   243→ * Get authentication token from secure storage
   244→ * NOTE: This should only be used internally for API calls
   245→ * External code should not access tokens directly
   246→ *
   247→ * @returns {string|null} Auth token if exists, null otherwise
   248→ */
   249→export function getAuthToken() {
   250→  return getSecureAuthToken();
   251→}
   252→
   253→/**
   254→ * Store authentication token in secure storage
   255→ *
   256→ * @param {string} token - Authentication token to store
   257→ */
   258→export function setAuthToken(token) {
   259→  setSecureAuthToken(token);
   260→}
   261→
   262→/**
   263→ * Get session ID from secure storage
   264→ * NOTE: This should only be used internally
   265→ * External code should not access session IDs directly
   266→ *
   267→ * @returns {string|null} Session ID if exists, null otherwise
   268→ */
   269→export function getSessionId() {
   270→  return getSecureSessionId();
   271→}
   272→
   273→/**
   274→ * Store session ID in secure storage
   275→ *
   276→ * @param {string} sessionId - Session ID to store
   277→ */
   278→export function setSessionId(sessionId) {
   279→  setSecureSessionId(sessionId);
   280→}
   281→
   282→/**
   283→ * Get user ID from public state (non-sensitive identifier)
   284→ * NOTE: This is public state, not the encrypted session ID
   285→ *
   286→ * @returns {string|null} User ID if exists, null otherwise
   287→ */
   288→export function getUserId() {
   289→  return getPublicUserId();
   290→}
   291→
   292→/**
   293→ * Get user type from storage (public state)
   294→ *
   295→ * @returns {string|null} User type ('Host' or 'Guest') if exists, null otherwise
   296→ */
   297→export function getUserType() {
   298→  return getSecureUserType();
   299→}
   300→
   301→/**
   302→ * Store user type (public state)
   303→ *
   304→ * @param {string} userType - User type to store ('Host' or 'Guest')
   305→ */
   306→export function setUserType(userType) {
   307→  if (userType) {
   308→    setSecureUserType(userType);
   309→  }
   310→}
   311→
   312→// ============================================================================
   313→// User Information
   314→// ============================================================================
   315→
   316→/**
   317→ * Get current logged-in username from cookies
   318→ *
   319→ * @returns {string|null} Username if logged in, null otherwise
   320→ */
   321→export function getCurrentUsername() {
   322→  if (!isUserLoggedInState) {
   323→    return null;
   324→  }
   325→  return getUsernameFromCookies();
   326→}
   327→
   328→/**
   329→ * Store username in global scope for use in redirects
   330→ * Also used by handleLoggedInUser to persist username
   331→ *
   332→ * @param {string} username - Username to store
   333→ */
   334→export function storeCurrentUsername(username) {
   335→  if (typeof window !== 'undefined') {
   336→    window.currentUsername = username;
   337→  }
   338→}
   339→
   340→/**
   341→ * Get stored username from global scope
   342→ *
   343→ * @returns {string|null} Stored username or null
   344→ */
   345→export function getStoredUsername() {
   346→  if (typeof window !== 'undefined') {
   347→    return window.currentUsername || null;
   348→  }
   349→  return null;
   350→}
   351→
   352→// ============================================================================
   353→// User Profile Getters (Proxied from secureStorage)
   354→// ============================================================================
   355→
   356→/**
   357→ * Get first name
   358→ * @returns {string|null} First name or null
   359→ */
   360→export function getFirstName() {
   361→  return getSecureFirstName();
   362→}
   363→
   364→/**
   365→ * Get avatar URL
