   848→/**
   849→ * Validate token via Supabase Edge Function (auth-user) and fetch user data
   850→ * Two-step process:
   851→ * 1. Validate token via Edge Function (validates with Bubble + fetches from Supabase)
   852→ * 2. Cache user type locally
   853→ *
   854→ * ✅ MIGRATED: Now uses Edge Functions instead of direct Bubble API calls
   855→ * API key is stored server-side in Supabase Secrets
   856→ *
   857→ * @param {Object} options - Configuration options
   858→ * @param {boolean} options.clearOnFailure - If true, clears auth data when validation fails. Default: true.
   859→ *                                           Set to false when calling immediately after login/signup to preserve
   860→ *                                           the fresh session even if user profile fetch fails.
   861→ * @returns {Promise<Object|null>} User data object with firstName, fullName, email, profilePhoto, userType, etc. or null if invalid
   862→ */
   863→export async function validateTokenAndFetchUser({ clearOnFailure = true } = {}) {
   864→  let token = getAuthToken();
   865→  let userId = getSessionId();
   866→
   867→  // If no legacy token/userId, check for Supabase Auth session and sync it
   868→  if (!token || !userId) {
   869→    console.log('[Auth] No legacy token found, checking for Supabase Auth session...');
   870→
   871→    try {
   872→      let { data: { session }, error } = await supabase.auth.getSession();
   873→
   874→      // CRITICAL: Supabase client may not have loaded session from localStorage yet
   875→      // Wait briefly for initialization if no session found
   876→      if (!session && !error) {
   877→        console.log('[Auth] 🔄 No immediate session, waiting briefly for Supabase initialization...');
   878→        await new Promise(resolve => setTimeout(resolve, 200));
   879→        const retryResult = await supabase.auth.getSession();
   880→        session = retryResult.data?.session;
   881→        error = retryResult.error;
   882→        if (session) {
   883→          console.log('[Auth] ✅ Found Supabase session after brief wait');
   884→        }
   885→      }
   886→
   887→      if (session && !error) {
   888→        console.log('[Auth] ✅ Found Supabase Auth session, syncing to secure storage');
   889→        token = session.access_token;
   890→        userId = session.user?.user_metadata?.user_id || session.user?.id;
   891→
   892→        // Sync to secure storage for consistency
   893→        setSecureAuthToken(token);
   894→        if (userId) {
   895→          setSecureSessionId(userId);
   896→          setAuthState(true, userId);
   897→        }
   898→        const userType = session.user?.user_metadata?.user_type;
   899→        if (userType) {
   900→          setSecureUserType(userType);
   901→        }
   902→      }
   903→    } catch (err) {
   904→      console.log('[Auth] Error checking Supabase session:', err.message);
   905→    }
   906→  }
   907→
   908→  if (!token || !userId) {
   909→    console.log('[Auth] No token or user ID found - user not logged in');
   910→    return null;
   911→  }
   912→
   913→  console.log('🔍 Validating token and fetching user data via Edge Function...');
   914→
   915→  try {
   916→    const { data, error } = await supabase.functions.invoke('auth-user', {
   917→      body: {
   918→        action: 'validate',
   919→        payload: {
   920→          token,
   921→          user_id: userId
   922→        }
   923→      }
   924→    });
   925→
   926→    if (error) {
   927→      console.error('❌ Edge Function error:', error);
   928→      console.error('   Error context:', error.context);
   929→
   930→      // Extract detailed error for logging
   931→      // In newer Supabase JS versions, error.context is the Response object
   932→      if (error.context && typeof error.context.json === 'function') {
   933→        try {
   934→          const errorBody = await error.context.json();
   935→          console.error('   Error body from Response:', errorBody);
   936→          if (errorBody?.error) {
   937→            console.error('   Detailed error from response:', errorBody.error);
   938→          }
   939→        } catch (parseErr) {
   940→          // Silent - just for logging
   941→        }
   942→      } else if (error.context?.body) {
   943→        // Fallback for older Supabase JS versions
   944→        try {
   945→          const errorBody = typeof error.context.body === 'string'
   946→            ? JSON.parse(error.context.body)
   947→            : error.context.body;
   948→          if (errorBody?.error) {
   949→            console.error('   Detailed error from response:', errorBody.error);
   950→          }
   951→        } catch (parseErr) {
   952→          // Silent - just for logging
   953→        }
   954→      }
   955→
   956→      // Only clear auth data if clearOnFailure is true
   957→      // This allows callers to preserve fresh sessions even when validation fails
   958→      if (clearOnFailure) {
   959→        clearAuthData();
   960→      }
   961→      isUserLoggedInState = false;
   962→      return null;
   963→    }
   964→
