  1146→// URL Auth Error Detection (Magic Link / OTP Errors)
  1147→// ============================================================================
  1148→
  1149→/**
  1150→ * Check URL hash for Supabase auth errors
  1151→ * Returns error info if present, null otherwise
  1152→ *
  1153→ * When Supabase redirects after a failed magic link (expired OTP, already used, etc.),
  1154→ * it includes error params in the URL hash:
  1155→ * #error=access_denied&error_code=otp_expired&error_description=Email+link+is+invalid+or+has+expired
  1156→ *
  1157→ * @returns {Object|null} Error object with error, errorCode, errorDescription, or null if no error
  1158→ */
  1159→export function checkUrlForAuthError() {
  1160→  const hash = window.location.hash;
  1161→  if (!hash) return null;
  1162→
  1163→  const params = new URLSearchParams(hash.substring(1));
  1164→  const error = params.get('error');
  1165→  const errorCode = params.get('error_code');
  1166→  const errorDescription = params.get('error_description');
  1167→
  1168→  if (error || errorCode) {
  1169→    return {
  1170→      error,
  1171→      errorCode,
  1172→      errorDescription: errorDescription ? decodeURIComponent(errorDescription.replace(/\+/g, ' ')) : null
  1173→    };
  1174→  }
  1175→
  1176→  return null;
  1177→}
  1178→
  1179→/**
  1180→ * Clear auth error params from URL hash
  1181→ * Call this after handling the error to prevent re-processing
  1182→ * Uses replaceState to not add to browser history
  1183→ */
  1184→export function clearAuthErrorFromUrl() {
  1185→  if (window.location.hash) {
  1186→    window.history.replaceState(null, '', window.location.pathname + window.location.search);
  1187→  }
  1188→}
