    42→};
    43→
    44→/**
    45→ * Store authentication token
    46→ * @param {string} token - Bearer token from Bubble API
    47→ */
    48→export function setAuthToken(token) {
    49→  if (!token) return;
    50→  localStorage.setItem(SECURE_KEYS.AUTH_TOKEN, token);
    51→}
    52→
    53→/**
    54→ * Retrieve authentication token
