     1→/**
     2→ * useMessagingPageLogic - WITH SUPABASE REALTIME
     3→ *
     4→ * All business logic for the Messaging Page following Hollow Component Pattern.
     5→ *
     6→ * Responsibilities:
     7→ * - Authentication check (redirect if not logged in)
     8→ * - Fetch threads on mount via Edge Function
     9→ * - URL parameter sync (?thread=THREAD_ID)
    10→ * - Fetch messages when thread selected
    11→ * - Message sending handler
    12→ * - REAL-TIME: Subscribe to thread channel for instant message delivery
    13→ * - REAL-TIME: Typing indicators via Presence
    14→ *
    15→ * NO FALLBACK: Real data only, no mock data in production
    16→ */
    17→
    18→import { useState, useEffect, useCallback, useRef } from 'react';
    19→import { checkAuthStatus, validateTokenAndFetchUser, getFirstName, getAvatarUrl } from '../../../lib/auth.js';
    20→import { getUserId } from '../../../lib/secureStorage.js';
    21→import { supabase } from '../../../lib/supabase.js';
    22→
    23→export function useMessagingPageLogic() {
    24→  // ============================================================================
    25→  // AUTH STATE
    26→  // ============================================================================
    27→  const [authState, setAuthState] = useState({
    28→    isChecking: true,
    29→    shouldRedirect: false
    30→  });
    31→  const [user, setUser] = useState(null);
    32→
    33→  // ============================================================================
    34→  // DATA STATE
    35→  // ============================================================================
    36→  const [threads, setThreads] = useState([]);
    37→  const [selectedThread, setSelectedThread] = useState(null);
    38→  const [messages, setMessages] = useState([]);
    39→  const [threadInfo, setThreadInfo] = useState(null);
    40→
    41→  // ============================================================================
    42→  // UI STATE
    43→  // ============================================================================
    44→  const [isLoading, setIsLoading] = useState(true);
    45→  const [isLoadingMessages, setIsLoadingMessages] = useState(false);
    46→  const [error, setError] = useState(null);
    47→  const [messageInput, setMessageInput] = useState('');
    48→  const [isSending, setIsSending] = useState(false);
    49→
    50→  // ============================================================================
    51→  // REALTIME STATE
    52→  // ============================================================================
    53→  const [isOtherUserTyping, setIsOtherUserTyping] = useState(false);
    54→  const [typingUserName, setTypingUserName] = useState(null);
    55→  const channelRef = useRef(null);
    56→  const typingTimeoutRef = useRef(null);
    57→
    58→  // Ref to track if initial load is complete
    59→  const initialLoadDone = useRef(false);
    60→
    61→  // ============================================================================
    62→  // AUTH CHECK ON MOUNT
    63→  // ============================================================================
    64→  useEffect(() => {
    65→    async function init() {
    66→      try {
    67→        // Step 1: Check basic auth status
    68→        const isAuthenticated = await checkAuthStatus();
    69→
    70→        if (!isAuthenticated) {
    71→          console.log('[Messaging] User not authenticated, redirecting to home');
    72→          setAuthState({ isChecking: false, shouldRedirect: true });
    73→          setTimeout(() => {
    74→            window.location.href = '/?login=true';
    75→          }, 100);
    76→          return;
    77→        }
    78→
    79→        // Step 2: Get user data using the gold standard pattern
    80→        // Use clearOnFailure: false to preserve session even if profile fetch fails
    81→        const userData = await validateTokenAndFetchUser({ clearOnFailure: false });
    82→
    83→        if (userData) {
    84→          // User profile fetched successfully
    85→          setUser({
    86→            id: userData.userId,
    87→            email: userData.email,
    88→            bubbleId: userData.userId,  // userId from validateTokenAndFetchUser is the Bubble _id
    89→            firstName: userData.firstName,
    90→            lastName: userData.fullName?.split(' ').slice(1).join(' ') || '',
    91→            profilePhoto: userData.profilePhoto
    92→          });
    93→          console.log('[Messaging] User data loaded:', userData.firstName);
    94→        } else {
    95→          // Fallback: Use session metadata if profile fetch failed but session is valid
    96→          const { data: { session } } = await supabase.auth.getSession();
    97→
    98→          if (session?.user) {
    99→            const fallbackUser = {
   100→              id: session.user.id,
   101→              email: session.user.email,
   102→              bubbleId: session.user.user_metadata?.user_id || getUserId() || session.user.id,
   103→              firstName: session.user.user_metadata?.first_name || getFirstName() || session.user.email?.split('@')[0] || 'User',
   104→              lastName: session.user.user_metadata?.last_name || '',
   105→              profilePhoto: getAvatarUrl() || null
   106→            };
   107→            setUser(fallbackUser);
   108→            console.log('[Messaging] Using fallback user data from session:', fallbackUser.firstName);
   109→          } else {
   110→            // No session at all - redirect
   111→            console.log('[Messaging] No valid session, redirecting');
   112→            setAuthState({ isChecking: false, shouldRedirect: true });
   113→            setTimeout(() => {
   114→              window.location.href = '/?login=true';
   115→            }, 100);
   116→            return;
   117→          }
   118→        }
   119→
   120→        setAuthState({ isChecking: false, shouldRedirect: false });
   121→
   122→        // Fetch threads after auth is confirmed
   123→        await fetchThreads();
   124→        initialLoadDone.current = true;
   125→      } catch (err) {
   126→        console.error('[Messaging] Auth check failed:', err);
   127→        setError('Failed to check authentication. Please refresh the page.');
   128→        setIsLoading(false);
   129→      }
   130→    }
   131→
   132→    init();
   133→  }, []);
   134→
   135→  // Ref to track if initial thread selection has been done
   136→  const hasAutoSelectedThread = useRef(false);
   137→
   138→  // ============================================================================
   139→  // URL PARAM SYNC FOR THREAD SELECTION (runs once when threads load)
   140→  // ============================================================================
   141→  useEffect(() => {
   142→    if (!initialLoadDone.current || threads.length === 0 || hasAutoSelectedThread.current) return;
   143→
   144→    const params = new URLSearchParams(window.location.search);
   145→    const threadId = params.get('thread');
   146→
   147→    if (threadId) {
   148→      const thread = threads.find(t => t._id === threadId);
   149→      if (thread) {
   150→        hasAutoSelectedThread.current = true;
   151→        handleThreadSelectInternal(thread);
   152→      }
   153→    } else if (threads.length > 0) {
   154→      hasAutoSelectedThread.current = true;
   155→      handleThreadSelectInternal(threads[0]);
   156→    }
   157→  }, [threads]);
   158→
   159→  // ============================================================================
   160→  // REALTIME SUBSCRIPTION - Using Postgres Changes (more reliable than broadcast)
   161→  // ============================================================================
   162→  useEffect(() => {
   163→    if (!selectedThread || authState.isChecking || !user?.bubbleId) return;
   164→
   165→    const channelName = `messages-${selectedThread._id}`;
   166→    console.log('[Realtime] Subscribing to postgres_changes for thread:', selectedThread._id);
   167→
   168→    const channel = supabase.channel(channelName);
   169→
   170→    // Listen for new messages via Postgres Changes (INSERT events on _message table)
   171→    // NOTE: Filter removed due to column name with special characters not working with Realtime
   172→    // Client-side filtering is done instead
   173→    channel.on(
   174→      'postgres_changes',
   175→      {
   176→        event: 'INSERT',
   177→        schema: 'public',
   178→        table: '_message'
   179→      },
   180→      (payload) => {
   181→        console.log('[Realtime] postgres_changes event received:', payload);
   182→
   183→        const newRow = payload.new;
   184→        if (!newRow) return;
   185→
   186→        // Client-side filter: only process messages for this thread
   187→        if (newRow['Associated Thread/Conversation'] !== selectedThread._id) {
   188→          console.log('[Realtime] Message is for different thread, ignoring');
   189→          return;
   190→        }
   191→
   192→        console.log('[Realtime] Message is for this thread, processing...');
   193→
   194→        // Skip if this is our own message (already added optimistically)
   195→        const isOwnMessage = newRow['-Originator User'] === user?.bubbleId;
   196→
   197→        // Add message to state (avoid duplicates)
   198→        setMessages(prev => {
   199→          if (prev.some(m => m._id === newRow._id)) return prev;
   200→
   201→          // Transform database row to UI format
   202→          const transformedMessage = {
   203→            _id: newRow._id,
   204→            message_body: newRow['Message Body'],
   205→            sender_name: newRow['is Split Bot'] ? 'Split Bot' : (isOwnMessage ? 'You' : selectedThread.contact_name || 'User'),
   206→            sender_avatar: isOwnMessage ? user?.profilePhoto : undefined,
   207→            sender_type: newRow['is Split Bot'] ? 'splitbot' :
   208→              (newRow['-Originator User'] === newRow['-Host User'] ? 'host' : 'guest'),
   209→            is_outgoing: isOwnMessage,
   210→            timestamp: new Date(newRow['Created Date']).toLocaleString('en-US', {
   211→              month: 'short', day: 'numeric', hour: 'numeric', minute: '2-digit'
   212→            }),
   213→            call_to_action: newRow['Call to Action'] ? {
   214→              type: newRow['Call to Action'],
   215→              message: 'View Details'
   216→            } : undefined,
   217→            split_bot_warning: newRow['Split Bot Warning'],
   218→          };
   219→
   220→          return [...prev, transformedMessage];
   221→        });
   222→
   223→        // Clear typing indicator when message received
   224→        if (!isOwnMessage) {
   225→          setIsOtherUserTyping(false);
   226→          setTypingUserName(null);
   227→        }
   228→      }
   229→    );
   230→
   231→    // Listen for typing indicators via presence
   232→    channel.on('presence', { event: 'sync' }, () => {
   233→      const state = channel.presenceState();
   234→      const typingUsers = Object.values(state)
   235→        .flat()
   236→        .filter(u => u.typing && u.user_id !== user?.bubbleId);
   237→
   238→      if (typingUsers.length > 0) {
   239→        setIsOtherUserTyping(true);
   240→        setTypingUserName(typingUsers[0].user_name);
   241→      } else {
   242→        setIsOtherUserTyping(false);
   243→        setTypingUserName(null);
   244→      }
   245→    });
   246→
   247→    channel.subscribe(async (status) => {
   248→      console.log('[Realtime] Subscription status:', status);
   249→      if (status === 'SUBSCRIBED') {
   250→        console.log('[Realtime] Successfully subscribed to channel:', channelName);
   251→        // Track presence for typing indicators
   252→        await channel.track({
   253→          user_id: user?.bubbleId,
   254→          user_name: user?.firstName || 'User',
   255→          typing: false,
   256→          online_at: new Date().toISOString(),
   257→        });
   258→      } else if (status === 'CHANNEL_ERROR') {
   259→        console.error('[Realtime] Channel error - check RLS policies on _message table');
   260→      } else if (status === 'TIMED_OUT') {
   261→        console.error('[Realtime] Subscription timed out');
   262→      }
   263→    });
   264→
   265→    channelRef.current = channel;
   266→
   267→    // Cleanup
   268→    return () => {
   269→      console.log('[Realtime] Unsubscribing from channel:', channelName);
   270→      channel.unsubscribe();
   271→      channelRef.current = null;
   272→    };
   273→  }, [selectedThread?._id, authState.isChecking, user?.bubbleId]);
   274→
   275→  // ============================================================================
   276→  // TYPING INDICATOR
   277→  // ============================================================================
   278→
   279→  /**
   280→   * Track typing state via Presence
   281→   */
   282→  const trackTyping = useCallback(async (isTyping) => {
   283→    if (!channelRef.current || !user) return;
   284→
   285→    try {
   286→      await channelRef.current.track({
   287→        user_id: user.bubbleId,
   288→        user_name: user.firstName || 'User',
   289→        typing: isTyping,
   290→        typing_at: isTyping ? new Date().toISOString() : null,
   291→        online_at: new Date().toISOString(),
   292→      });
   293→    } catch (err) {
   294→      console.error('[Realtime] Failed to track typing:', err);
   295→    }
   296→  }, [user]);
   297→
   298→  // ============================================================================
   299→  // API CALLS
   300→  // ============================================================================
   301→
   302→  /**
   303→   * Fetch all threads for the authenticated user
   304→   * Direct Supabase query - no Edge Function needed for reads
   305→   */
   306→  async function fetchThreads() {
   307→    try {
   308→      setIsLoading(true);
   309→      setError(null);
   310→
   311→      // Get user's Bubble ID from multiple sources (state may not be set yet due to async setState)
   312→      let bubbleId = user?.bubbleId;
   313→
   314→      if (!bubbleId) {
   315→        // Try secure storage first (works for legacy auth users)
   316→        bubbleId = getUserId();
   317→      }
   318→
   319→      if (!bubbleId) {
   320→        // Fallback to Supabase session metadata
   321→        const { data: { session } } = await supabase.auth.getSession();
   322→        bubbleId = session?.user?.user_metadata?.user_id;
   323→      }
   324→
   325→      if (!bubbleId) {
   326→        throw new Error('User ID not available');
   327→      }
   328→
   329→      await fetchThreadsWithBubbleId(bubbleId);
   330→    } catch (err) {
   331→      console.error('Error fetching threads:', err);
   332→      setError(err.message || 'Failed to load conversations');
   333→    } finally {
   334→      setIsLoading(false);
   335→    }
   336→  }
   337→
   338→  /**
   339→   * Fetch threads with a known Bubble ID
   340→   */
   341→  async function fetchThreadsWithBubbleId(bubbleId) {
   342→    // Step 1: Query threads where user is host or guest
   343→    const { data: threads, error: threadsError } = await supabase
   344→      .from('thread')
   345→      .select(`
   346→        _id,
   347→        "Modified Date",
   348→        "-Host User",
   349→        "-Guest User",
   350→        "Listing",
   351→        "~Last Message",
   352→        "Thread Subject"
   353→      `)
   354→      .or(`"-Host User".eq.${bubbleId},"-Guest User".eq.${bubbleId}`)
   355→      .order('"Modified Date"', { ascending: false });
   356→
   357→    if (threadsError) {
   358→      throw new Error(`Failed to fetch threads: ${threadsError.message}`);
   359→    }
   360→
   361→    if (!threads || threads.length === 0) {
   362→      setThreads([]);
   363→      return;
   364→    }
   365→
   366→    // Step 2: Collect contact IDs and listing IDs for batch lookup
   367→    const contactIds = new Set();
   368→    const listingIds = new Set();
   369→
   370→    threads.forEach(thread => {
   371→      const hostId = thread['-Host User'];
   372→      const guestId = thread['-Guest User'];
   373→      const contactId = hostId === bubbleId ? guestId : hostId;
   374→      if (contactId) contactIds.add(contactId);
   375→      if (thread['Listing']) listingIds.add(thread['Listing']);
   376→    });
   377→
   378→    // Step 3: Batch fetch contact user data
   379→    let contactMap = {};
   380→    if (contactIds.size > 0) {
   381→      const { data: contacts } = await supabase
   382→        .from('user')
   383→        .select('_id, "Name - First", "Name - Last", "Profile Photo"')
   384→        .in('_id', Array.from(contactIds));
   385→
   386→      if (contacts) {
   387→        contactMap = contacts.reduce((acc, contact) => {
   388→          acc[contact._id] = {
   389→            name: `${contact['Name - First'] || ''} ${contact['Name - Last'] || ''}`.trim() || 'Unknown User',
   390→            avatar: contact['Profile Photo'],
   391→          };
   392→          return acc;
   393→        }, {});
   394→      }
   395→    }
   396→
   397→    // Step 4: Batch fetch listing data
   398→    let listingMap = {};
   399→    if (listingIds.size > 0) {
   400→      const { data: listings } = await supabase
   401→        .from('listing')
   402→        .select('_id, Name')
   403→        .in('_id', Array.from(listingIds));
   404→
   405→      if (listings) {
   406→        listingMap = listings.reduce((acc, listing) => {
   407→          acc[listing._id] = listing.Name || 'Unnamed Property';
   408→          return acc;
   409→        }, {});
   410→      }
   411→    }
   412→
   413→    // Step 5: Transform threads to UI format
   414→    const transformedThreads = threads.map(thread => {
   415→      const hostId = thread['-Host User'];
   416→      const guestId = thread['-Guest User'];
   417→      const contactId = hostId === bubbleId ? guestId : hostId;
   418→      const contact = contactId ? contactMap[contactId] : null;
   419→
   420→      // Format the last modified time
   421→      const modifiedDate = thread['Modified Date'] ? new Date(thread['Modified Date']) : new Date();
   422→      const now = new Date();
   423→      const diffMs = now.getTime() - modifiedDate.getTime();
   424→      const diffDays = Math.floor(diffMs / (1000 * 60 * 60 * 24));
   425→
   426→      let lastMessageTime;
   427→      if (diffDays === 0) {
   428→        lastMessageTime = modifiedDate.toLocaleTimeString('en-US', { hour: 'numeric', minute: '2-digit' });
   429→      } else if (diffDays === 1) {
   430→        lastMessageTime = 'Yesterday';
   431→      } else if (diffDays < 7) {
   432→        lastMessageTime = modifiedDate.toLocaleDateString('en-US', { weekday: 'short' });
   433→      } else {
   434→        lastMessageTime = modifiedDate.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
   435→      }
   436→
   437→      return {
   438→        _id: thread._id,
   439→        contact_name: contact?.name || 'Split Lease',
   440→        contact_avatar: contact?.avatar,
   441→        property_name: thread['Listing'] ? listingMap[thread['Listing']] : undefined,
   442→        last_message_preview: thread['~Last Message'] || 'No messages yet',
   443→        last_message_time: lastMessageTime,
   444→        unread_count: 0, // TODO: Implement unread count if needed
   445→        is_with_splitbot: false,
   446→      };
   447→    });
   448→
   449→    setThreads(transformedThreads);
   450→  }
   451→
   452→  /**
   453→   * Fetch messages for a specific thread
   454→   * Uses supabase.functions.invoke() for automatic token refresh
   455→   */
   456→  async function fetchMessages(threadId) {
   457→    try {
   458→      setIsLoadingMessages(true);
   459→
   460→      // Use supabase.functions.invoke() instead of raw fetch()
   461→      // This ensures automatic token refresh for stale sessions
   462→      const { data, error } = await supabase.functions.invoke('messages', {
   463→        body: {
   464→          action: 'get_messages',
   465→          payload: { thread_id: threadId }
   466→        }
   467→      });
   468→
   469→      if (error) {
   470→        throw new Error(error.message || 'Failed to fetch messages');
   471→      }
   472→
   473→      if (data?.success) {
   474→        setMessages(data.data.messages || []);
   475→        setThreadInfo(data.data.thread_info || null);
   476→      } else {
   477→        throw new Error(data?.error || 'Failed to fetch messages');
   478→      }
   479→    } catch (err) {
   480→      console.error('Error fetching messages:', err);
   481→      // Don't set global error, just log it - user can still see threads
   482→    } finally {
   483→      setIsLoadingMessages(false);
   484→    }
   485→  }
   486→
   487→  /**
   488→   * Send a new message
   489→   * After sending, Realtime will deliver the message to all subscribers
   490→   * Uses supabase.functions.invoke() for automatic token refresh
   491→   */
   492→  async function sendMessage() {
   493→    if (!messageInput.trim() || !selectedThread || isSending) return;
   494→
   495→    try {
   496→      setIsSending(true);
   497→
   498→      // Clear typing indicator immediately
   499→      trackTyping(false);
   500→      if (typingTimeoutRef.current) {
   501→        clearTimeout(typingTimeoutRef.current);
   502→      }
   503→
   504→      // Use supabase.functions.invoke() instead of raw fetch()
   505→      // This ensures automatic token refresh for stale sessions
   506→      const { data, error } = await supabase.functions.invoke('messages', {
   507→        body: {
   508→          action: 'send_message',
   509→          payload: {
   510→            thread_id: selectedThread._id,
   511→            message_body: messageInput.trim(),
   512→          },
   513→        }
   514→      });
   515→
   516→      if (error) {
   517→        throw new Error(error.message || 'Failed to send message');
   518→      }
   519→
   520→      if (data?.success) {
   521→        // Clear input immediately
   522→        setMessageInput('');
   523→
   524→        // Note: We no longer need to fetch messages or threads here!
   525→        // The Realtime subscription will automatically add the new message
   526→        // when the database trigger broadcasts it.
   527→
   528→        // However, for the sender's immediate feedback, add optimistically
   529→        // (the Realtime broadcast might also arrive, but we'll dedupe)
   530→        const optimisticMessage = {
   531→          _id: data.data.message_id,
   532→          message_body: messageInput.trim(),
   533→          sender_name: 'You',
   534→          sender_type: 'guest', // Will be corrected by Realtime if wrong
   535→          is_outgoing: true,
   536→          timestamp: new Date().toLocaleString('en-US', {
   537→            month: 'short', day: 'numeric', hour: 'numeric', minute: '2-digit'
   538→          }),
   539→        };
   540→
   541→        setMessages(prev => {
   542→          // Avoid duplicates
   543→          if (prev.some(m => m._id === optimisticMessage._id)) return prev;
   544→          return [...prev, optimisticMessage];
   545→        });
   546→
   547→      } else {
   548→        throw new Error(data?.error || 'Failed to send message');
   549→      }
   550→    } catch (err) {
   551→      console.error('Error sending message:', err);
   552→      setError(err.message || 'Failed to send message');
   553→    } finally {
   554→      setIsSending(false);
   555→    }
   556→  }
   557→
   558→  // ============================================================================
   559→  // HANDLERS
   560→  // ============================================================================
   561→
   562→  /**
   563→   * Internal thread selection (does not update URL, used by effect)
   564→   */
   565→  function handleThreadSelectInternal(thread) {
   566→    setSelectedThread(thread);
   567→    setMessages([]);
   568→    setThreadInfo(null);
   569→    setIsOtherUserTyping(false);
   570→    setTypingUserName(null);
   571→    fetchMessages(thread._id);
   572→  }
   573→
   574→  /**
   575→   * Handle thread selection from user interaction
   576→   */
   577→  const handleThreadSelect = useCallback((thread) => {
   578→    setSelectedThread(thread);
   579→    setMessages([]);
   580→    setThreadInfo(null);
   581→    setIsOtherUserTyping(false);
   582→    setTypingUserName(null);
   583→
   584→    // Update URL
   585→    const params = new URLSearchParams(window.location.search);
   586→    params.set('thread', thread._id);
   587→    window.history.replaceState({}, '', `?${params.toString()}`);
   588→
   589→    // Fetch messages for selected thread
   590→    fetchMessages(thread._id);
   591→  }, []);
   592→
   593→  /**
   594→   * Handle message input change with typing indicator
   595→   */
   596→  const handleMessageInputChange = useCallback((value) => {
   597→    if (value.length <= 1000) {
   598→      setMessageInput(value);
   599→
   600→      // Track typing
   601→      trackTyping(true);
   602→
   603→      // Clear typing after 2 seconds of no input
   604→      if (typingTimeoutRef.current) {
   605→        clearTimeout(typingTimeoutRef.current);
   606→      }
   607→      typingTimeoutRef.current = setTimeout(() => {
   608→        trackTyping(false);
   609→      }, 2000);
   610→    }
   611→  }, [trackTyping]);
   612→
   613→  /**
   614→   * Handle send message
   615→   */
   616→  const handleSendMessage = useCallback(() => {
   617→    sendMessage();
   618→  }, [messageInput, selectedThread, isSending]);
   619→
   620→  /**
   621→   * Handle retry after error
   622→   */
   623→  const handleRetry = useCallback(() => {
   624→    setError(null);
   625→    setIsLoading(true);
   626→    fetchThreads();
   627→  }, []);
   628→
   629→  // ============================================================================
   630→  // RETURN HOOK API
   631→  // ============================================================================
   632→  return {
   633→    // Auth state
   634→    authState,
   635→    user,
   636→
   637→    // Thread data
   638→    threads,
   639→    selectedThread,
   640→    messages,
   641→    threadInfo,
   642→
   643→    // UI state
   644→    isLoading,
   645→    isLoadingMessages,
   646→    error,
   647→    messageInput,
   648→    isSending,
   649→
   650→    // Realtime state
   651→    isOtherUserTyping,
   652→    typingUserName,
   653→
   654→    // Handlers
   655→    handleThreadSelect,
   656→    handleMessageInputChange,
   657→    handleSendMessage,
   658→    handleRetry,
   659→  };
   660→}
   661→

