     1→/**
     2→ * Host Overview Page Logic Hook
     3→ *
     4→ * Contains all business logic for the Host Overview page:
     5→ * - Data fetching for listings, house manuals, virtual meetings
     6→ * - CRUD operations for listings and house manuals
     7→ * - Toast notifications
     8→ * - Modal state management
     9→ *
    10→ * Database Tables Used:
    11→ * - listing: Property listings owned by host
    12→ * - House manual: Documentation for guests
    13→ * - virtualmeetingschedulesandlinks: Scheduled virtual meetings
    14→ * - user: Current user data
    15→ */
    16→
    17→import { useState, useEffect, useCallback } from 'react';
    18→import { checkAuthStatus, validateTokenAndFetchUser, getFirstName, getAvatarUrl } from '../../../lib/auth.js';
    19→import { getUserId } from '../../../lib/secureStorage.js';
    20→import { supabase } from '../../../lib/supabase.js';
    21→import { initializeLookups, getBoroughName } from '../../../lib/dataLookups.js';
    22→
    23→/**
    24→ * Extract borough name from address string
    25→ * Handles formats like "276 Belmont Ave, Brooklyn, NY 11207, USA"
    26→ * @param {string|object} addressField - The Location - Address field (JSONB or string)
    27→ * @returns {string} The borough name or empty string
    28→ */
    29→function extractBoroughFromAddress(addressField) {
    30→  if (!addressField) return '';
    31→
    32→  // If it's a JSONB object, get the address string
    33→  const addressStr = typeof addressField === 'object' ? addressField.address : addressField;
    34→  if (!addressStr || typeof addressStr !== 'string') return '';
    35→
    36→  // NYC boroughs to look for in the address
    37→  const boroughPatterns = [
    38→    { pattern: /,\s*Brooklyn\s*,/i, name: 'Brooklyn' },
    39→    { pattern: /,\s*Manhattan\s*,/i, name: 'Manhattan' },
    40→    { pattern: /,\s*Queens\s*,/i, name: 'Queens' },
    41→    { pattern: /,\s*Bronx\s*,/i, name: 'Bronx' },
    42→    { pattern: /,\s*Staten Island\s*,/i, name: 'Staten Island' },
    43→    // "New York, NY" typically means Manhattan
    44→    { pattern: /,\s*New York\s*,\s*NY/i, name: 'Manhattan' },
    45→  ];
    46→
    47→  for (const { pattern, name } of boroughPatterns) {
    48→    if (pattern.test(addressStr)) {
    49→      return name;
    50→    }
    51→  }
    52→
    53→  return '';
    54→}
    55→
    56→export function useHostOverviewPageLogic() {
