   138→  const fetchHostListings = useCallback(async (hostAccountId, userId) => {
   139→    if (!hostAccountId && !userId) return [];
   140→
   141→    try {
   142→      // Initialize lookups cache (boroughs, neighborhoods, etc.)
   143→      await initializeLookups();
   144→
   145→      // Fetch listings from multiple sources in parallel:
   146→      // 1. Bubble API (existing synced listings)
   147→      // 2. listing table via RPC (self-listing submissions)
   148→      // 3. user.Listings array (linked listings)
   149→
   150→      const fetchPromises = [];
   151→
   152→      // 1. Try Bubble API (only if hostAccountId is available)
   153→      if (hostAccountId) {
   154→        fetchPromises.push(
   155→          supabase.functions.invoke('bubble-proxy', {
   156→            body: {
   157→              endpoint: 'listing',
   158→              method: 'GET',
   159→              params: {
   160→                constraints: JSON.stringify([
   161→                  { key: 'Creator', constraint_type: 'equals', value: hostAccountId },
   162→                  { key: 'Complete', constraint_type: 'equals', value: true }
   163→                ])
   164→              }
   165→            }
   166→          }).then(result => {
   167→            if (result.error) {
   168→              console.warn('Bubble listings fetch failed:', result.error);
   169→              return { type: 'bubble', data: { response: { results: [] } } };
   170→            }
   171→            return { type: 'bubble', ...result };
   172→          }).catch(err => {
   173→            console.warn('Bubble listings fetch failed:', err);
   174→            return { type: 'bubble', data: { response: { results: [] } } };
   175→          })
   176→        );
   177→      }
   178→
   179→      // 2. Fetch from listing table where Host User = userId or Created By = userId
   180→      // Using RPC function to handle column names with special characters
   181→      if (userId) {
   182→        fetchPromises.push(
   183→          supabase
   184→            .rpc('get_host_listings', { host_user_id: userId })
   185→            .then(result => {
   186→              console.log('[HostOverview] listing query result:', result);
   187→              return { type: 'listing_rpc', ...result };
   188→            })
   189→            .catch(err => {
   190→              console.warn('listing fetch failed:', err);
   191→              return { type: 'listing_rpc', data: [], error: err };
   192→            })
   193→        );
   194→      }
   195→
   196→      // 3. Fetch listing IDs from user.Listings array
   197→      if (userId) {
   198→        fetchPromises.push(
   199→          supabase
   200→            .from('user')
   201→            .select('Listings')
   202→            .eq('_id', userId)
   203→            .maybeSingle()
   204→            .then(result => ({ type: 'user_listings', ...result }))
   205→            .catch(err => {
   206→              console.warn('user Listings fetch failed:', err);
   207→              return { type: 'user_listings', data: null, error: err };
   208→            })
   209→        );
   210→      }
   211→
   212→      const results = await Promise.all(fetchPromises);
   213→
   214→      // Process Bubble listings
   215→      const bubbleResult = results.find(r => r?.type === 'bubble');
   216→      const bubbleListings = bubbleResult?.data?.response?.results || [];
   217→      const mappedBubbleListings = bubbleListings.map(listing => ({
   218→        id: listing._id,
   219→        _id: listing._id,
   220→        name: listing.Name || listing.name || 'Unnamed Listing',
   221→        Name: listing.Name,
   222→        complete: listing.Complete || listing.complete,
   223→        source: 'bubble',
   224→        location: {
   225→          borough: listing['Location - Borough']?.Display || listing.borough || ''
   226→        },
   227→        leasesCount: listing['Leases Count'] || 0,
   228→        proposalsCount: listing['Proposals Count'] || 0,
   229→        photos: listing['Features - Photos'] || [],
   230→        // Pricing fields
   231→        rental_type: listing['rental type'] || 'Nightly',
   232→        monthly_rate: listing['💰Monthly Host Rate'],
   233→        weekly_rate: listing['💰Weekly Host Rate'],
   234→        // Nightly rates for each night count
   235→        nightly_rate_2: listing['💰Nightly Host Rate for 2 nights'],
   236→        nightly_rate_3: listing['💰Nightly Host Rate for 3 nights'],
   237→        nightly_rate_4: listing['💰Nightly Host Rate for 4 nights'],
   238→        nightly_rate_5: listing['💰Nightly Host Rate for 5 nights'],
   239→        nightly_rate_7: listing['💰Nightly Host Rate for 7 nights'],
   240→        cleaning_fee: listing['💰Cleaning Cost / Maintenance Fee'],
   241→        damage_deposit: listing['💰Damage Deposit']
   242→      }));
   243→
   244→      // Process listings from RPC
   245→      let rpcListings = [];
   246→      const rpcResult = results.find(r => r?.type === 'listing_rpc');
   247→      if (rpcResult?.data && !rpcResult.error) {
   248→        rpcListings = rpcResult.data.map(listing => {
   249→          // Try FK lookup first, fall back to extracting from address string
   250→          const boroughFromFK = getBoroughName(listing['Location - Borough']);
   251→          const boroughFromAddress = extractBoroughFromAddress(listing['Location - Address']);
   252→          const borough = boroughFromFK || boroughFromAddress || '';
   253→
   254→          return {
   255→            id: listing.id,
   256→            _id: listing._id,
   257→            name: listing.Name || 'Unnamed Listing',
   258→            Name: listing.Name,
   259→            complete: listing.Complete || false,
   260→            source: listing.source || 'listing',
   261→              location: {
   262→              borough,
   263→              city: listing['Location - City'] || '',
   264→              state: listing['Location - State'] || ''
   265→            },
   266→            leasesCount: 0,
   267→            proposalsCount: 0,
   268→            photos: listing['Features - Photos'] || [],
   269→            // Pricing fields from RPC
   270→            rental_type: listing.rental_type,
   271→            monthly_rate: listing.monthly_rate,
   272→            weekly_rate: listing.weekly_rate,
   273→            // Individual nightly rates from RPC
   274→            nightly_rate_2: listing.rate_2_nights,
   275→            nightly_rate_3: listing.rate_3_nights,
   276→            nightly_rate_4: listing.rate_4_nights,
   277→            nightly_rate_5: listing.rate_5_nights,
   278→            nightly_rate_7: listing.rate_7_nights,
   279→            rate_5_nights: listing.rate_5_nights,
   280→            cleaning_fee: listing.cleaning_fee,
   281→            damage_deposit: listing.damage_deposit,
   282→            pricing_list: listing.pricing_list
   283→          };
   284→        });
   285→      }
   286→
   287→      // Check if we need to fetch additional listings from user.Listings
   288→      const userListingsResult = results.find(r => r?.type === 'user_listings');
   289→      const linkedListingIds = userListingsResult?.data?.Listings || [];
   290→
   291→      // Fetch any linked listings that aren't already in our results
   292→      const existingIds = new Set([
   293→        ...mappedBubbleListings.map(l => l.id),
   294→        ...rpcListings.map(l => l.id)
   295→      ]);
   296→
   297→      const missingIds = linkedListingIds.filter(id => !existingIds.has(id));
   298→
   299→      if (missingIds.length > 0) {
   300→        // Fetch missing listings from listing table
   301→        const { data: missingListings } = await supabase
   302→          .from('listing')
   303→          .select('*')
   304→          .in('_id', missingIds)
   305→          .eq('Deleted', false);
   306→
   307→        if (missingListings) {
   308→          const mappedMissing = missingListings.map(listing => {
   309→            // Try FK lookup first, fall back to extracting from address string
   310→            const boroughFromFK = getBoroughName(listing['Location - Borough']);
   311→            const boroughFromAddress = extractBoroughFromAddress(listing['Location - Address']);
   312→            const borough = boroughFromFK || boroughFromAddress || '';
   313→
   314→            return {
   315→              id: listing._id,
   316→              _id: listing._id,
   317→              name: listing.Name || 'Unnamed Listing',
   318→              Name: listing.Name,
   319→              complete: listing.Complete || false,
   320→              source: 'listing',
   321→              location: {
   322→                borough,
   323→                city: listing['Location - City'] || '',
   324→                state: listing['Location - State'] || ''
   325→              },
   326→              leasesCount: 0,
   327→              proposalsCount: 0,
   328→              photos: listing['Features - Photos'] || [],
   329→              // Pricing fields (using original column names from direct query)
   330→              rental_type: listing['rental type'],
   331→              monthly_rate: listing['💰Monthly Host Rate'],
   332→              weekly_rate: listing['💰Weekly Host Rate'],
   333→              // Individual nightly rates
   334→              nightly_rate_2: listing['💰Nightly Host Rate for 2 nights'],
   335→              nightly_rate_3: listing['💰Nightly Host Rate for 3 nights'],
   336→              nightly_rate_4: listing['💰Nightly Host Rate for 4 nights'],
   337→              nightly_rate_5: listing['💰Nightly Host Rate for 5 nights'],
   338→              nightly_rate_7: listing['💰Nightly Host Rate for 7 nights'],
   339→              rate_5_nights: listing['💰Nightly Host Rate for 5 nights'],
   340→              cleaning_fee: listing['💰Cleaning Cost / Maintenance Fee'],
   341→              damage_deposit: listing['💰Damage Deposit'],
   342→              pricing_list: listing.pricing_list
   343→            };
   344→          });
   345→          rpcListings = [...rpcListings, ...mappedMissing];
   346→        }
   347→      }
   348→
   349→      // Combine all listings, deduplicated by id
   350→      const allListings = [...mappedBubbleListings, ...rpcListings];
   351→      const uniqueListings = allListings.filter((listing, index, self) =>
   352→        index === self.findIndex(l => l.id === listing.id)
   353→      );
   354→
   355→      console.log('[HostOverview] Fetched listings:', {
   356→        bubble: mappedBubbleListings.length,
   357→        rpc: rpcListings.length,
   358→        total: uniqueListings.length
   359→      });
   360→
   361→      // Fetch proposal and lease counts for all listings
   362→      if (uniqueListings.length > 0) {
   363→        const listingIds = uniqueListings.map(l => l.id);
   364→
   365→        // Fetch proposals grouped by listing
   366→        const { data: proposalData } = await supabase
   367→          .from('proposal')
   368→          .select('Listing')
   369→          .in('Listing', listingIds);
   370→
   371→        // Fetch leases grouped by listing
   372→        const { data: leaseData } = await supabase
   373→          .from('bookings_leases')
   374→          .select('Listing')
   375→          .in('Listing', listingIds);
   376→
   377→        // Count proposals per listing
   378→        const proposalCounts = {};
   379→        (proposalData || []).forEach(p => {
   380→          if (p.Listing) {
   381→            proposalCounts[p.Listing] = (proposalCounts[p.Listing] || 0) + 1;
   382→          }
   383→        });
   384→
   385→        // Count leases per listing
   386→        const leaseCounts = {};
   387→        (leaseData || []).forEach(l => {
   388→          if (l.Listing) {
   389→            leaseCounts[l.Listing] = (leaseCounts[l.Listing] || 0) + 1;
   390→          }
   391→        });
   392→
   393→        // Merge counts into listings
   394→        uniqueListings.forEach(listing => {
   395→          listing.proposalsCount = proposalCounts[listing.id] || 0;
   396→          listing.leasesCount = leaseCounts[listing.id] || 0;
   397→        });
   398→
   399→        console.log('[HostOverview] Proposal counts:', proposalCounts);
   400→        console.log('[HostOverview] Lease counts:', leaseCounts);
   401→      }
   402→
   403→      return uniqueListings;
   404→    } catch (err) {
   405→      console.error('Error fetching host listings:', err);
   406→      return [];
   407→    }
   408→  }, []);
