   908→  // Update listing in database and local state
   909→  // NOTE: This is defined here (before handleStartAIGeneration) to avoid temporal dead zone issues
   910→  const updateListing = useCallback(async (listingId, updates) => {
   911→    console.log('📝 Updating listing:', listingId, updates);
   912→
   913→    // Map UI field names to database column names (handles quirky column names with leading spaces)
   914→    const fieldMapping = {
   915→      'First Available': ' First Available', // DB column has leading space
   916→    };
   917→
   918→    // Transform updates to use correct database column names
   919→    const dbUpdates = {};
   920→    for (const [key, value] of Object.entries(updates)) {
   921→      const dbColumnName = fieldMapping[key] || key;
   922→      dbUpdates[dbColumnName] = value;
   923→    }
   924→
   925→    console.log(`📋 Updating listing table with _id=${listingId}`);
   926→    console.log('📋 DB updates:', dbUpdates);
   927→
   928→    // Perform the update without .select() to avoid 409 conflicts from PostgREST
   929→    // The .select().maybeSingle() pattern can fail when:
   930→    // 1. Multiple concurrent updates occur (React StrictMode double-mounting)
   931→    // 2. PostgREST has timing issues between UPDATE and SELECT
   932→    const { error: updateError } = await supabase
   933→      .from('listing')
   934→      .update(dbUpdates)
   935→      .eq('_id', listingId);
   936→
   937→    if (updateError) {
   938→      console.error('❌ Error updating listing:', updateError);
   939→      console.error('❌ Error code:', updateError.code);
   940→      console.error('❌ Error message:', updateError.message);
   941→      console.error('❌ Error details:', updateError.details);
   942→      console.error('❌ Error hint:', updateError.hint);
   943→      console.error('❌ Full error object:', JSON.stringify(updateError, null, 2));
   944→      throw updateError;
   945→    }
   946→
   947→    // Fetch the updated row separately for reliable data retrieval
   948→    const { data, error: fetchError } = await supabase
   949→      .from('listing')
   950→      .select('*')
   951→      .eq('_id', listingId)
   952→      .maybeSingle();
   953→
   954→    if (fetchError) {
   955→      console.warn('⚠️ Update succeeded but failed to fetch updated data:', fetchError);
   956→      // Return partial data - the update still succeeded
   957→      return { _id: listingId, ...dbUpdates };
   958→    }
   959→
   960→    console.log('✅ Listing updated:', data);
   961→    return data;
   962→  }, []);
