     1→/**
     2→ * Price Calculation Utilities
     3→ * Handles all pricing logic for view-split-lease page
     4→ *
     5→ * Usage:
     6→ *   import { calculate4WeekRent, calculateReservationTotal } from './priceCalculations.js';
     7→ */
     8→
     9→/**
    10→ * Calculate 4-week rent based on nightly price and selected nights
    11→ * Formula: nightly price × nights per week × 4 weeks
    12→ * @param {number} nightlyPrice - Price per night
    13→ * @param {number} nightsPerWeek - Number of nights selected per week
    14→ * @returns {number} 4-week rent amount
    15→ */
    16→export function calculate4WeekRent(nightlyPrice, nightsPerWeek) {
    17→  if (!nightlyPrice || !nightsPerWeek) return 0;
    18→  return nightlyPrice * nightsPerWeek * 4;
    19→}
    20→
    21→/**
    22→ * Calculate estimated reservation total
    23→ * Formula: 4-week rent × (total weeks / 4)
    24→ * @param {number} fourWeekRent - 4-week rent amount
    25→ * @param {number} totalWeeks - Total reservation span in weeks
    26→ * @returns {number} Estimated total cost
    27→ */
    28→export function calculateReservationTotal(fourWeekRent, totalWeeks) {
    29→  if (!fourWeekRent || !totalWeeks) return 0;
    30→  return fourWeekRent * (totalWeeks / 4);
    31→}
    32→
    33→/**
    34→ * Get nightly price based on number of nights selected
    35→ * Matches Bubble logic for price field selection
    36→ * @param {object} listing - Listing object with price fields
    37→ * @param {number} nightsSelected - Number of nights per week (2-7)
    38→ * @returns {number|null} Nightly price
    39→ */
    40→export function getNightlyPriceForNights(listing, nightsSelected) {
    41→  if (!listing || !nightsSelected) return null;
    42→
    43→  // Price override takes precedence
    44→  if (listing['💰Price Override']) {
    45→    return listing['💰Price Override'];
    46→  }
    47→
    48→  // Map nights to price fields
    49→  const priceFieldMap = {
    50→    1: '💰Nightly Host Rate for 1 night',
    51→    2: '💰Nightly Host Rate for 2 nights',
    52→    3: '💰Nightly Host Rate for 3 nights',
    53→    4: '💰Nightly Host Rate for 4 nights',
    54→    5: '💰Nightly Host Rate for 5 nights',
    55→    7: '💰Nightly Host Rate for 7 nights'
    56→  };
    57→
    58→  const fieldName = priceFieldMap[nightsSelected];
    59→  if (fieldName && listing[fieldName]) {
    60→    return listing[fieldName];
    61→  }
    62→
    63→  // Default to 4-night rate if available
    64→  return listing['💰Nightly Host Rate for 4 nights'] || null;
    65→}
    66→
    67→/**
    68→ * Format price as currency string
    69→ * @param {number} amount - Dollar amount
    70→ * @param {boolean} showCents - Whether to show cents (default: false)
    71→ * @returns {string} Formatted price (e.g., "$1,234" or "$1,234.56")
    72→ */
    73→export function formatPrice(amount, showCents = false) {
    74→  if (amount === null || amount === undefined || isNaN(amount)) {
    75→    return '$0';
    76→  }
    77→
    78→  const formatted = new Intl.NumberFormat('en-US', {
    79→    style: 'currency',
    80→    currency: 'USD',
    81→    minimumFractionDigits: showCents ? 2 : 0,
    82→    maximumFractionDigits: showCents ? 2 : 0
    83→  }).format(amount);
    84→
    85→  return formatted;
    86→}
    87→
    88→/**
    89→ * Calculate complete pricing breakdown
    90→ * @param {object} listing - Listing object
    91→ * @param {number} nightsPerWeek - Nights selected per week
    92→ * @param {number} reservationWeeks - Total reservation span in weeks
    93→ * @returns {object} Complete pricing breakdown
    94→ */
    95→export function calculatePricingBreakdown(listing, nightsPerWeek, reservationWeeks) {
    96→  const nightlyPrice = getNightlyPriceForNights(listing, nightsPerWeek);
    97→
    98→  if (!nightlyPrice) {
    99→    return {
   100→      nightlyPrice: null,
   101→      fourWeekRent: null,
   102→      reservationTotal: null,
   103→      cleaningFee: listing['💰Cleaning Cost / Maintenance Fee'] || 0,
   104→      damageDeposit: listing['💰Damage Deposit'] || 0,
   105→      valid: false
   106→    };
   107→  }
   108→
   109→  const fourWeekRent = calculate4WeekRent(nightlyPrice, nightsPerWeek);
   110→  const reservationTotal = calculateReservationTotal(fourWeekRent, reservationWeeks);
   111→
   112→  return {
   113→    nightlyPrice,
   114→    fourWeekRent,
   115→    reservationTotal,
   116→    cleaningFee: listing['💰Cleaning Cost / Maintenance Fee'] || 0,
   117→    damageDeposit: listing['💰Damage Deposit'] || 0,
   118→    grandTotal: reservationTotal + (listing['💰Cleaning Cost / Maintenance Fee'] || 0),
   119→    valid: true
   120→  };
   121→}
   122→
   123→/**
   124→ * Validate if enough days are selected for price calculation
   125→ * @param {number} daysSelected - Number of days selected
   126→ * @returns {boolean} True if valid for pricing
   127→ */
   128→export function isValidForPricing(daysSelected) {
   129→  return daysSelected >= 2 && daysSelected <= 7;
   130→}
   131→
   132→/**
   133→ * Get price display message based on selection state
   134→ * @param {number|null} daysSelected - Number of days selected
   135→ * @returns {string} Display message
   136→ */
   137→export function getPriceDisplayMessage(daysSelected) {
   138→  if (!daysSelected || daysSelected < 2) {
   139→    return 'Please Select More Days';
   140→  }
   141→  if (daysSelected > 7) {
   142→    return 'Please Select 7 Days or Less';
   143→  }
   144→  return null; // Valid selection
   145→}
