   458→/**
   459→ * Login user via Supabase Edge Function (auth-user)
   460→ * Stores token and user_id in localStorage on success
   461→ *
   462→ * ✅ MIGRATED: Now uses Edge Functions instead of direct Bubble API calls
   463→ * API key is stored server-side in Supabase Secrets
   464→ *
   465→ * @param {string} email - User email
   466→ * @param {string} password - User password
   467→ * @returns {Promise<Object>} Response object with status, token, user_id, or error
   468→ */
   469→export async function loginUser(email, password) {
   470→  console.log('🔐 Attempting login via Edge Function for:', email);
   471→
   472→  try {
   473→    // CRITICAL: Use direct fetch to bypass Supabase client session handling
   474→    // The Supabase client's functions.invoke can hang indefinitely when there's a stale
   475→    // session because it tries to refresh the token before making requests. Since auth-user
   476→    // has verify_jwt=false, we don't need any auth token. Using direct fetch bypasses this.
   477→    console.log('🔄 Using direct fetch to bypass Supabase client session handling...');
   478→
   479→    // Clear localStorage directly to avoid signOut() hanging
   480→    try {
   481→      const storageKey = `sb-${import.meta.env.VITE_SUPABASE_URL?.split('//')[1]?.split('.')[0]}-auth-token`;
   482→      if (storageKey && storageKey !== 'sb-undefined-auth-token') {
   483→        localStorage.removeItem(storageKey);
   484→        console.log('✅ Cleared auth token from localStorage');
   485→      }
   486→    } catch (clearErr) {
   487→      console.warn('⚠️ Could not clear localStorage:', clearErr);
   488→    }
   489→
   490→    const supabaseUrl = import.meta.env.VITE_SUPABASE_URL;
   491→    const supabaseAnonKey = import.meta.env.VITE_SUPABASE_ANON_KEY;
   492→
   493→    console.log('📡 Calling auth-user edge function via direct fetch...');
   494→    const response = await fetch(`${supabaseUrl}/functions/v1/auth-user`, {
   495→      method: 'POST',
   496→      headers: {
   497→        'Content-Type': 'application/json',
   498→        'apikey': supabaseAnonKey,
   499→        'Authorization': `Bearer ${supabaseAnonKey}` // Use anon key, not user token
   500→      },
   501→      body: JSON.stringify({
   502→        action: 'login',
   503→        payload: {
   504→          email,
   505→          password
   506→        }
   507→      })
   508→    });
   509→
   510→    const data = await response.json();
   511→    console.log('📡 Response status:', response.status);
   512→    console.log('📡 Response data:', data);
   513→
   514→    // Handle HTTP errors
   515→    if (!response.ok) {
   516→      console.error('❌ Edge Function HTTP error:', response.status);
   517→      const errorMessage = data?.error || `HTTP ${response.status}: Failed to authenticate`;
   518→      return {
   519→        success: false,
   520→        error: errorMessage
   521→      };
   522→    }
   523→
   524→    if (!data.success) {
   525→      console.error('❌ Login failed:', data.error);
   526→      return {
   527→        success: false,
   528→        error: data.error || 'Login failed. Please try again.'
   529→      };
   530→    }
   531→
   532→    // Extract Supabase session data (login now returns same format as signup)
   533→    const {
   534→      access_token,
   535→      refresh_token,
   536→      expires_in,
   537→      user_id,
   538→      host_account_id,
   539→      guest_account_id,
   540→      supabase_user_id,
   541→      user_type
   542→    } = data.data;
   543→
   544→    // Set Supabase session using the client
   545→    // This stores the session in localStorage and enables authenticated requests
   546→    if (access_token && refresh_token) {
   547→      const { error: sessionError } = await supabase.auth.setSession({
   548→        access_token,
   549→        refresh_token
   550→      });
   551→
   552→      if (sessionError) {
   553→        console.error('❌ Failed to set Supabase session:', sessionError.message);
   554→        // Continue anyway - tokens are still valid, just not in client state
   555→      } else {
   556→        console.log('✅ Supabase session set successfully');
   557→      }
   558→
   559→      // CRITICAL: Verify the session is actually persisted before proceeding
   560→      // This fixes a race condition where the page can reload before localStorage is written
   561→      let verifyAttempts = 0;
   562→      const maxVerifyAttempts = 5;
   563→      while (verifyAttempts < maxVerifyAttempts) {
   564→        const { data: { session: verifiedSession } } = await supabase.auth.getSession();
   565→        if (verifiedSession && verifiedSession.access_token === access_token) {
   566→          console.log('✅ Session verified and persisted');
   567→          break;
   568→        }
   569→        verifyAttempts++;
   570→        console.log(`⏳ Waiting for session to persist (attempt ${verifyAttempts}/${maxVerifyAttempts})...`);
   571→        await new Promise(resolve => setTimeout(resolve, 100)); // Wait 100ms
   572→      }
   573→
   574→      if (verifyAttempts >= maxVerifyAttempts) {
   575→        console.warn('⚠️ Session may not be fully persisted, but continuing...');
   576→      }
   577→    }
   578→
   579→    // Store access_token as auth token for backward compatibility
   580→    setAuthToken(access_token);
   581→    setSessionId(user_id);
   582→
   583→    // Set auth state with user ID (public, non-sensitive)
   584→    setAuthState(true, user_id);
   585→
   586→    // Store user type
