   589→    }
   590→
   591→    // Update login state
   592→    isUserLoggedInState = true;
   593→
   594→    console.log('✅ Login successful (Supabase Auth)');
   595→    console.log('   User ID (_id):', user_id);
   596→    console.log('   Supabase Auth ID:', supabase_user_id);
   597→    console.log('   User Type:', user_type);
   598→    console.log('   Session expires in:', expires_in, 'seconds');
   599→
   600→    // Store Supabase user ID for reference
   601→    if (supabase_user_id) {
   602→      localStorage.setItem('splitlease_supabase_user_id', supabase_user_id);
   603→    }
   604→
   605→    return {
   606→      success: true,
   607→      user_id: user_id,
   608→      host_account_id: host_account_id,
   609→      guest_account_id: guest_account_id,
   610→      supabase_user_id: supabase_user_id,
   611→      user_type: user_type,
   612→      expires_in: expires_in
   613→    };
   614→
   615→  } catch (error) {
   616→    console.error('❌ Login error:', error);
   617→    return {
   618→      success: false,
   619→      error: 'Network error. Please check your connection and try again.'
   620→    };
   621→  }
   622→}
