  1248→/**
  1249→ * Update password after clicking reset link
  1250→ * Must be called when user has active session from PASSWORD_RECOVERY event
  1251→ *
  1252→ * After successful password update, the user remains logged in with their
  1253→ * existing session synced to secure storage.
  1254→ *
  1255→ * Uses Edge Functions - API keys stored server-side
  1256→ *
  1257→ * @param {string} newPassword - New password to set
  1258→ * @returns {Promise<Object>} Response object with success status
  1259→ */
  1260→export async function updatePassword(newPassword) {
  1261→  console.log('🔐 Updating password...');
  1262→
  1263→  if (!newPassword) {
  1264→    return {
  1265→      success: false,
  1266→      error: 'New password is required.'
  1267→    };
  1268→  }
  1269→
  1270→  if (newPassword.length < 4) {
  1271→    return {
  1272→      success: false,
  1273→      error: 'Password must be at least 4 characters long.'
  1274→    };
  1275→  }
  1276→
  1277→  // Get current session (from PASSWORD_RECOVERY event)
  1278→  const { data: { session }, error: sessionError } = await supabase.auth.getSession();
  1279→
  1280→  if (sessionError || !session) {
  1281→    console.error('❌ No active session for password update');
  1282→    return {
  1283→      success: false,
  1284→      error: 'Invalid or expired reset link. Please request a new password reset.'
  1285→    };
  1286→  }
  1287→
  1288→  try {
  1289→    const { data, error } = await supabase.functions.invoke('auth-user', {
  1290→      body: {
  1291→        action: 'update_password',
  1292→        payload: {
  1293→          password: newPassword,
  1294→          access_token: session.access_token
  1295→        }
  1296→      }
  1297→    });
  1298→
  1299→    if (error) {
  1300→      console.error('❌ Password update failed:', error);
  1301→
  1302→      // Extract detailed error from response body if available
  1303→      let errorMessage = 'Failed to update password. Please try again.';
  1304→
  1305→      // In newer Supabase JS versions, error.context is the Response object
  1306→      if (error.context && typeof error.context.json === 'function') {
  1307→        try {
  1308→          const errorBody = await error.context.json();
  1309→          console.error('   Error body from Response:', errorBody);
  1310→          if (errorBody?.error) {
  1311→            errorMessage = errorBody.error;
  1312→          }
  1313→        } catch (parseErr) {
  1314→          // Silent - use default message
  1315→        }
  1316→      } else if (error.context?.body) {
  1317→        // Fallback for older Supabase JS versions
  1318→        try {
  1319→          const errorBody = typeof error.context.body === 'string'
  1320→            ? JSON.parse(error.context.body)
  1321→            : error.context.body;
  1322→          if (errorBody?.error) {
  1323→            errorMessage = errorBody.error;
  1324→          }
  1325→        } catch (parseErr) {
  1326→          // Silent - use default message
  1327→        }
  1328→      }
  1329→
  1330→      return {
  1331→        success: false,
  1332→        error: errorMessage
  1333→      };
  1334→    }
  1335→
  1336→    if (!data.success && data.error) {
  1337→      return {
  1338→        success: false,
  1339→        error: data.error
  1340→      };
  1341→    }
  1342→
  1343→    console.log('✅ Password updated successfully');
  1344→
  1345→    // Keep user logged in by syncing the Supabase session to secure storage
  1346→    // The user has proven account ownership by accessing their email
  1347→    const userId = session.user?.user_metadata?.user_id || session.user?.id;
  1348→    const userType = session.user?.user_metadata?.user_type;
  1349→
  1350→    // Store session tokens
  1351→    setSecureAuthToken(session.access_token);
  1352→    if (userId) {
  1353→      setSecureSessionId(userId);
  1354→      setAuthState(true, userId);
  1355→    }
  1356→    if (userType) {
  1357→      setSecureUserType(userType);
  1358→    }
  1359→
  1360→    // Update login state
  1361→    isUserLoggedInState = true;
  1362→
  1363→    console.log('✅ User session preserved after password update');
  1364→    console.log('   User ID:', userId);
  1365→    console.log('   User Type:', userType);
  1366→
  1367→    return {
  1368→      success: true,
  1369→      message: data?.data?.message || 'Password updated successfully.'
  1370→    };
  1371→
  1372→  } catch (error) {
  1373→    console.error('❌ Password update error:', error);
  1374→    return {
  1375→      success: false,
  1376→      error: 'Network error. Please check your connection and try again.'
  1377→    };
