  1388→ *
  1389→ * @param {string} userType - 'Host' or 'Guest'
  1390→ * @returns {Promise<Object>} Result with success status or error
  1391→ */
  1392→export async function initiateLinkedInOAuth(userType) {
  1393→  console.log('[Auth] Initiating LinkedIn OAuth with userType:', userType);
  1394→
  1395→  // Store user type before redirect
  1396→  setLinkedInOAuthUserType(userType);
  1397→
  1398→  try {
  1399→    const { data, error } = await supabase.auth.signInWithOAuth({
  1400→      provider: 'linkedin_oidc',
  1401→      options: {
  1402→        redirectTo: `${window.location.origin}/account-profile`,
  1403→        scopes: 'openid profile email',
  1404→      }
  1405→    });
  1406→
  1407→    if (error) {
  1408→      clearLinkedInOAuthUserType();
  1409→      return { success: false, error: error.message };
  1410→    }
  1411→
  1412→    // OAuth redirect will happen automatically
  1413→    return { success: true, data };
  1414→  } catch (err) {
  1415→    clearLinkedInOAuthUserType();
  1416→    return { success: false, error: err.message };
  1417→  }
  1418→}
  1419→
  1420→/**
  1421→ * Handle LinkedIn OAuth callback after redirect
  1422→ * Creates user record if new user, or links accounts if existing
  1423→ *
  1424→ * @returns {Promise<Object>} Result with user data or error
  1425→ */
  1426→export async function handleLinkedInOAuthCallback() {
  1427→  console.log('[Auth] Handling LinkedIn OAuth callback');
  1428→
  1429→  // Get the session from OAuth callback
  1430→  const { data: { session }, error: sessionError } = await supabase.auth.getSession();
  1431→
  1432→  if (sessionError || !session) {
  1433→    clearLinkedInOAuthUserType();
  1434→    return {
  1435→      success: false,
  1436→      error: sessionError?.message || 'No session found after OAuth'
  1437→    };
  1438→  }
  1439→
  1440→  // Check if this is a fresh OAuth session (from LinkedIn)
  1441→  const user = session.user;
  1442→  const isLinkedInProvider = user?.app_metadata?.provider === 'linkedin_oidc';
  1443→
  1444→  if (!isLinkedInProvider) {
  1445→    return { success: false, error: 'Not a LinkedIn OAuth session' };
  1446→  }
  1447→
  1448→  // Retrieve stored user type
  1449→  const userType = getLinkedInOAuthUserType() || 'Guest';
  1450→
  1451→  // Extract LinkedIn data from user_metadata
  1452→  const linkedInData = {
  1453→    email: user.email,
  1454→    firstName: user.user_metadata?.given_name || user.user_metadata?.first_name || '',
  1455→    lastName: user.user_metadata?.family_name || user.user_metadata?.last_name || '',
  1456→    profilePhoto: user.user_metadata?.picture || user.user_metadata?.avatar_url || null,
  1457→    supabaseUserId: user.id,
  1458→  };
  1459→
  1460→  console.log('[Auth] LinkedIn data:', linkedInData);
  1461→  console.log('[Auth] Stored userType:', userType);
  1462→
  1463→  // Call Edge Function to create/link user record
  1464→  try {
  1465→    const response = await fetch(`${import.meta.env.VITE_SUPABASE_URL}/functions/v1/auth-user`, {
  1466→      method: 'POST',
  1467→      headers: {
  1468→        'Content-Type': 'application/json',
  1469→        'apikey': import.meta.env.VITE_SUPABASE_ANON_KEY,
  1470→        'Authorization': `Bearer ${session.access_token}`,
  1471→      },
  1472→      body: JSON.stringify({
  1473→        action: 'oauth_signup',
  1474→        payload: {
  1475→          ...linkedInData,
  1476→          userType,
  1477→          provider: 'linkedin_oidc',
  1478→          access_token: session.access_token,
  1479→          refresh_token: session.refresh_token,
  1480→        }
  1481→      })
  1482→    });
  1483→
  1484→    const data = await response.json();
  1485→
  1486→    if (!response.ok || !data.success) {
  1487→      clearLinkedInOAuthUserType();
  1488→      return {
  1489→        success: false,
  1490→        error: data.error || 'Failed to create user record',
  1491→        isDuplicate: data.isDuplicate || false,
  1492→        existingEmail: data.existingEmail || null,
  1493→      };
  1494→    }
  1495→
  1496→    // Clear OAuth storage keys
  1497→    clearLinkedInOAuthUserType();
  1498→
  1499→    // Store session data
  1500→    setAuthToken(session.access_token);
  1501→    setSessionId(data.data.user_id);
  1502→    setAuthState(true, data.data.user_id);
  1503→    if (userType) {
  1504→      setUserType(userType);
  1505→    }
  1506→
  1507→    return {
  1508→      success: true,
  1509→      data: data.data,
  1510→      isNewUser: data.data.isNewUser,
  1511→    };
  1512→
  1513→  } catch (err) {
  1514→    clearLinkedInOAuthUserType();
  1515→    return { success: false, error: err.message };
  1516→  }
  1517→}
  1518→
  1519→/**
  1520→ * Initiate LinkedIn OAuth login flow
  1521→ * Sets login flow flag and redirects to current page after OAuth
  1522→ *
  1523→ * @returns {Promise<Object>} Result with success status or error
  1524→ */
  1525→export async function initiateLinkedInOAuthLogin() {
  1526→  console.log('[Auth] Initiating LinkedIn OAuth Login');
  1527→
  1528→  // Clear any existing signup flow flags to prevent conflicts
  1529→  clearLinkedInOAuthUserType();
  1530→
  1531→  // Set login flow flag before redirect
  1532→  setLinkedInOAuthLoginFlow(true);
  1533→
  1534→  try {
  1535→    const { data, error } = await supabase.auth.signInWithOAuth({
  1536→      provider: 'linkedin_oidc',
  1537→      options: {
  1538→        // Stay on current page after OAuth (no redirect to profile)
  1539→        redirectTo: window.location.href,
  1540→        scopes: 'openid profile email',
  1541→      }
  1542→    });
  1543→
  1544→    if (error) {
  1545→      clearLinkedInOAuthLoginFlow();
  1546→      return { success: false, error: error.message };
  1547→    }
  1548→
  1549→    // OAuth redirect will happen automatically
  1550→    return { success: true, data };
  1551→  } catch (err) {
  1552→    clearLinkedInOAuthLoginFlow();
  1553→    return { success: false, error: err.message };
  1554→  }
  1555→}
  1556→
  1557→/**
  1558→ * Handle LinkedIn OAuth login callback
  1559→ * Verifies user exists in database, returns session data or error
  1560→ *
  1561→ * @returns {Promise<Object>} Result with user data or error (userNotFound: true if account doesn't exist)
  1562→ */
  1563→export async function handleLinkedInOAuthLoginCallback() {
  1564→  console.log('[Auth] Handling LinkedIn OAuth Login callback');
  1565→
  1566→  // Verify this is a login flow
  1567→  if (!getLinkedInOAuthLoginFlow()) {
  1568→    console.log('[Auth] Not a login flow callback, skipping');
  1569→    return { success: false, error: 'Not a login flow' };
  1570→  }
  1571→
