  1581→      };
  1582→    }
  1583→
  1584→    // Check if this is a fresh OAuth session (from LinkedIn)
  1585→    const user = session.user;
  1586→    const isLinkedInProvider = user?.app_metadata?.provider === 'linkedin_oidc';
  1587→
  1588→    if (!isLinkedInProvider) {
  1589→      clearLinkedInOAuthLoginFlow();
  1590→      return { success: false, error: 'Not a LinkedIn OAuth session' };
  1591→    }
  1592→
  1593→    // Extract LinkedIn data
  1594→    const email = user.email;
  1595→    const supabaseUserId = user.id;
  1596→
  1597→    console.log('[Auth] LinkedIn OAuth login data:', { email, supabaseUserId });
  1598→
  1599→    // Call Edge Function to verify user exists
  1600→    const response = await fetch(`${import.meta.env.VITE_SUPABASE_URL}/functions/v1/auth-user`, {
  1601→      method: 'POST',
  1602→      headers: {
  1603→        'Content-Type': 'application/json',
  1604→        'apikey': import.meta.env.VITE_SUPABASE_ANON_KEY,
  1605→        'Authorization': `Bearer ${session.access_token}`,
  1606→      },
  1607→      body: JSON.stringify({
  1608→        action: 'oauth_login',
  1609→        payload: {
  1610→          email,
  1611→          supabaseUserId,
  1612→          access_token: session.access_token,
  1613→          refresh_token: session.refresh_token,
  1614→        }
  1615→      })
  1616→    });
  1617→
  1618→    const data = await response.json();
