   366→/**
   367→ * PropertyCard - Individual listing card
   368→ */
   369→function PropertyCard({ listing, selectedDaysCount, onLocationClick, onOpenContactModal, onOpenInfoModal }) {
   370→  const [currentImageIndex, setCurrentImageIndex] = useState(0);
   371→  const [isFavorite, setIsFavorite] = useState(false);
   372→  const priceInfoTriggerRef = useRef(null);
   373→
   374→  const hasImages = listing.images && listing.images.length > 0;
   375→  const hasMultipleImages = listing.images && listing.images.length > 1;
   376→
   377→  // Format host name to show "FirstName L."
   378→  const formatHostName = (fullName) => {
   379→    if (!fullName || fullName === 'Host') return 'Host';
   380→
   381→    const nameParts = fullName.trim().split(/\s+/);
   382→    if (nameParts.length === 1) return nameParts[0];
   383→
   384→    const firstName = nameParts[0];
   385→    const lastInitial = nameParts[nameParts.length - 1].charAt(0).toUpperCase();
   386→
   387→    return `${firstName} ${lastInitial}.`;
   388→  };
   389→
   390→  const handlePrevImage = (e) => {
   391→    e.preventDefault();
   392→    e.stopPropagation();
   393→    if (!hasMultipleImages) return;
   394→    setCurrentImageIndex((prev) =>
   395→      prev === 0 ? listing.images.length - 1 : prev - 1
   396→    );
   397→  };
   398→
   399→  const handleNextImage = (e) => {
   400→    e.preventDefault();
   401→    e.stopPropagation();
   402→    if (!hasMultipleImages) return;
   403→    setCurrentImageIndex((prev) =>
   404→      prev === listing.images.length - 1 ? 0 : prev + 1
   405→    );
   406→  };
   407→
   408→  const handleFavoriteClick = (e) => {
   409→    e.preventDefault();
   410→    e.stopPropagation();
   411→    setIsFavorite(!isFavorite);
   412→  };
   413→
   414→  // Calculate dynamic price based on selected days
   415→  // Uses the same formula as priceCalculations.js for Nightly rental type
   416→  const calculateDynamicPrice = () => {
   417→    const nightsCount = Math.max(selectedDaysCount - 1, 1);
   418→
   419→    const priceFieldMap = {
   420→      2: 'Price 2 nights selected',
   421→      3: 'Price 3 nights selected',
   422→      4: 'Price 4 nights selected',
   423→      5: 'Price 5 nights selected',
   424→      6: 'Price 6 nights selected',
   425→      7: 'Price 7 nights selected'
   426→    };
   427→
   428→    // Get the host compensation rate for the selected nights
   429→    let nightlyHostRate = 0;
   430→    if (nightsCount >= 2 && nightsCount <= 7) {
   431→      const fieldName = priceFieldMap[nightsCount];
   432→      nightlyHostRate = listing[fieldName] || 0;
   433→    }
   434→
   435→    // Fallback to starting price if no specific rate found
   436→    if (!nightlyHostRate || nightlyHostRate === 0) {
   437→      nightlyHostRate = listing['Starting nightly price'] || listing.price?.starting || 0;
   438→    }
   439→
   440→    // Apply the same markup calculation as priceCalculations.js
   441→    // Step 1: Calculate base price (host rate × nights)
   442→    const basePrice = nightlyHostRate * nightsCount;
   443→
   444→    // Step 2: Apply full-time discount (only for 7 nights, 13% discount)
   445→    const fullTimeDiscount = nightsCount === 7 ? basePrice * 0.13 : 0;
   446→
   447→    // Step 3: Price after discounts
   448→    const priceAfterDiscounts = basePrice - fullTimeDiscount;
   449→
   450→    // Step 4: Apply site markup (17%)
   451→    const siteMarkup = priceAfterDiscounts * 0.17;
   452→
   453→    // Step 5: Calculate total price
   454→    const totalPrice = basePrice - fullTimeDiscount + siteMarkup;
   455→
   456→    // Step 6: Calculate price per night (guest-facing price)
   457→    const pricePerNight = totalPrice / nightsCount;
   458→
   459→    return pricePerNight;
   460→  };
   461→
   462→  const dynamicPrice = calculateDynamicPrice();
   463→  const startingPrice = listing['Starting nightly price'] || listing.price?.starting || 0;
   464→
   465→  // Render amenity icons
   466→  const renderAmenityIcons = () => {
   467→    if (!listing.amenities || listing.amenities.length === 0) return null;
   468→
   469→    const maxVisible = 6;
   470→    const visibleAmenities = listing.amenities.slice(0, maxVisible);
   471→    const hiddenCount = Math.max(0, listing.amenities.length - maxVisible);
   472→
   473→    return (
   474→      <div className="listing-amenities">
   475→        {visibleAmenities.map((amenity, idx) => (
   476→          <span key={idx} className="amenity-icon" data-tooltip={amenity.name}>
   477→            {amenity.icon}
   478→          </span>
   479→        ))}
   480→        {hiddenCount > 0 && (
   481→          <span className="amenity-more-count" title="Show all amenities">
   482→            +{hiddenCount} more
   483→          </span>
   484→        )}
   485→      </div>
   486→    );
   487→  };
   488→
   489→  const listingId = listing.id || listing._id;
   490→
   491→  return (
   492→    <a
   493→      className="listing-card"
   494→      href={listingId ? `/view-split-lease/${listingId}` : '#'}
   495→      target="_blank"
   496→      rel="noopener noreferrer"
   497→      style={{ textDecoration: 'none', color: 'inherit' }}
   498→      onClick={(e) => {
   499→        if (!listingId) {
   500→          e.preventDefault();
   501→          console.error('[PropertyCard] No listing ID found', { listing });
   502→        }
   503→      }}
   504→    >
   505→      {hasImages && (
   506→        <div className="listing-images">
   507→          <img
   508→            src={listing.images[currentImageIndex]}
   509→            alt={listing.title}
   510→          />
   511→          {hasMultipleImages && (
   512→            <>
   513→              <button className="image-nav prev-btn" onClick={handlePrevImage}>
   514→                ‹
   515→              </button>
   516→              <button className="image-nav next-btn" onClick={handleNextImage}>
   517→                ›
   518→              </button>
   519→              <div className="image-counter">
   520→                <span className="current-image">{currentImageIndex + 1}</span> /{' '}
   521→                <span className="total-images">{listing.images.length}</span>
   522→              </div>
   523→            </>
   524→          )}
   525→          <button className="favorite-btn" onClick={handleFavoriteClick}>
   526→            <svg
   527→              width="20"
   528→              height="20"
   529→              viewBox="0 0 24 24"
   530→              fill={isFavorite ? 'red' : 'none'}
   531→              stroke="currentColor"
   532→              strokeWidth="2"
   533→            >
   534→              <path d="M20.84 4.61a5.5 5.5 0 0 0-7.78 0L12 5.67l-1.06-1.06a5.5 5.5 0 0 0-7.78 7.78l1.06 1.06L12 21.23l7.78-7.78 1.06-1.06a5.5 5.5 0 0 0 0-7.78z" />
   535→            </svg>
   536→          </button>
   537→          {listing.isNew && <span className="new-badge">New Listing</span>}
   538→        </div>
   539→      )}
   540→
   541→      <div className="listing-content">
   542→        <div className="listing-info">
   543→          <div
   544→            className="listing-location"
   545→            onClick={(e) => {
   546→              e.preventDefault();
   547→              e.stopPropagation();
   548→              if (onLocationClick) {
   549→                onLocationClick(listing);
   550→              }
   551→            }}
   552→            style={{ cursor: onLocationClick ? 'pointer' : 'default' }}
   553→          >
   554→            <svg width="14" height="14" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
   555→              <path d="M21 10c0 7-9 13-9 13s-9-6-9-13a9 9 0 0 1 18 0z" />
   556→              <circle cx="12" cy="10" r="3" />
   557→            </svg>
   558→            <span className="location-text">{listing.location}</span>
   559→          </div>
   560→          <h3 className="listing-title">{listing.title}</h3>
   561→          <p className="listing-type">
   562→            {listing.type}
   563→            {listing.squareFeet ? ` (${listing.squareFeet} SQFT)` : ''} - {listing.maxGuests} guests max
   564→          </p>
   565→          {renderAmenityIcons()}
   566→          <p className="listing-details">{listing.description}</p>
   567→        </div>
   568→
   569→        <div className="listing-footer">
   570→          <div className="host-info">
   571→            {listing.host?.image && (
   572→              <img src={listing.host.image} alt={listing.host.name} className="host-avatar" />
   573→            )}
   574→            {!listing.host?.image && (
   575→              <div className="host-avatar-placeholder">?</div>
   576→            )}
   577→            <div className="host-details">
   578→              <span className="host-name">
   579→                {formatHostName(listing.host?.name)}
   580→                {listing.host.verified && <span className="verified-badge" title="Verified">✓</span>}
   581→              </span>
   582→              <button
   583→                className="message-btn"
   584→                onClick={(e) => {
   585→                  e.preventDefault();
   586→                  e.stopPropagation();
   587→                  onOpenContactModal(listing);
   588→                }}
   589→              >
   590→                Message
   591→              </button>
   592→            </div>
   593→          </div>
   594→
   595→          <div className="pricing-info">
   596→            <div
   597→              ref={priceInfoTriggerRef}
   598→              className="starting-price"
   599→              style={{ cursor: 'pointer', display: 'flex', alignItems: 'center', gap: '4px' }}
   600→              onClick={(e) => {
   601→                e.preventDefault();
   602→                e.stopPropagation();
   603→                onOpenInfoModal(listing, priceInfoTriggerRef);
   604→              }}
   605→            >
   606→              <span>Starting at ${parseFloat(startingPrice).toFixed(2)}/night</span>
   607→              <svg
   608→                viewBox="0 0 24 24"
   609→                width="14"
   610→                height="14"
   611→                style={{ color: '#3b82f6', fill: 'currentColor', cursor: 'pointer' }}
   612→              >
   613→                <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm1 15h-2v-2h2v2zm0-4h-2V7h2v6z" />
   614→              </svg>
   615→            </div>
   616→            <div className="full-price">${dynamicPrice.toFixed(2)}/night</div>
   617→            <div className="availability-text">Message Split Lease for Availability</div>
   618→          </div>
   619→        </div>
   620→      </div>
   621→    </a>
   622→  );
   623→}
