   625→/**
   626→ * ListingsGrid - Grid of property cards with lazy loading
   627→ */
   628→function ListingsGrid({ listings, selectedDaysCount, onLoadMore, hasMore, isLoading, onOpenContactModal, onOpenInfoModal, mapRef }) {
   629→  const sentinelRef = useRef(null);
   630→
   631→  useEffect(() => {
   632→    if (!sentinelRef.current || !hasMore || isLoading) return;
   633→
   634→    const observer = new IntersectionObserver(
   635→      (entries) => {
   636→        if (entries[0].isIntersecting) {
   637→          onLoadMore();
   638→        }
   639→      },
   640→      {
   641→        root: null,
   642→        rootMargin: '100px',
   643→        threshold: 0.1
   644→      }
   645→    );
   646→
   647→    observer.observe(sentinelRef.current);
   648→
   649→    return () => {
   650→      if (sentinelRef.current) {
   651→        observer.unobserve(sentinelRef.current);
   652→      }
   653→    };
   654→  }, [hasMore, isLoading, onLoadMore]);
   655→
   656→  return (
   657→    <div className="listings-container">
   658→      {listings.map((listing, index) => (
   659→        <PropertyCard
   660→          key={listing.id}
   661→          listing={listing}
   662→          selectedDaysCount={selectedDaysCount}
   663→          onLocationClick={(listing) => {
   664→            if (mapRef.current) {
   665→              mapRef.current.zoomToListing(listing.id);
   666→            }
   667→          }}
   668→          onOpenContactModal={onOpenContactModal}
   669→          onOpenInfoModal={onOpenInfoModal}
   670→        />
   671→      ))}
   672→
   673→      {hasMore && (
   674→        <div ref={sentinelRef} className="lazy-load-sentinel">
   675→          <div className="loading-more">
   676→            <div className="spinner"></div>
   677→            <span>Loading more listings...</span>
   678→          </div>
   679→        </div>
   680→      )}
   681→    </div>
   682→  );
   683→}
