     1→/**
     2→ * Date and Timezone Utility Functions
     3→ * Handles EST timezone conversions and date formatting
     4→ */
     5→
     6→import { format, addMonths, subMonths, getDaysInMonth, startOfMonth, getDay } from 'date-fns';
     7→import { toZonedTime, fromZonedTime } from 'date-fns-tz';
     8→
     9→export const EST_TIMEZONE = 'America/New_York';
    10→
    11→/**
    12→ * Convert a date to UTC from EST timezone
    13→ * @param {Date} estDate - Date in EST
    14→ * @returns {Date} - Date in UTC
    15→ */
    16→export const toUTC = (estDate) => {
    17→  return fromZonedTime(estDate, EST_TIMEZONE);
    18→};
    19→
    20→/**
    21→ * Convert a UTC date to EST timezone
    22→ * @param {Date} utcDate - Date in UTC
    23→ * @returns {Date} - Date in EST
    24→ */
    25→export const toEST = (utcDate) => {
    26→  return toZonedTime(utcDate, EST_TIMEZONE);
    27→};
    28→
    29→/**
    30→ * Format a date in EST timezone
    31→ * @param {Date} date - Date to format
    32→ * @param {string} formatString - Format string (default: 'MMM d, yyyy h:mm a')
    33→ * @returns {string} - Formatted date string with (EST) suffix
    34→ */
    35→export const formatTimeEST = (date, formatString = 'MMM d, yyyy h:mm a') => {
    36→  const estDate = toEST(date);
    37→  return format(estDate, formatString) + ' (EST)';
    38→};
    39→
    40→/**
    41→ * Format time only in EST
    42→ * @param {Date} date - Date to format
    43→ * @returns {string} - Formatted time string with (EST) suffix
    44→ */
    45→export const formatTimeOnlyEST = (date) => {
    46→  const estDate = toEST(date);
    47→  return format(estDate, 'h:mm a') + ' (EST)';
    48→};
    49→
    50→/**
    51→ * Format date only in EST
    52→ * @param {Date} date - Date to format
    53→ * @returns {string} - Formatted date string
    54→ */
    55→export const formatDateOnlyEST = (date) => {
    56→  const estDate = toEST(date);
    57→  return format(estDate, 'MMM d, yyyy');
    58→};
    59→
    60→/**
    61→ * Generate time slots for a given day
    62→ * @param {Date} date - Base date for time slots
    63→ * @param {number} startHour - Starting hour (24-hour format, default: 8)
    64→ * @param {number} endHour - Ending hour (24-hour format, default: 20)
    65→ * @param {number} interval - Interval in minutes (default: 30)
    66→ * @returns {Date[]} - Array of Date objects representing time slots
    67→ */
    68→export const generateTimeSlots = (date, startHour = 8, endHour = 20, interval = 30) => {
    69→  const slots = [];
    70→
    71→  for (let hour = startHour; hour < endHour; hour++) {
    72→    for (let minute = 0; minute < 60; minute += interval) {
    73→      const slotTime = new Date(date);
    74→      slotTime.setHours(hour, minute, 0, 0);
    75→      slots.push(slotTime);
    76→    }
    77→  }
    78→
    79→  return slots;
    80→};
    81→
    82→/**
    83→ * Generate calendar days for a month
    84→ * @param {Date} currentMonth - The month to generate days for
    85→ * @returns {(Date|null)[]} - Array of Date objects (or null for empty cells)
    86→ */
    87→export const generateCalendarDays = (currentMonth) => {
    88→  const daysInMonth = getDaysInMonth(currentMonth);
    89→  const firstDayOfMonth = startOfMonth(currentMonth);
    90→  const startingDayOfWeek = getDay(firstDayOfMonth);
    91→
    92→  const days = [];
    93→
    94→  // Add empty cells for days before month starts
    95→  for (let i = 0; i < startingDayOfWeek; i++) {
    96→    days.push(null);
    97→  }
    98→
    99→  // Add all days of the month
   100→  for (let day = 1; day <= daysInMonth; day++) {
   101→    days.push(new Date(currentMonth.getFullYear(), currentMonth.getMonth(), day));
   102→  }
   103→
   104→  return days;
   105→};
   106→
   107→/**
   108→ * Navigate to previous month
   109→ * @param {Date} currentMonth - Current month
   110→ * @returns {Date} - Previous month
   111→ */
   112→export const getPreviousMonth = (currentMonth) => {
   113→  return subMonths(currentMonth, 1);
   114→};
   115→
   116→/**
   117→ * Navigate to next month
   118→ * @param {Date} currentMonth - Current month
   119→ * @returns {Date} - Next month
   120→ */
   121→export const getNextMonth = (currentMonth) => {
   122→  return addMonths(currentMonth, 1);
   123→};
   124→
   125→/**
   126→ * Check if a date is in the past
   127→ * @param {Date} date - Date to check
   128→ * @returns {boolean} - True if date is in the past
   129→ */
   130→export const isPastDate = (date) => {
   131→  const now = new Date();
   132→  return date < now;
   133→};
   134→
   135→/**
   136→ * Check if two dates are the same (ignoring time)
   137→ * @param {Date} date1 - First date
   138→ * @param {Date} date2 - Second date
   139→ * @returns {boolean} - True if dates are the same day
   140→ */
   141→export const isSameDate = (date1, date2) => {
   142→  return (
   143→    date1.getFullYear() === date2.getFullYear() &&
   144→    date1.getMonth() === date2.getMonth() &&
   145→    date1.getDate() === date2.getDate()
   146→  );
   147→};
   148→
   149→/**
   150→ * Check if two dates and times are the same
   151→ * @param {Date} date1 - First date
   152→ * @param {Date} date2 - Second date
   153→ * @returns {boolean} - True if dates and times are the same
   154→ */
   155→export const isSameDateTime = (date1, date2) => {
   156→  return date1.getTime() === date2.getTime();
   157→};
   158→
   159→/**
   160→ * Convert Date to ISO string for API requests
   161→ * @param {Date} date - Date to convert
   162→ * @returns {string} - ISO string
   163→ */
   164→export const toISOString = (date) => {
   165→  return date.toISOString();
   166→};
   167→
   168→/**
   169→ * Parse ISO string to Date
   170→ * @param {string} isoString - ISO string to parse
   171→ * @returns {Date} - Parsed Date object
   172→ */
   173→export const fromISOString = (isoString) => {
   174→  return new Date(isoString);
   175→};
   176→
   177→/**
   178→ * Generate Google Calendar URL
   179→ * @param {Object} meeting - Virtual meeting details
   180→ * @param {Object} proposal - Proposal details
   181→ * @returns {string} - Google Calendar URL
   182→ */
   183→export const generateGoogleCalendarUrl = (meeting, proposal) => {
   184→  if (!meeting.bookedDate) return '';
   185→
   186→  const startDate = toEST(new Date(meeting.bookedDate));
   187→  const endDate = new Date(startDate);
   188→  endDate.setHours(endDate.getHours() + 1); // 1 hour meeting
   189→
   190→  const formatForGoogle = (date) => {
   191→    return format(date, "yyyyMMdd'T'HHmmss");
   192→  };
   193→
   194→  const title = encodeURIComponent(`Virtual Meeting - ${proposal.listing?.name || 'Split Lease Property'}`);
   195→  const guestName = proposal.guest?.firstName || proposal.guest?.name || 'Guest';
   196→  const details = encodeURIComponent(
   197→    `Virtual meeting with ${guestName}\n${meeting.googleMeetLink || meeting.meetingLink || ''}`
   198→  );
   199→  const dates = `${formatForGoogle(startDate)}/${formatForGoogle(endDate)}`;
