   171→function ListingsGrid({ listings, onLoadMore, hasMore, isLoading, onOpenContactModal, onOpenInfoModal, mapRef, onCardLeave, isLoggedIn, userId, favoritedListingIds, onToggleFavorite, onRequireAuth, showCreateProposalButton, onOpenCreateProposalModal, proposalsByListingId, selectedNightsCount }) {
   172→
   173→  const sentinelRef = useRef(null);
   174→
   175→  useEffect(() => {
   176→    if (!sentinelRef.current || !hasMore || isLoading) return;
   177→
   178→    const observer = new IntersectionObserver(
   179→      (entries) => {
   180→        if (entries[0].isIntersecting) {
   181→          onLoadMore();
   182→        }
   183→      },
   184→      {
   185→        root: null,
   186→        rootMargin: '100px',
