   215→ * Days of the week constant - Starting with Sunday (S, M, T, W, T, F, S)
   216→ * Uses 0-based indexing matching JavaScript Date.getDay()
   217→ */
   218→const DAYS_OF_WEEK = [
   219→  { id: '0', singleLetter: 'S', fullName: 'Sunday', index: 0 },
   220→  { id: '1', singleLetter: 'M', fullName: 'Monday', index: 1 },
   221→  { id: '2', singleLetter: 'T', fullName: 'Tuesday', index: 2 },
   222→  { id: '3', singleLetter: 'W', fullName: 'Wednesday', index: 3 },
   223→  { id: '4', singleLetter: 'T', fullName: 'Thursday', index: 4 },
   224→  { id: '5', singleLetter: 'F', fullName: 'Friday', index: 5 },
   225→  { id: '6', singleLetter: 'S', fullName: 'Saturday', index: 6 },
   226→];
   227→
   228→/**
   229→ * Get human-readable repeat pattern description
   230→ * @param {string} weekPattern - The week pattern key (e.g., 'every-week', 'one-on-off')
   231→ * @returns {string|null} The repeat description or null if every week
   232→ */
   233→const getRepeatPatternText = (weekPattern) => {
   234→  const patterns = {
   235→    'every-week': null, // No repeat text for every week
   236→    'one-on-off': 'Repeats 1 week on, 1 week off',
   237→    'two-on-off': 'Repeats 2 weeks on, 2 weeks off',
   238→    'one-three-off': 'Repeats 1 week on, 3 weeks off',
   239→  };
   240→  return patterns[weekPattern] || null;
   241→};
   242→
   243→// ============================================================================
   244→// COMPONENT
   245→// ============================================================================
   246→
   247→/**
   248→ * Get initial selection from URL parameter or default to Monday-Friday
   249→ * URL parameter format: ?days-selected=1,2,3,4,5 (0-based, where 0=Sunday)
   250→ * Internal format: [1,2,3,4,5] (0-based, where 0=Sunday)
   251→ */
   252→const getInitialSelectionFromUrl = () => {
   253→  // Try to get from URL parameter first
   254→  const urlParams = new URLSearchParams(window.location.search);
   255→  const daysParam = urlParams.get('days-selected');
   256→
   257→  if (daysParam) {
   258→    try {
   259→      // Parse 0-based indices from URL directly
   260→      const dayIndices = daysParam.split(',').map(d => parseInt(d.trim(), 10));
   261→      const validDays = dayIndices.filter(d => d >= 0 && d <= 6); // Validate 0-based range
   262→
   263→      if (validDays.length > 0) {
   264→        console.log('📅 SearchScheduleSelector: Loaded selection from URL:', {
   265→          urlParam: daysParam,
   266→          dayIndices: validDays
   267→        });
   268→        return validDays;
   269→      }
   270→    } catch (e) {
   271→      console.warn('⚠️ Failed to parse days-selected URL parameter:', e);
   272→    }
   273→  }
   274→
   275→  // Default to Monday-Friday (0-based: [1,2,3,4,5])
   276→  console.log('📅 SearchScheduleSelector: Using default Monday-Friday selection');
   277→  return [1, 2, 3, 4, 5];
   278→};
   279→
   280→/**
   281→ * SearchScheduleSelector Component
   282→ *
   283→ * A weekly schedule selector for split-lease arrangements.
   284→ * Allows users to select 2-5 contiguous nights per week.
   285→ *
   286→ * @param {Object} props - Component props
   287→ * @param {Function} [props.onSelectionChange] - Callback fired when the selection changes
   288→ * @param {Function} [props.onError] - Callback fired when a validation error occurs
   289→ * @param {string} [props.className] - Custom styling class name
   290→ * @param {number} [props.minDays=2] - Minimum number of days that can be selected
   291→ * @param {boolean} [props.requireContiguous=true] - Whether to require contiguous day selection
   292→ * @param {number[]} [props.initialSelection] - Initial selected days (array of day indices 0-6). If not provided, reads from URL or defaults to Monday-Friday
   293→ * @param {string} [props.weekPattern='every-week'] - The weekly pattern (e.g., 'every-week', 'one-on-off', 'two-on-off', 'one-three-off')
   294→ *
   295→ * @example
   296→ * ```jsx
   297→ * <SearchScheduleSelector
   298→ *   onSelectionChange={(days) => console.log(days)}
   299→ *   onError={(error) => console.error(error)}
   300→ * />
   301→ * ```
   302→ */
   303→export default function SearchScheduleSelector({
   304→  onSelectionChange,
   305→  onError,
   306→  className,
   307→  minDays = 2,
   308→  requireContiguous = true,
   309→  initialSelection,
   310→  updateUrl = true,
   311→  weekPattern = 'every-week',
   312→}) {
   313→  // Use initialSelection if provided, otherwise get from URL or use default
   314→  const getInitialState = () => {
   315→    if (initialSelection !== undefined && initialSelection !== null) {
   316→      return new Set(initialSelection);
   317→    }
   318→    return new Set(getInitialSelectionFromUrl());
   319→  };
   320→
   321→  const [selectedDays, setSelectedDays] = useState(getInitialState());
   322→  const [isDragging, setIsDragging] = useState(false);
   323→  const [mouseDownIndex, setMouseDownIndex] = useState(null);
   324→  const [showError, setShowError] = useState(false);
   325→  const [errorMessage, setErrorMessage] = useState('');
   326→  const [hasContiguityError, setHasContiguityError] = useState(false);
   327→  const [validationTimeout, setValidationTimeout] = useState(null);
   328→  const [errorTimeout, setErrorTimeout] = useState(null);
   329→  const [checkinDay, setCheckinDay] = useState('');
   330→  const [checkoutDay, setCheckoutDay] = useState('');
   331→
   332→  /**
   333→   * Check if selected days are contiguous (handles wrap-around)
   334→   * Uses the exact logic from index_lite page
   335→   * Example: [5, 6, 0, 1, 2] (Fri, Sat, Sun, Mon, Tue) is contiguous
   336→   */
   337→  const isContiguous = useCallback((days) => {
   338→    const daysArray = Array.from(days);
   339→
   340→    // Edge cases
   341→    if (daysArray.length <= 1) {
   342→      return true;
   343→    }
   344→
   345→    if (daysArray.length >= 6) {
   346→      return true;
   347→    }
   348→
   349→    const sortedDays = [...daysArray].sort((a, b) => a - b);
   350→
   351→    // STEP 1: Check if selected days are continuous (regular check)
   352→    let isRegularContinuous = true;
   353→    for (let i = 1; i < sortedDays.length; i++) {
   354→      if (sortedDays[i] !== sortedDays[i - 1] + 1) {
   355→        isRegularContinuous = false;
   356→        break;
   357→      }
   358→    }
   359→
   360→    if (isRegularContinuous) {
   361→      return true;
   362→    }
   363→
   364→    // STEP 2: Check if UNSELECTED days are continuous (implies wrap-around)
   365→    const allDays = [0, 1, 2, 3, 4, 5, 6];
   366→    const unselectedDays = allDays.filter(day => !sortedDays.includes(day));
   367→
   368→    if (unselectedDays.length === 0) {
   369→      // All days selected
   370→      return true;
   371→    }
   372→
   373→    // Check if unselected days are continuous
   374→    const sortedUnselected = [...unselectedDays].sort((a, b) => a - b);
   375→    for (let i = 1; i < sortedUnselected.length; i++) {
   376→      if (sortedUnselected[i] !== sortedUnselected[i - 1] + 1) {
   377→        // Unselected days not continuous, selection is not valid
   378→        return false;
   379→      }
   380→    }
   381→
   382→    // Unselected days are continuous, selection wraps around!
   383→    return true;
   384→  }, []);
   385→
   386→  /**
   387→   * Validate the current selection
   388→   * NOTE: Nights = Days - 1 (last day is checkout, doesn't count as a night)
   389→   */
   390→  const validateSelection = useCallback(
   391→    (days) => {
   392→      const dayCount = days.size;
   393→      const nightCount = dayCount - 1; // Checkout day doesn't count as a night
   394→
   395→      if (dayCount === 0) {
   396→        return { valid: true };
   397→      }
   398→
   399→      // Need at least minDays + 1 days to have minDays nights
   400→      // Example: 2 nights requires 3 days (check-in + 2 nights + checkout)
   401→      if (nightCount < minDays) {
   402→        return {
   403→          valid: false,
   404→          error: `Please select at least ${minDays} night${minDays > 1 ? 's' : ''} per week`,
   405→        };
   406→      }
   407→
   408→      if (requireContiguous && !isContiguous(days)) {
   409→        return {
   410→          valid: false,
   411→          error: 'Please select contiguous days (e.g., Mon-Tue-Wed, not Mon-Wed-Fri)',
   412→        };
   413→      }
   414→
   415→      return { valid: true };
   416→    },
   417→    [minDays, requireContiguous, isContiguous]
   418→  );
   419→
   420→  /**
   421→   * Display error message
   422→   */
   423→  const displayError = useCallback(
   424→    (error) => {
   425→      // Clear any existing error timeout
   426→      if (errorTimeout) {
   427→        clearTimeout(errorTimeout);
   428→      }
   429→
   430→      setErrorMessage(error);
   431→      setShowError(true);
   432→
   433→      // Hide error after 6 seconds
   434→      const timeout = setTimeout(() => {
   435→        setShowError(false);
   436→      }, 6000);
   437→
   438→      setErrorTimeout(timeout);
   439→
   440→      if (onError) {
   441→        onError(error);
   442→      }
   443→    },
   444→    [onError, errorTimeout]
   445→  );
   446→
   447→  /**
   448→   * Handle mouse down - Start tracking for click vs drag
   449→   */
   450→  const handleMouseDown = useCallback((dayIndex) => {
   451→    setMouseDownIndex(dayIndex);
   452→  }, []);
   453→
   454→  /**
   455→   * Handle mouse enter - If dragging, fill range
   456→   */
   457→  const handleMouseEnter = useCallback(
   458→    (dayIndex) => {
   459→      // Only drag if mouse is down and we moved to a different cell
   460→      if (mouseDownIndex !== null && dayIndex !== mouseDownIndex) {
   461→        setIsDragging(true);
   462→
   463→        const newSelection = new Set();
   464→        const totalDays = 7;
   465→        const start = mouseDownIndex;
   466→
   467→        // Calculate range with wrap-around
   468→        let dayCount;
   469→        if (dayIndex >= start) {
   470→          dayCount = dayIndex - start + 1;
   471→        } else {
   472→          dayCount = (totalDays - start) + dayIndex + 1;
   473→        }
   474→
   475→        // Fill all days in range
   476→        for (let i = 0; i < dayCount; i++) {
   477→          const currentDay = (start + i) % totalDays;
   478→          newSelection.add(currentDay);
   479→        }
   480→
   481→        setSelectedDays(newSelection);
   482→      }
   483→    },
   484→    [mouseDownIndex]
   485→  );
   486→
   487→  /**
   488→   * Handle mouse up - Determine if click or drag, then act accordingly
   489→   */
   490→  const handleMouseUp = useCallback(
   491→    (dayIndex) => {
   492→      if (mouseDownIndex === null) return;
   493→
   494→      // Check if this was a click (same cell) or drag (different cell)
   495→      if (!isDragging && dayIndex === mouseDownIndex) {
   496→        // CLICK - Toggle the day
   497→        setSelectedDays(prev => {
   498→          const newSelection = new Set(prev);
   499→
   500→          if (newSelection.has(dayIndex)) {
   501→            // Check if removing this day would go below minimum nights
   502→            // After removal: (size - 1) days, which gives (size - 1 - 1) = (size - 2) nights
   503→            const daysAfterRemoval = newSelection.size - 1;
   504→            const nightsAfterRemoval = daysAfterRemoval - 1; // Checkout day doesn't count
   505→            if (nightsAfterRemoval < minDays) {
   506→              // Prevent removal and show error
   507→              displayError(`Cannot remove day - you must select at least ${minDays} night${minDays > 1 ? 's' : ''} per week`);
   508→              return prev; // Return unchanged selection
   509→            }
   510→            newSelection.delete(dayIndex);
   511→          } else {
   512→            newSelection.add(dayIndex);
   513→          }
   514→
   515→          // Clear existing validation timeout
   516→          if (validationTimeout) {
   517→            clearTimeout(validationTimeout);
   518→          }
   519→
   520→          // Schedule validation after 3 seconds of inactivity
   521→          const timeout = setTimeout(() => {
   522→            // Only validate if selection is invalid
   523→            const validation = validateSelection(newSelection);
   524→            if (!validation.valid && validation.error) {
   525→              displayError(validation.error);
   526→            }
   527→          }, 3000);
   528→
   529→          setValidationTimeout(timeout);
   530→
   531→          return newSelection;
   532→        });
   533→      } else if (isDragging) {
   534→        // DRAG - Validate immediately
   535→        const validation = validateSelection(selectedDays);
   536→        if (!validation.valid && validation.error) {
   537→          displayError(validation.error);
   538→          setSelectedDays(new Set());
   539→        }
   540→      }
   541→
   542→      // Reset drag state
   543→      setIsDragging(false);
   544→      setMouseDownIndex(null);
   545→    },
   546→    [isDragging, mouseDownIndex, selectedDays, validationTimeout, validateSelection, displayError, minDays]
   547→  );
   548→
   549→  /**
   550→   * Calculate check-in and check-out days based on selection
   551→   * Uses the exact logic from index_lite page
   552→   */
   553→  const calculateCheckinCheckout = useCallback((days) => {
   554→    if (days.size === 0) {
   555→      setCheckinDay('');
   556→      setCheckoutDay('');
   557→      return;
   558→    }
   559→
   560→    if (!isContiguous(days)) {
   561→      setCheckinDay('');
   562→      setCheckoutDay('');
   563→      return;
   564→    }
   565→
   566→    const selectedDaysArray = Array.from(days);
   567→
   568→    // Single day selection
   569→    if (selectedDaysArray.length === 1) {
   570→      setCheckinDay(DAYS_OF_WEEK[selectedDaysArray[0]].fullName);
   571→      setCheckoutDay(DAYS_OF_WEEK[selectedDaysArray[0]].fullName);
   572→      return;
   573→    }
   574→
   575→    // Multiple day selection
   576→    const sortedDays = [...selectedDaysArray].sort((a, b) => a - b);
   577→    const hasSunday = sortedDays.includes(0);
   578→    const hasSaturday = sortedDays.includes(6);
   579→
   580→    // Check if this is a wrap-around case
   581→    if (hasSunday && hasSaturday && sortedDays.length < 7) {
   582→      // Find the gap (unselected days) in the week
   583→      let gapStart = -1;
   584→      let gapEnd = -1;
   585→
   586→      // Look for the gap in the sorted days
   587→      for (let i = 0; i < sortedDays.length - 1; i++) {
   588→        if (sortedDays[i + 1] - sortedDays[i] > 1) {
   589→          // Found the gap
   590→          gapStart = sortedDays[i] + 1;  // First unselected day
   591→          gapEnd = sortedDays[i + 1] - 1;  // Last unselected day
   592→          break;
   593→        }
   594→      }
   595→
   596→      if (gapStart !== -1 && gapEnd !== -1) {
   597→        // Wrap-around case with a gap in the middle
   598→        // Check-in: First selected day AFTER the gap ends
   599→        // Check-out: Last selected day BEFORE the gap starts
   600→
   601→        // Check-in is the smallest day after the gap (considering wrap)
   602→        let checkinDayIndex;
   603→        if (sortedDays.some(day => day > gapEnd)) {
   604→          // There are days after the gap in the same week
   605→          checkinDayIndex = sortedDays.find(day => day > gapEnd);
   606→        } else {
   607→          // Wrap to Sunday
   608→          checkinDayIndex = 0;
   609→        }
   610→
   611→        // Check-out is the largest day before the gap
   612→        let checkoutDayIndex;
   613→        if (sortedDays.some(day => day < gapStart)) {
   614→          // There are days before the gap
   615→          checkoutDayIndex = sortedDays.filter(day => day < gapStart).pop();
   616→        } else {
   617→          // Wrap to Saturday
   618→          checkoutDayIndex = 6;
   619→        }
   620→
   621→        setCheckinDay(DAYS_OF_WEEK[checkinDayIndex].fullName);
   622→        setCheckoutDay(DAYS_OF_WEEK[checkoutDayIndex].fullName);
   623→      } else {
   624→        // No gap found (shouldn't happen with Sunday and Saturday selected)
   625→        // Use standard min/max
   626→        setCheckinDay(DAYS_OF_WEEK[sortedDays[0]].fullName);
   627→        setCheckoutDay(DAYS_OF_WEEK[sortedDays[sortedDays.length - 1]].fullName);
   628→      }
   629→    } else {
   630→      // Non-wrap-around case: use first and last in sorted order
   631→      setCheckinDay(DAYS_OF_WEEK[sortedDays[0]].fullName);
   632→      setCheckoutDay(DAYS_OF_WEEK[sortedDays[sortedDays.length - 1]].fullName);
   633→    }
   634→  }, [isContiguous]);
   635→
   636→  /**
   637→   * Update URL parameter when selection changes
   638→   * Format: ?days-selected=1,2,3,4,5 (0-based, where 0=Sunday)
   639→   * Only updates URL if updateUrl prop is true
   640→   */
   641→  useEffect(() => {
   642→    if (!updateUrl) {
   643→      console.log('📅 SearchScheduleSelector: URL updates disabled');
   644→      return;
   645→    }
   646→
   647→    const selectedDaysArray = Array.from(selectedDays).sort((a, b) => a - b);
   648→
   649→    if (selectedDaysArray.length > 0) {
   650→      // Use 0-based indices directly in URL
   651→      const daysParam = selectedDaysArray.join(',');
   652→
   653→      // Update URL without reloading the page
   654→      const url = new URL(window.location);
   655→      url.searchParams.set('days-selected', daysParam);
   656→      window.history.replaceState({}, '', url);
   657→
   658→      console.log('📅 SearchScheduleSelector: Updated URL parameter:', {
   659→        dayIndices: selectedDaysArray,
   660→        urlParam: daysParam
   661→      });
   662→
   663→      // Dispatch custom event for other components to listen to
   664→      window.dispatchEvent(new CustomEvent('daysSelected', { detail: { days: selectedDaysArray } }));
   665→    } else {
   666→      // Remove parameter if no days selected
   667→      const url = new URL(window.location);
   668→      url.searchParams.delete('days-selected');
   669→      window.history.replaceState({}, '', url);
   670→
   671→      console.log('📅 SearchScheduleSelector: Removed URL parameter (no days selected)');
   672→    }
   673→  }, [selectedDays, updateUrl]);
   674→
   675→  /**
   676→   * Update parent component on selection change
   677→   * Also check for contiguity errors in real-time
   678→   */
   679→  useEffect(() => {
   680→    if (onSelectionChange) {
   681→      const selectedDaysArray = Array.from(selectedDays).map(
   682→        index => DAYS_OF_WEEK[index]
   683→      );
   684→      onSelectionChange(selectedDaysArray);
   685→    }
   686→
   687→    // Calculate check-in/check-out
   688→    calculateCheckinCheckout(selectedDays);
   689→
   690→    // Validate the current selection to determine if error should be cleared
   691→    const validation = validateSelection(selectedDays);
   692→    const selectionIsValid = validation.valid;
   693→
   694→    // Check for contiguity error (visual feedback + immediate alert)
   695→    if (selectedDays.size > 1 && requireContiguous) {
   696→      const isContiguousNow = isContiguous(selectedDays);
   697→      const wasContiguousError = hasContiguityError;
   698→      setHasContiguityError(!isContiguousNow);
   699→
   700→      // Show contiguity error immediately only if it's a NEW error (wasn't already showing)
   701→      if (!isContiguousNow && !wasContiguousError && !showError) {
   702→        displayError('Please select contiguous days (e.g., Mon-Tue-Wed, not Mon-Wed-Fri)');
   703→      } else if (isContiguousNow && showError && wasContiguousError) {
   704→        // Selection became contiguous, clear the error immediately
   705→        if (errorTimeout) {
   706→          clearTimeout(errorTimeout);
   707→        }
   708→        setShowError(false);
   709→      } else if (selectionIsValid && showError) {
   710→        // Selection became valid (enough nights AND contiguous), clear any error
   711→        if (errorTimeout) {
   712→          clearTimeout(errorTimeout);
   713→        }
   714→        setShowError(false);
   715→      }
   716→    } else {
   717→      setHasContiguityError(false);
   718→      // Hide error if selection becomes valid (has enough nights)
   719→      if (showError && selectionIsValid) {
   720→        if (errorTimeout) {
   721→          clearTimeout(errorTimeout);
   722→        }
   723→        setShowError(false);
   724→      }
   725→    }
   726→  // eslint-disable-next-line react-hooks/exhaustive-deps
   727→  }, [selectedDays]);
   728→
   729→
   730→  return (
   731→    <Container className={className}>
   732→      <SelectorRow>
   733→        <CalendarIcon>
   734→          <img
   735→            src="https://50bf0464e4735aabad1cc8848a0e8b8a.cdn.bubble.io/f1748370325535x745487629939088300/calendar-minimalistic-svgrepo-com%202.svg"
   736→            alt="Calendar"
   737→          />
   738→        </CalendarIcon>
   739→
   740→        <DaysGrid>
   741→          {DAYS_OF_WEEK.map((day, index) => (
   742→            <DayCell
   743→              key={day.id}
   744→              $isSelected={selectedDays.has(index)}
   745→              $isDragging={isDragging}
   746→              $hasError={hasContiguityError}
   747→              $errorStyle={1}
   748→              onMouseDown={(e) => {
   749→                e.preventDefault();
   750→                handleMouseDown(index);
   751→              }}
   752→              onMouseEnter={() => handleMouseEnter(index)}
   753→              onMouseUp={() => handleMouseUp(index)}
   754→              role="button"
   755→              aria-pressed={selectedDays.has(index)}
   756→              aria-label={`Select ${day.fullName}`}
   757→            >
   758→              {day.singleLetter}
   759→            </DayCell>
   760→          ))}
   761→        </DaysGrid>
   762→      </SelectorRow>
   763→
   764→      <InfoContainer>
   765→        {selectedDays.size > 0 && (
   766→          <>
   767→            <InfoText>
   768→              {showError ? (
   769→                <span style={{ color: '#d32f2f' }}>
   770→                  {errorMessage}
   771→                </span>
   772→              ) : selectedDays.size === 7 ? (
   773→                <span className="day-name">Full Time</span>
   774→              ) : (
   775→                checkinDay && checkoutDay && (
   776→                  <CheckInOutRow>
   777→                    <RepeatIcon viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
   778→                      <path
   779→                        d="M17 2L21 6M21 6L17 10M21 6H8C5.23858 6 3 8.23858 3 11M7 22L3 18M3 18L7 14M3 18H16C18.7614 18 21 15.7614 21 13"
   780→                        stroke="currentColor"
   781→                        strokeWidth="2"
   782→                        strokeLinecap="round"
   783→                        strokeLinejoin="round"
   784→                      />
   785→                    </RepeatIcon>
   786→                    <span>
   787→                      <strong>Check-in:</strong> <span className="day-name">{checkinDay}</span> • <strong>Check-out:</strong> <span className="day-name">{checkoutDay}</span>
   788→                    </span>
   789→                  </CheckInOutRow>
   790→                )
   791→              )}
   792→            </InfoText>
   793→            {!showError && selectedDays.size < 7 && getRepeatPatternText(weekPattern) && (
   794→              <RepeatPatternText>
   795→                {getRepeatPatternText(weekPattern)}
   796→              </RepeatPatternText>
   797→            )}
   798→          </>
   799→        )}
   800→      </InfoContainer>
   801→    </Container>
   802→  );
   803→}
