   270→  // Calculate total
   271→  const totalReservationPrice = pricePerNight * nightsCount * actualWeeksDuringReservation;
   272→
   273→  return totalReservationPrice;
   274→}
   275→
   276→/**
   277→ * Get weekly schedule period based on "Weeks offered" pattern
   278→ */
   279→function getWeeklySchedulePeriod(weeksOffered) {
   280→  const pattern = (weeksOffered || 'every week').toLowerCase();
   281→
   282→  // Check for various patterns
   283→  if (pattern.includes('1 on 1 off') || pattern.includes('1on1off') ||
   284→      (pattern.includes('one week on') && pattern.includes('one week off')) ||
   285→      (pattern.includes('1 week on') && pattern.includes('1 week off'))) {
   286→    return 2;
   287→  }
   288→
   289→  if (pattern.includes('2 on 2 off') || pattern.includes('2on2off') ||
   290→      (pattern.includes('two week') && pattern.includes('two week')) ||
   291→      (pattern.includes('2 week on') && pattern.includes('2 week off'))) {
   292→    return 2;
   293→  }
   294→
   295→  if (pattern.includes('1 on 3 off') || pattern.includes('1on3off') ||
   296→      (pattern.includes('one week on') && pattern.includes('three week')) ||
   297→      (pattern.includes('1 week on') && pattern.includes('3 week off'))) {
   298→    return 4;
   299→  }
   300→
   301→  return 1; // Default: "Every week"
   302→}
   303→
   304→/**
   305→ * Get actual weeks during 4-week period based on pattern
   306→ */
   307→function getActualWeeksDuring4Week(weeksOffered) {
   308→  const pattern = (weeksOffered || 'every week').toLowerCase();
   309→
   310→  // Check for various patterns
   311→  if (pattern.includes('1 on 1 off') || pattern.includes('1on1off') ||
   312→      (pattern.includes('one week on') && pattern.includes('one week off')) ||
   313→      (pattern.includes('1 week on') && pattern.includes('1 week off'))) {
   314→    return 2;
   315→  }
