     1→/**
     2→ * Split Lease Authentication Utilities
     3→ * Extracted from input/index/script.js
     4→ *
     5→ * Provides authentication-related functions for:
     6→ * - Cookie checking and parsing
     7→ * - Token management
     8→ * - Login status detection
     9→ * - Username extraction
    10→ * - Session validation
    11→ *
    12→ * No fallback mechanisms - returns null or throws error on auth failure
    13→ *
    14→ * Usage:
    15→ *   import { checkAuthStatus, getUsernameFromCookies } from './auth.js'
    16→ */
    17→
    18→import {
    19→  AUTH_STORAGE_KEYS,
    20→  SESSION_VALIDATION,
    21→  SIGNUP_LOGIN_URL,
    22→  ACCOUNT_PROFILE_URL
    23→} from './constants.js';
    24→import { supabase } from './supabase.js';
    25→import { logger } from './logger.js';
    26→import { delay } from './timing.js';
    27→import {
    28→  setAuthToken as setSecureAuthToken,
    29→  getAuthToken as getSecureAuthToken,
    30→  setSessionId as setSecureSessionId,
    31→  getSessionId as getSecureSessionId,
    32→  setAuthState,
    33→  getAuthState,
    34→  getUserId as getPublicUserId,
    35→  setUserType as setSecureUserType,
    36→  getUserType as getSecureUserType,
    37→  clearAllAuthData,
    38→  hasValidTokens,
    39→  migrateFromLegacyStorage,
    40→  getFirstName as getSecureFirstName,
    41→  getAvatarUrl as getSecureAvatarUrl,
    42→  setFirstName as setSecureFirstName,
    43→  setAvatarUrl as setSecureAvatarUrl,
    44→  setLinkedInOAuthUserType,
    45→  getLinkedInOAuthUserType,
    46→  clearLinkedInOAuthUserType,
    47→  setLinkedInOAuthLoginFlow,
    48→  getLinkedInOAuthLoginFlow,
    49→  clearLinkedInOAuthLoginFlow,
    50→  setGoogleOAuthUserType,
    51→  getGoogleOAuthUserType,
    52→  clearGoogleOAuthUserType,
    53→  setGoogleOAuthLoginFlow,
    54→  getGoogleOAuthLoginFlow,
    55→  clearGoogleOAuthLoginFlow
    56→} from './secureStorage.js';
    57→
    58→// ============================================================================
    59→// Auth State Management
    60→// ============================================================================
    61→
    62→let isUserLoggedInState = false;
    63→let authCheckAttempts = 0;
    64→const MAX_AUTH_CHECK_ATTEMPTS = SESSION_VALIDATION.MAX_AUTH_CHECK_ATTEMPTS;
    65→
    66→// ============================================================================
    67→// Cookie Parsing Utilities
    68→// ============================================================================
    69→
    70→/**
    71→ * Parse username from document cookies
    72→ * Decodes URL-encoded cookie values and removes surrounding quotes
    73→ *
    74→ * @returns {string|null} Username if found, null if no username cookie exists
    75→ */
    76→export function getUsernameFromCookies() {
    77→  const cookies = document.cookie.split('; ');
    78→  const usernameCookie = cookies.find(c => c.startsWith('username='));
    79→
    80→  if (usernameCookie) {
    81→    let username = decodeURIComponent(usernameCookie.split('=')[1]);
    82→    // Remove surrounding quotes if present (both single and double quotes)
    83→    username = username.replace(/^["']|["']$/g, '');
    84→    return username;
    85→  }
    86→
    87→  return null;
    88→}
    89→
    90→/**
    91→ * Check Split Lease cookies from Bubble app
    92→ * Verifies both loggedIn and username cookies
    93→ *
    94→ * @returns {Object} Authentication status object
    95→ *   - isLoggedIn: boolean indicating if user is logged in
    96→ *   - username: string with username or null if not set
    97→ */
    98→export function checkSplitLeaseCookies() {
    99→  const cookies = document.cookie.split('; ');
   100→  const loggedInCookie = cookies.find(c => c.startsWith('loggedIn='));
   101→  const usernameCookie = cookies.find(c => c.startsWith('username='));
   102→
   103→  const isLoggedIn = loggedInCookie ? loggedInCookie.split('=')[1] === 'true' : false;
   104→  const username = getUsernameFromCookies();
   105→
   106→  // Log the authentication status to console
   107→  logger.debug('🔐 Split Lease Cookie Auth Check:');
   108→  logger.debug('   Logged In:', isLoggedIn);
   109→  logger.debug('   Username:', username || 'not set');
   110→  logger.debug('   Raw Cookies:', { loggedInCookie, usernameCookie });
   111→
   112→  return { isLoggedIn, username };
   113→}
   114→
   115→// ============================================================================
   116→// Authentication Status Checking
   117→// ============================================================================
   118→
   119→/**
   120→ * Lightweight authentication status check
   121→ * Checks auth state (not tokens) and validates session
   122→ *
   123→ * Supports both:
   124→ * - Supabase Auth sessions (native signup)
   125→ * - Legacy Bubble auth (tokens in localStorage)
   126→ *
   127→ * No fallback mechanisms - returns boolean directly
   128→ * On failure: returns false without fallback logic
   129→ *
   130→ * @returns {Promise<boolean>} True if user is authenticated, false otherwise
   131→ */
   132→export async function checkAuthStatus() {
   133→  logger.debug('🔍 Checking authentication status...');
   134→
   135→  // Try to migrate from legacy storage first
   136→  const migrated = await migrateFromLegacyStorage();
   137→  if (migrated) {
   138→    logger.debug('✅ Migrated from legacy storage');
   139→  }
   140→
   141→  // First check cross-domain cookies from .split.lease (for compatibility)
   142→  const splitLeaseAuth = checkSplitLeaseCookies();
   143→
   144→  if (splitLeaseAuth.isLoggedIn) {
   145→    logger.debug('✅ User authenticated via Split Lease cookies');
   146→    logger.debug('   Username:', splitLeaseAuth.username);
   147→    isUserLoggedInState = true;
   148→    setAuthState(true);
   149→    return true;
   150→  }
   151→
   152→  // Check Supabase Auth session (for native Supabase signups)
   153→  // CRITICAL: Supabase client may not have loaded session from localStorage yet
   154→  // We need to handle the race condition where getSession() returns null initially
   155→  // but INITIAL_SESSION fires shortly after with a valid session
   156→  try {
   157→    let { data: { session }, error } = await supabase.auth.getSession();
   158→
   159→    // If no session on first check, wait briefly for Supabase to initialize
   160→    // This handles the race condition on page load
   161→    if (!session && !error) {
   162→      logger.debug('🔄 No immediate Supabase session, waiting briefly for initialization...');
   163→      await delay(200);
   164→      const retryResult = await supabase.auth.getSession();
   165→      session = retryResult.data?.session;
   166→      error = retryResult.error;
   167→      if (session) {
   168→        logger.debug('✅ Found Supabase session after brief wait');
   169→      }
   170→    }
   171→
   172→    if (session && !error) {
   173→      logger.debug('✅ User authenticated via Supabase Auth session');
   174→      logger.debug('   User ID:', session.user?.id);
   175→      logger.debug('   Email:', session.user?.email);
   176→
   177→      // Sync Supabase session to our storage for consistency
   178→      const userId = session.user?.user_metadata?.user_id || session.user?.id;
   179→      const userType = session.user?.user_metadata?.user_type;
   180→
   181→      setSecureAuthToken(session.access_token);
   182→      if (userId) {
   183→        setSecureSessionId(userId);
   184→        setAuthState(true, userId);
   185→      }
   186→      if (userType) {
   187→        setSecureUserType(userType);
   188→      }
   189→
   190→      isUserLoggedInState = true;
   191→      return true;
   192→    }
   193→  } catch (err) {
   194→    logger.debug('⚠️ Supabase session check failed:', err.message);
   195→    // Continue to check legacy auth
   196→  }
   197→
   198→  // Check auth state (not tokens directly) - legacy Bubble auth
   199→  const authState = getAuthState();
   200→
   201→  if (authState) {
   202→    // Verify we actually have tokens
   203→    const hasTokens = await hasValidTokens();
   204→
   205→    if (hasTokens) {
   206→      logger.debug('✅ User authenticated via secure storage (legacy)');
   207→      isUserLoggedInState = true;
   208→      return true;
   209→    }
   210→  }
   211→
   212→  logger.debug('❌ User not authenticated');
   213→  isUserLoggedInState = false;
   214→  setAuthState(false);
   215→  return false;
   216→}
   217→
   218→// ============================================================================
   219→// Session Management
   220→// ============================================================================
   221→
   222→/**
   223→ * Validate session by checking if tokens exist
   224→ * Bubble API handles actual token expiry - we validate on each request
   225→ *
   226→ * @returns {boolean} True if session is valid, false if expired or missing
   227→ */
   228→export function isSessionValid() {
   229→  // Simply check if auth state is set
   230→  // Bubble will reject expired tokens on API calls
   231→  return getAuthState();
   232→}
   233→
   234→/**
   235→ * Clear authentication data from storage
   236→ * Removes all auth tokens, session IDs, timestamps, user type, and cookies
   237→ */
   238→export function clearAuthData() {
   239→  // Use secure storage clear (handles both secure tokens and state)
   240→  clearAllAuthData();
   241→
   242→  isUserLoggedInState = false;
   243→  logger.debug('🗑️ Authentication data cleared (secure storage and cookies)');
   244→}
   245→
   246→// ============================================================================
   247→// Token Management
   248→// ============================================================================
   249→
   250→/**
   251→ * Get authentication token from secure storage
   252→ * NOTE: This should only be used internally for API calls
   253→ * External code should not access tokens directly
   254→ *
   255→ * @returns {string|null} Auth token if exists, null otherwise
   256→ */
   257→export function getAuthToken() {
   258→  return getSecureAuthToken();
   259→}
   260→
   261→/**
   262→ * Store authentication token in secure storage
   263→ *
   264→ * @param {string} token - Authentication token to store
   265→ */
   266→export function setAuthToken(token) {
   267→  setSecureAuthToken(token);
   268→}
   269→
   270→/**
   271→ * Get session ID from secure storage
   272→ * NOTE: This should only be used internally
   273→ * External code should not access session IDs directly
   274→ *
   275→ * @returns {string|null} Session ID if exists, null otherwise
   276→ */
   277→export function getSessionId() {
   278→  return getSecureSessionId();
   279→}
   280→
   281→/**
   282→ * Store session ID in secure storage
   283→ *
   284→ * @param {string} sessionId - Session ID to store
   285→ */
   286→export function setSessionId(sessionId) {
   287→  setSecureSessionId(sessionId);
   288→}
   289→
   290→/**
   291→ * Get user ID from public state (non-sensitive identifier)
   292→ * NOTE: This is public state, not the encrypted session ID
   293→ *
   294→ * @returns {string|null} User ID if exists, null otherwise
   295→ */
   296→export function getUserId() {
   297→  return getPublicUserId();
   298→}
   299→
   300→/**
   301→ * Get user type from storage (public state)
   302→ *
   303→ * @returns {string|null} User type ('Host' or 'Guest') if exists, null otherwise
   304→ */
   305→export function getUserType() {
   306→  return getSecureUserType();
   307→}
   308→
   309→/**
   310→ * Store user type (public state)
   311→ *
   312→ * @param {string} userType - User type to store ('Host' or 'Guest')
   313→ */
   314→export function setUserType(userType) {
   315→  if (userType) {
   316→    setSecureUserType(userType);
   317→  }
   318→}
   319→
   320→// ============================================================================
   321→// User Information
   322→// ============================================================================
   323→
   324→/**
   325→ * Get current logged-in username from cookies
   326→ *
   327→ * @returns {string|null} Username if logged in, null otherwise
   328→ */
   329→export function getCurrentUsername() {
   330→  if (!isUserLoggedInState) {
   331→    return null;
   332→  }
   333→  return getUsernameFromCookies();
   334→}
   335→
   336→/**
   337→ * Store username in global scope for use in redirects
   338→ * Also used by handleLoggedInUser to persist username
   339→ *
   340→ * @param {string} username - Username to store
   341→ */
   342→export function storeCurrentUsername(username) {
   343→  if (typeof window !== 'undefined') {
   344→    window.currentUsername = username;
   345→  }
   346→}
   347→
   348→/**
   349→ * Get stored username from global scope
   350→ *
   351→ * @returns {string|null} Stored username or null
   352→ */
   353→export function getStoredUsername() {
   354→  if (typeof window !== 'undefined') {
   355→    return window.currentUsername || null;
   356→  }
   357→  return null;
   358→}
   359→
   360→// ============================================================================
   361→// User Profile Getters (Proxied from secureStorage)
   362→// ============================================================================
   363→
   364→/**
   365→ * Get first name
   366→ * @returns {string|null} First name or null
   367→ */
   368→export function getFirstName() {
   369→  return getSecureFirstName();
   370→}
   371→
   372→/**
   373→ * Get avatar URL
   374→ * @returns {string|null} Avatar URL or null
   375→ */
   376→export function getAvatarUrl() {
   377→  return getSecureAvatarUrl();
   378→}
   379→
   380→// ============================================================================
   381→// Authentication Redirect Utilities
   382→// ============================================================================
   383→
   384→/**
   385→ * Redirect to login page
   386→ * Direct redirect without modal or iframe
   387→ *
   388→ * @param {string} returnUrl - Optional URL to return to after login
   389→ */
   390→export function redirectToLogin(returnUrl = null) {
   391→  let url = SIGNUP_LOGIN_URL;
   392→
   393→  if (returnUrl) {
   394→    url += `?returnTo=${encodeURIComponent(returnUrl)}`;
   395→  }
   396→
   397→  window.location.href = url;
   398→}
   399→
   400→/**
   401→ * Redirect to account profile page with user ID
   402→ * Only redirect if user is logged in and has a valid session ID
   403→ *
   404→ * @returns {Promise<boolean>} True if redirect initiated, false if not logged in or no user ID
   405→ */
