  1113→
  1114→  try {
  1115→    const { data, error } = await supabase.functions.invoke('auth-user', {
  1116→      body: {
  1117→        action: 'logout',
  1118→        payload: {
  1119→          token
  1120→        }
  1121→      }
  1122→    });
  1123→
  1124→    // Clear auth data regardless of API response
  1125→    // This ensures clean logout even if API call fails
  1126→    clearAuthData();
  1127→
  1128→    if (error || !data.success) {
