  1153→// ============================================================================
  1154→// URL Auth Error Detection (Magic Link / OTP Errors)
  1155→// ============================================================================
  1156→
  1157→/**
  1158→ * Check URL hash for Supabase auth errors
  1159→ * Returns error info if present, null otherwise
  1160→ *
  1161→ * When Supabase redirects after a failed magic link (expired OTP, already used, etc.),
  1162→ * it includes error params in the URL hash:
  1163→ * #error=access_denied&error_code=otp_expired&error_description=Email+link+is+invalid+or+has+expired
  1164→ *
  1165→ * @returns {Object|null} Error object with error, errorCode, errorDescription, or null if no error
  1166→ */
  1167→export function checkUrlForAuthError() {
  1168→  const hash = window.location.hash;
  1169→  if (!hash) return null;
  1170→
  1171→  const params = new URLSearchParams(hash.substring(1));
  1172→  const error = params.get('error');
  1173→  const errorCode = params.get('error_code');
  1174→  const errorDescription = params.get('error_description');
  1175→
  1176→  if (error || errorCode) {
  1177→    return {
  1178→      error,
  1179→      errorCode,
  1180→      errorDescription: errorDescription ? decodeURIComponent(errorDescription.replace(/\+/g, ' ')) : null
  1181→    };
  1182→  }
  1183→
  1184→  return null;
  1185→}
  1186→
  1187→/**
  1188→ * Clear auth error params from URL hash
  1189→ * Call this after handling the error to prevent re-processing
  1190→ * Uses replaceState to not add to browser history
  1191→ */
  1192→export function clearAuthErrorFromUrl() {
  1193→  if (window.location.hash) {
  1194→    window.history.replaceState(null, '', window.location.pathname + window.location.search);
  1195→  }
  1196→}
