  1428→/**
  1429→ * Handle LinkedIn OAuth callback after redirect
  1430→ * Creates user record if new user, or links accounts if existing
  1431→ *
  1432→ * @returns {Promise<Object>} Result with user data or error
  1433→ */
  1434→export async function handleLinkedInOAuthCallback() {
  1435→  logger.debug('[Auth] Handling LinkedIn OAuth callback');
  1436→
  1437→  // Get the session from OAuth callback
  1438→  const { data: { session }, error: sessionError } = await supabase.auth.getSession();
  1439→
  1440→  if (sessionError || !session) {
  1441→    clearLinkedInOAuthUserType();
  1442→    return {
  1443→      success: false,
  1444→      error: sessionError?.message || 'No session found after OAuth'
  1445→    };
  1446→  }
  1447→
  1448→  // Check if this is a fresh OAuth session (from LinkedIn)
  1449→  const user = session.user;
  1450→  const isLinkedInProvider =
  1451→    user?.app_metadata?.provider === 'linkedin_oidc' ||
  1452→    user?.app_metadata?.providers?.includes('linkedin_oidc');
  1453→
  1454→  if (!isLinkedInProvider) {
  1455→    return { success: false, error: 'Not a LinkedIn OAuth session' };
  1456→  }
  1457→
  1458→  // Retrieve stored user type
  1459→  const userType = getLinkedInOAuthUserType() || 'Guest';
  1460→
  1461→  // Extract LinkedIn data from user_metadata
  1462→  const linkedInData = {
  1463→    email: user.email,
  1464→    firstName: user.user_metadata?.given_name || user.user_metadata?.first_name || '',
  1465→    lastName: user.user_metadata?.family_name || user.user_metadata?.last_name || '',
  1466→    profilePhoto: user.user_metadata?.picture || user.user_metadata?.avatar_url || null,
  1467→    supabaseUserId: user.id,
  1468→  };
  1469→
  1470→  logger.debug('[Auth] LinkedIn data:', linkedInData);
  1471→  logger.debug('[Auth] Stored userType:', userType);
  1472→
  1473→  // Call Edge Function to create/link user record
  1474→  try {
  1475→    const response = await fetch(`${import.meta.env.VITE_SUPABASE_URL}/functions/v1/auth-user`, {
  1476→      method: 'POST',
  1477→      headers: {
  1478→        'Content-Type': 'application/json',
  1479→        'apikey': import.meta.env.VITE_SUPABASE_ANON_KEY,
  1480→        'Authorization': `Bearer ${session.access_token}`,
  1481→      },
  1482→      body: JSON.stringify({
  1483→        action: 'oauth_signup',
  1484→        payload: {
  1485→          ...linkedInData,
  1486→          userType,
  1487→          provider: 'linkedin_oidc',
  1488→          access_token: session.access_token,
  1489→          refresh_token: session.refresh_token,
  1490→        }
  1491→      })
  1492→    });
  1493→
  1494→    const data = await response.json();
  1495→
  1496→    if (!response.ok || !data.success) {
  1497→      clearLinkedInOAuthUserType();
  1498→      return {
  1499→        success: false,
  1500→        error: data.error || 'Failed to create user record',
  1501→        isDuplicate: data.isDuplicate || false,
  1502→        existingEmail: data.existingEmail || null,
  1503→      };
  1504→    }
  1505→
  1506→    // Clear OAuth storage keys
  1507→    clearLinkedInOAuthUserType();
  1508→
  1509→    // Store session data
  1510→    setAuthToken(session.access_token);
  1511→    setSessionId(data.data.user_id);
  1512→    setAuthState(true, data.data.user_id);
  1513→    if (userType) {
  1514→      setUserType(userType);
  1515→    }
  1516→
  1517→    return {
  1518→      success: true,
  1519→      data: data.data,
  1520→      isNewUser: data.data.isNewUser,
  1521→    };
  1522→
  1523→  } catch (err) {
  1524→    clearLinkedInOAuthUserType();
  1525→    return { success: false, error: err.message };
  1526→  }
  1527→}
