  1581→
  1582→  try {
  1583→    // Get the session from OAuth callback
  1584→    const { data: { session }, error: sessionError } = await supabase.auth.getSession();
  1585→
  1586→    if (sessionError || !session) {
  1587→      clearLinkedInOAuthLoginFlow();
  1588→      return {
  1589→        success: false,
  1590→        error: sessionError?.message || 'No session found after OAuth'
  1591→      };
  1592→    }
  1593→
  1594→    // Check if this is a fresh OAuth session (from LinkedIn)
  1595→    const user = session.user;
  1596→    const isLinkedInProvider =
  1597→      user?.app_metadata?.provider === 'linkedin_oidc' ||
  1598→      user?.app_metadata?.providers?.includes('linkedin_oidc');
  1599→
  1600→    if (!isLinkedInProvider) {
  1601→      clearLinkedInOAuthLoginFlow();
