  1608→
  1609→    logger.debug('[Auth] LinkedIn OAuth login data:', { email, supabaseUserId });
  1610→
  1611→    // Call Edge Function to verify user exists
  1612→    const response = await fetch(`${import.meta.env.VITE_SUPABASE_URL}/functions/v1/auth-user`, {
  1613→      method: 'POST',
  1614→      headers: {
  1615→        'Content-Type': 'application/json',
  1616→        'apikey': import.meta.env.VITE_SUPABASE_ANON_KEY,
  1617→        'Authorization': `Bearer ${session.access_token}`,
  1618→      },
  1619→      body: JSON.stringify({
  1620→        action: 'oauth_login',
  1621→        payload: {
  1622→          email,
  1623→          supabaseUserId,
  1624→          access_token: session.access_token,
  1625→          refresh_token: session.refresh_token,
  1626→        }
  1627→      })
  1628→    });
  1629→
  1630→    const data = await response.json();
  1631→
  1632→    // Always clear the login flow flag
  1633→    clearLinkedInOAuthLoginFlow();
  1634→
  1635→    if (!response.ok || !data.success) {
  1636→      // Check if user was not found
  1637→      if (data.data?.userNotFound) {
  1638→        logger.debug('[Auth] User not found for OAuth login:', email);
  1639→        return {
  1640→          success: false,
  1641→          userNotFound: true,
  1642→          email: email,
  1643→          error: 'No account found with this email'
  1644→        };
  1645→      }
  1646→
  1647→      return {
  1648→        success: false,
  1649→        error: data.error || 'Failed to login'
  1650→      };
  1651→    }
  1652→
  1653→    // Store session data
  1654→    const { user_id, user_type, supabase_user_id, access_token, refresh_token } = data.data;
  1655→
  1656→    setAuthToken(session.access_token);
  1657→    setSessionId(user_id);
  1658→    setAuthState(true, user_id);
  1659→    if (user_type) {
  1660→      setUserType(user_type);
  1661→    }
  1662→
  1663→    // Store Supabase user ID for reference
  1664→    if (supabase_user_id) {
  1665→      localStorage.setItem('splitlease_supabase_user_id', supabase_user_id);
  1666→    }
  1667→
  1668→    logger.debug('[Auth] LinkedIn OAuth login successful');
  1669→    return {
  1670→      success: true,
  1671→      data: data.data
  1672→    };
  1673→
  1674→  } catch (err) {
  1675→    clearLinkedInOAuthLoginFlow();
  1676→    logger.error('[Auth] LinkedIn OAuth login callback error:', err);
  1677→    return { success: false, error: err.message };
  1678→  }
  1679→}
