  1681→// ============================================================================
  1682→// Google OAuth Functions
  1683→// ============================================================================
  1684→
  1685→/**
  1686→ * Initiate Google OAuth signup flow
  1687→ * Stores user type in localStorage before redirecting to Google
  1688→ *
  1689→ * @param {string} userType - 'Host' or 'Guest'
  1690→ * @returns {Promise<Object>} Result with success status or error
  1691→ */
  1692→export async function initiateGoogleOAuth(userType) {
  1693→  logger.debug('[Auth] Initiating Google OAuth with userType:', userType);
  1694→
  1695→  // Store user type before redirect
  1696→  setGoogleOAuthUserType(userType);
  1697→
  1698→  try {
  1699→    const { data, error } = await supabase.auth.signInWithOAuth({
  1700→      provider: 'google',
