  1823→    return { success: false, error: err.message };
  1824→  }
  1825→}
  1826→
  1827→/**
  1828→ * Initiate Google OAuth login flow
  1829→ * Sets login flow flag and redirects to current page after OAuth
  1830→ *
  1831→ * @returns {Promise<Object>} Result with success status or error
  1832→ */
  1833→export async function initiateGoogleOAuthLogin() {
  1834→  logger.debug('[Auth] Initiating Google OAuth Login');
  1835→
  1836→  // Clear any existing signup flow flags to prevent conflicts
