     6→import { useAIImportAssistant } from './hooks/useAIImportAssistant';
     7→
     8→/**
     9→ * Custom hook for ListingDashboardPage logic - Orchestrator Pattern
    10→ * Composes focused hooks for auth, data, photos, and AI functionality
    11→ * Follows the Hollow Component Pattern - all business logic is here
    12→ */
    13→export default function useListingDashboardPageLogic() {
    14→  // Get listing ID from URL
    15→  const getListingIdFromUrl = useCallback(() => {
    16→    const params = new URLSearchParams(window.location.search);
    17→    return params.get('id') || params.get('listing_id');
    18→  }, []);
    19→
    20→  const listingId = getListingIdFromUrl();
    21→
    22→  // Compose focused hooks
    23→  const auth = useListingAuth();
    24→  const data = useListingData(listingId);
    25→  const photos = usePhotoManagement(
    26→    data.listing,
    27→    data.setListing,
    28→    (silent) => data.fetchListing(silent),
    29→    listingId
    30→  );
    31→  const ai = useAIImportAssistant(
    32→    data.listing,
    33→    data.updateListing,
    34→    data.setListing,
    35→    (silent) => data.fetchListing(silent),
    36→    listingId
    37→  );
    38→
    39→  // Local UI state
    40→  const [activeTab, setActiveTab] = useState('manage');
    41→  const [editSection, setEditSection] = useState(null);
    42→  const [editFocusField, setEditFocusField] = useState(null);
    43→  const [showScheduleCohost, setShowScheduleCohost] = useState(false);
    44→  const [showImportReviews, setShowImportReviews] = useState(false);
    45→  const [isImportingReviews, setIsImportingReviews] = useState(false);
    46→
    47→  // Tab change handler
    48→  const handleTabChange = useCallback((tab) => {
    49→    setActiveTab(tab);
    50→
    51→    switch (tab) {
    52→      case 'preview':
    53→        if (data.listing) {
    54→          window.open(`/preview-split-lease.html?id=${data.listing.id}`, '_blank');
    55→        }
    56→        break;
    57→      case 'proposals':
    58→      case 'virtual-meetings':
    59→      case 'leases':
    60→      default:
    61→        break;
    62→    }
    63→  }, [data.listing]);
    64→
    65→  // Copy link handler
    66→  const handleCopyLink = useCallback(async () => {
    67→    if (!data.listing?.id) {
    68→      window.showToast?.({
    69→        title: 'Error',
    70→        content: 'No listing ID available',
    71→        type: 'error'
    72→      });
    73→      return;
    74→    }
    75→
    76→    const listingUrl = `${window.location.origin}/view-split-lease/${data.listing.id}`;
    77→
    78→    try {
    79→      if (navigator.clipboard && navigator.clipboard.writeText) {
