/**
 * Price Calculation Utilities
 * Handles all pricing logic for view-split-lease page
 *
 * Usage:
 *   import { calculate4WeekRent, calculateReservationTotal } from './priceCalculations.js';
 */

/**
 * Calculate 4-week rent based on nightly price and selected nights
 * Formula: nightly price × nights per week × 4 weeks
 * @param {number} nightlyPrice - Price per night
 * @param {number} nightsPerWeek - Number of nights selected per week
 * @returns {number} 4-week rent amount
 */
export function calculate4WeekRent(nightlyPrice, nightsPerWeek) {
  if (!nightlyPrice || !nightsPerWeek) return 0;
  return nightlyPrice * nightsPerWeek * 4;
}

/**
 * Calculate estimated reservation total
 * Formula: 4-week rent × (total weeks / 4)
 * @param {number} fourWeekRent - 4-week rent amount
 * @param {number} totalWeeks - Total reservation span in weeks
 * @returns {number} Estimated total cost
 */
export function calculateReservationTotal(fourWeekRent, totalWeeks) {
  if (!fourWeekRent || !totalWeeks) return 0;
  return fourWeekRent * (totalWeeks / 4);
}

/**
 * Get nightly price based on number of nights selected
 * Matches Bubble logic for price field selection
 * @param {object} listing - Listing object with price fields
 * @param {number} nightsSelected - Number of nights per week (2-7)
 * @returns {number|null} Nightly price
 */
export function getNightlyPriceForNights(listing, nightsSelected) {
  if (!listing || !nightsSelected) return null;

  // Price override takes precedence
  if (listing['💰Price Override']) {
    return listing['💰Price Override'];
  }

  // Map nights to price fields
  const priceFieldMap = {
    1: '💰Nightly Host Rate for 1 night',
    2: '💰Nightly Host Rate for 2 nights',
    3: '💰Nightly Host Rate for 3 nights',
    4: '💰Nightly Host Rate for 4 nights',
    5: '💰Nightly Host Rate for 5 nights',
    7: '💰Nightly Host Rate for 7 nights'
  };

  const fieldName = priceFieldMap[nightsSelected];
  if (fieldName && listing[fieldName]) {
    return listing[fieldName];
  }

  // Default to 4-night rate if available
  return listing['💰Nightly Host Rate for 4 nights'] || null;
}

// Re-export canonical price formatter
export { formatPriceOrZero as formatPrice } from './formatters/priceFormatter.js';

/**
 * Calculate complete pricing breakdown
 * @param {object} listing - Listing object
 * @param {number} nightsPerWeek - Nights selected per week
 * @param {number} reservationWeeks - Total reservation span in weeks
 * @returns {object} Complete pricing breakdown
 */
export function calculatePricingBreakdown(listing, nightsPerWeek, reservationWeeks) {
  const nightlyPrice = getNightlyPriceForNights(listing, nightsPerWeek);

  if (!nightlyPrice) {
    return {
      nightlyPrice: null,
      fourWeekRent: null,
      reservationTotal: null,
      cleaningFee: listing['💰Cleaning Cost / Maintenance Fee'] || 0,
      damageDeposit: listing['💰Damage Deposit'] || 0,
      valid: false
    };
  }

  const fourWeekRent = calculate4WeekRent(nightlyPrice, nightsPerWeek);
  const reservationTotal = calculateReservationTotal(fourWeekRent, reservationWeeks);

  return {
    nightlyPrice,
    fourWeekRent,
    reservationTotal,
    cleaningFee: listing['💰Cleaning Cost / Maintenance Fee'] || 0,
    damageDeposit: listing['💰Damage Deposit'] || 0,
    grandTotal: reservationTotal + (listing['💰Cleaning Cost / Maintenance Fee'] || 0),
    valid: true
  };
}

/**
 * Validate if enough days are selected for price calculation
 * @param {number} daysSelected - Number of days selected
 * @returns {boolean} True if valid for pricing
 */
export function isValidForPricing(daysSelected) {
  return daysSelected >= 2 && daysSelected <= 7;
}

/**
 * Get price display message based on selection state
 * @param {number|null} daysSelected - Number of days selected
 * @returns {string} Display message
 */
export function getPriceDisplayMessage(daysSelected) {
  if (!daysSelected || daysSelected < 2) {
    return 'Please Select More Days';
  }
  if (daysSelected > 7) {
    return 'Please Select 7 Days or Less';
  }
  return null; // Valid selection
}

import { getNightlyRateByFrequency } from './getNightlyRateByFrequency.js'
import { calculateFourWeekRent } from './calculateFourWeekRent.js'
import { calculateReservationTotal } from './calculateReservationTotal.js'

/**
 * Calculate complete pricing breakdown for a listing rental.
 *
 * @intent Provide comprehensive price calculation including all fees.
 * @rule Combines nightly rate, 4-week rent, reservation total, and fees.
 * @rule All calculations must succeed or throw - no partial results.
 *
 * @param {object} params - Named parameters.
 * @param {object} params.listing - Listing object with all pricing fields.
 * @param {number} params.nightsPerWeek - Nights selected per week (2-7).
 * @param {number} params.reservationWeeks - Total reservation span in weeks.
 * @returns {object} Complete pricing breakdown with all calculated values.
 *
 * @throws {Error} If any required parameter is missing or invalid.
 * @throws {Error} If any calculation in the chain fails.
 *
 * @example
 * const breakdown = calculatePricingBreakdown({
 *   listing: { '💰Nightly Host Rate for 4 nights': 100, '💰Cleaning Cost / Maintenance Fee': 50 },
 *   nightsPerWeek: 4,
 *   reservationWeeks: 13
 * })
 * // => { nightlyPrice: 100, fourWeekRent: 1600, reservationTotal: 5200, ... }
 */
export function calculatePricingBreakdown({ listing, nightsPerWeek, reservationWeeks }) {
  // No Fallback: Validate all inputs
  if (!listing || typeof listing !== 'object') {
    throw new Error(
      'calculatePricingBreakdown: listing must be a valid object'
    )
  }

  if (typeof nightsPerWeek !== 'number' || isNaN(nightsPerWeek)) {
    throw new Error(
      `calculatePricingBreakdown: nightsPerWeek must be a number, got ${typeof nightsPerWeek}`
    )
  }

  if (typeof reservationWeeks !== 'number' || isNaN(reservationWeeks)) {
    throw new Error(
      `calculatePricingBreakdown: reservationWeeks must be a number, got ${typeof reservationWeeks}`
    )
  }

  // Get nightly rate (throws if not found)
  const nightlyPrice = getNightlyRateByFrequency({
    listing,
    nightsSelected: nightsPerWeek
  })

  // Calculate 4-week rent (throws on invalid input)
  const fourWeekRent = calculateFourWeekRent({
    nightlyRate: nightlyPrice,
    frequency: nightsPerWeek
  })

  // Calculate reservation total (throws on invalid input)
  const reservationTotal = calculateReservationTotal({
    fourWeekRent,
    totalWeeks: reservationWeeks
  })

  // Extract fees (with explicit validation)
  const cleaningFee = extractFee(
    listing['💰Cleaning Cost / Maintenance Fee'],
    'Cleaning Fee'
  )

  const damageDeposit = extractFee(
    listing['💰Damage Deposit'],
    'Damage Deposit'
  )

  // Calculate grand total
  const grandTotal = reservationTotal + cleaningFee

  return {
    nightlyPrice,
    fourWeekRent,
    reservationTotal,
    cleaningFee,
    damageDeposit,
    grandTotal,
    valid: true
  }
}

/**
 * Extract and validate a fee value from listing.
 * Returns 0 for explicitly missing optional fees (null/undefined).
 * Throws for invalid fee values.
 *
 * @param {any} feeValue - The fee value from listing
 * @param {string} feeName - Name of fee for error messages
 * @returns {number} Validated fee amount (0 if optional and missing)
 * @throws {Error} If fee value is invalid (e.g., negative, NaN)
 */
function extractFee(feeValue, feeName) {
  // Optional fees can be null/undefined - default to 0
  if (feeValue === null || feeValue === undefined) {
    return 0
  }

  const fee = Number(feeValue)

  if (isNaN(fee)) {
    throw new Error(
      `calculatePricingBreakdown: ${feeName} has invalid value ${feeValue}`
    )
  }

  if (fee < 0) {
    throw new Error(
      `calculatePricingBreakdown: ${feeName} cannot be negative, got ${fee}`
    )
  }

  return fee
}

