const showToastSafe = (options) => {
  if (typeof window.showToast === 'function') {
    window.showToast(options);
  } else {
    console.warn('[Header] Toast system not initialized:', options.title, options.content);
  }
};

// Usage:
showToastSafe({
  title: 'Error',
  content: 'No listing ID available',
  type: 'error'
});
```

