export function getNightlyRateFieldName(nights) {
  const fieldMap = {
    1: BUBBLE_FIELDS.NIGHTLY_RATE_1,
    2: BUBBLE_FIELDS.NIGHTLY_RATE_2,
    3: BUBBLE_FIELDS.NIGHTLY_RATE_3,
